css:
@-moz-document domain("www.google.com") {
.TAB_PANEL_NAV {
	background: #fff;
    color: darkslategray;
    display: none;
}
.TAB_PANEL_TAB_TD:hover {
	background: #fff;  
}
.CT_SHORT_MESSAGE_CONTAINER {
	text-align: left;
  	top: 60px;
  	max-width: 190px;
}
.CT_SHORT_MESSAGE {
	background: #fff;
    font-weight: normal;
  	padding: 8px 13px;
  	max-width: 190px;
  	text-align: left;
  	color: darkslategray;
}
.goog-menu-content {
	max-height: 500px;
  	overflow-y: scroll;
    position: relative;
}
.G_LINK {
    padding: 0 !important;
    font-size: 12px;
    color: darkslategray;
}
.A_LAYOUT_MAIN a,
.A_LAYOUT_MAIN a:visited,
.A_LAYOUT_MAIN a:hover {
  	color: darkslategray !important;
}
.ID-tm-block.P_NAVPANEL_MENU_BLOCK >li:nth-child(1) .G_LINK {
	color: darkslategray !important;
}
.ID-tm-block.P_NAVPANEL_MENU_BLOCK >li:nth-child(2) .G_LINK,
.CT_ICON_TAG:before {
	color: steelblue !important;
}
.ID-tm-block.P_NAVPANEL_MENU_BLOCK >li:nth-child(3) .G_LINK,
.CT_ICON_RULE:before {
	color: seagreen !important;
}
.ID-tm-block.P_NAVPANEL_MENU_BLOCK >li:nth-child(4) .G_LINK,
.CT_ICON_MACRO:before {
	color: indianred !important;
}
.P_NAVPANEL_GROUP {
    /*max-height: 550px;
    overflow: scroll;*/
}
.CL_KENNEDY_BUTTON.submit,
.CL_KENNEDY_BUTTON.submit:hover {
  	background: steelblue;
    border: steelblue;
}
.CL_KENNEDY_BUTTON.create,
.CL_KENNEDY_BUTTON.create:hover {
  	background: indianred;
    border: indianred;
}
.CT_COMPONENT_COMMON_TOOLBAR {
  	margin-top: 20px;
}
.CT_COMPONENT_CONDITION_EDITOR_PREDICATE_TEXTINPUT_WIDTH {
    width: auto;
    max-width: 70px;
}
div.CT_COMPONENT_CONDITION_EDITOR_PREDICATE_BUTTON.ACTION-addPredicate {
    width: 400px;
    margin-top: 10px;
    cursor: pointer;
}
.P_NAVPANEL .P_NAVPANEL_ITEM-_tm+.P_NAVPANEL_MENU_BLOCK, .P_NAVPANEL .P_NAVPANEL_ITEM-_queue+.P_NAVPANEL_MENU_BLOCK, .P_NAVPANEL .P_NAVPANEL_ITEM-_links+.P_NAVPANEL_MENU_BLOCK, .P_NAVPANEL .P_NAVPANEL_ITEM-_versions+.P_NAVPANEL_MENU_BLOCK, .P_NAVPANEL .P_NAVPANEL_ITEM-_us_dynamic+.P_NAVPANEL_MENU_BLOCK, .P_NAVPANEL .P_NAVPANEL_ITEM-_monitor+.P_NAVPANEL_MENU_BLOCK, .P_NAVPANEL .P_NAVPANEL_ITEM-_monitor+.P_NAVPANEL_MENU_BLOCK, .P_NAVPANEL .P_NAVPANEL_ITEM-_importexport+.P_NAVPANEL_MENU_BLOCK, .P_NAVPANEL .P_NAVPANEL_ITEM-_installcontainer_dynamic+.P_NAVPANEL_MENU_BLOCK {
    padding-left: 38px;
}
.P_NAVPANEL_SUBMENU_NAME {
	font-weight: bold;
    padding: 2px 0;
}
.P_NAVPANEL .P_NAVPANEL_SUBMENU_BLOCK {
    margin-left: 0;
}
.P_NAVPANEL .P_NAVPANEL_MENU_BLOCK .P_NAVPANEL_SUBMENU .P_NAVPANEL_ITEM .G_LINK div, .P_NAVPANEL .P_NAVPANEL_MENU_BLOCK .P_NAVPANEL_SUBMENU .P_NAVPANEL_ITEM_TOPLEVEL .G_LINK div {
	padding-left: 0;
}
.P_NAVPANEL .P_NAVPANEL_SUBMENU .P_NAVPANEL_SUBMENU_ITEM .P_NAVPANEL_ZIPPY_ICON:before {
    margin-top: -3px !important;
}
/* rule selection overlay */
.ID-conditionList .CT_TABLE_ROW td:nth-child(2) {
  width: 90% !important;
}
.ID-conditionList .CT_TABLE_ROW td:nth-child(3) {
  display: none;
}

input.ID-tag-name {
	width: 100%;
}
.CT_COMPONENT_TAG_EDITOR_CONTROLS_GROUP_HELP {
	display: none;
}
}
