css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?://(www.)?torrentsm((d.(com|eu|me))|(oldova.(com|org|net))).*") {
#torrents tr:hover {
    background-color: rgba(50,50,50, 0.2) ;
    color: #FF9900 ;
}

body, .mCenter, .categories, .tipContinut, #newslisttd {
    background: url(http://i.imgur.com/eWCuAoG.jpg) no-repeat center center fixed;
    background-size: cover;
}

.mCenter {
    border-radius: 5px;
    box-shadow: inset 0px 1px 0px 0px #fff, inset 0px -1px 2px 0px #000, 0 5px 8px rgba(0, 0, 0, .8), 0 0px 0px 1px rgba(0, 0, 0, .3);
    background: -moz-linear-gradient(top, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.7) 100%);
    z-index: 2;
}

#user_box a {
    color: lime;
}

#user_box > tbody > tr > td > font {
    color: lime;
}

#user_box a:hover, #user_box > tbody > tr > td > font:hover {
    color: yellow;
}

#user_box > tbody > tr > td > span > font {
    color: lime;
}

table#tbl_menu a, #user_box {
    margin: auto;
    background: rgba(51, 59, 70, 0.5);
    border: 1px solid #111;
    border-radius: 3px;
    box-shadow: inset 0px 24px 0px rgba(255,255,255,0.1),
    inset 0 1px 0 rgba(255,255,255,0.4),
    inset 0 10px 10px rgba(255,255,255,0.2),
    inset 0 -5px 10px rgba(0,0,0,0.2),
    0 3px 3px rgba(0,0,0,0.3);
    padding: 5px 10px;
    color: white;
    font-size: 12px;
    text-shadow: 0 1px 1px rgba(0,0,0,0.8);
    transition: all .5s ease;
    backface-visibility: hidden;
}

#torrents {
    margin: auto;
    background: rgba(51, 59, 70, 0.5);
    border-radius: 3px;
    box-shadow: inset 0px 24px 0px rgba(255,255,255,0.1),
    inset 0 1px 0 rgba(255,255,255,0.4),
    inset 0 10px 10px rgba(255,255,255,0.2),
    inset 0 -5px 10px rgba(0,0,0,0.2),
    0 3px 3px rgba(0,0,0,0.3);
    padding: 5px 10px;
    color: white;
    font-size: 12px;
    transition: all .5s ease;
    backface-visibility: hidden;
}

div#top_menu>table>tbody>tr>td:first-child>a {
    display: block ;
    background: url("http://funkyimg.com/i/2vobS.png") no-repeat scroll 0 0 
transparent ;
    width: 280px ;
    height: 76px ;
}

#tbl_menu a {
    color: #fff ;
    padding: 0 5px ;
    height: 26px ;
    line-height: 26px ;
    display: inline-block ;
}

#tbl_menu a:hover {
    background: #4370A0 ;
    border: 1px solid #335477 ;
}

#tmd_logo {
    display: none ;
}

td.topheader a {
    color: white;
}

#newslisttd {
    border-radius: 1px;
    box-shadow: inset 0px 1px 0px 0px #fff, inset 0px -1px 2px 0px #000, 0 5px 8px rgba(0, 0, 0, .8), 0 0px 0px 1px rgba(0, 0, 0, .3);
    background: -moz-linear-gradient(top, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.7) 100%);
    z-index: 2;
}
}
