css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("2ch.hk") {
@-moz-document domain("2ch.hk") {
html{
        background-color: white;
    }    
a{
color: #2B587A;
cursor: pointer;        
            
}        
body{
        margin-top: 30px;
        margin-left: 25%;
        margin-right: 15%;
        background-color: white;
        padding-right: 0%;
        margin-bottom: 30px;
        font-family: tahoma,arial,verdana,sans-serif,Lucida Sans;
        font-size: 14px;
          line-height: 1.182;
          font-weight: normal;    
         border: 1px solid #D9E0E7;
          
        }
#adminbar{
        position: absolute;
        left: 10%;
        top: 0px;
        color: white;
        width: 80%;
        z-index: 100;
        background-color: rgb(82,116,152);
        color: white;     
        padding-top:5px;
        padding-bottom: 2px;
        border-radius: 0px 0px 10px 10px;   
    }    
#adminbar ul.menu ul li a,#adminbar ul.menu ul li a:visited{
 color: white;    
 text-decoration: none;   
       
        
    }  
input{
        padding: 3px;
        border-radius: 5px;
        border: 1px solid #ccc;
        margin: 2px;
    }    
#adminbar select{
        border-radius: 5px;
        border: 1px solid #ccc;
    }    
#boardNav{
       position: absolute; 
       left: 15%;
       width: 15%;
       font-size: 14px;
       top: 20px;     
        
    }    
.rmenu li{
    list-style-type: none;        
            
        }        
.rmenu a,.rmenu a:visited{
text-decoration: none;
color: black;

}
.rmenu a:hover{
border-radius: 5px;
background-color: #D9E0E7;

}
.logo{
        display: none;
    }
.reply{
        background-color: #f5f5f5;
        border: none;
        border-top: 1px solid #e5e5e5;
        
        border-radius: 0;
    }    
}
}
