css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boards.4chan.org")
{

/* center the page */

   
    pre.prettyprint
    {
        width: 95% !important;
    }
    .stub
    {
        text-decoration: line-through !important;
    }

    .post .reply
    {
        width:100% !important;
        display: block !important;
        /*overflow: auto !important;*/
    }
    
    .opContainer
    {
        border-top-style: dashed !important;
        border-width: 2px !important;
    }

    .thread .postContainer
    {
        float: left !important;
        width: 50% !important;
        min-width: 200px !important;
        clear: none !important;
        overflow: auto !important;
        /*word-break: keep-all !important;*/
        word-wrap: break-word !important;
    }
    
    .center
    {
        clear:both !important;
    }
    
    div.reply{ background: inherit !important; display: block; }
    
    .UIPanel
    {
        background: rgba(255, 255, 255, 0.8) !important;
    }
    hr
    {
        display: none !important;
    }
    .board hr
    {
        width: 50% !important;
        position: absolute;
        display: none !important;
    }
    
    div.navLinks.navLinksBot.desktop
    {
        clear: both !important;
    }
    .postMessage
    {
        margin: 0px 0px 10px 10px !important;
    }
    body
    {
        width: 80% !important;
        margin-left: auto !important;
        margin-right: auto !important;
        /*background:#EEF2FF url(http://static.4chan.org/image/fade-blue.png) top center repeat-x !important;*/
    }
    

    
    #nav-links:before
    {
        content: "Search: " !important;
    }
    
    button, input, select
    {
        font-size: 80% !important;
        border-style: none !important;
    }

    div.topPageNav
    {
        display: none !important;
    }
    div.pages
    {
        padding: 0px !important;
    }
    .pagelist, .pagelist div
    {
        border-style: none !important;
        background: inherit !important;
    }


/* prettify */
  
    .summary.desktop, .post, .stub
    {
       /*display: block !important;*/
        clear: both !important;
    }


    .replyContainer + .replyContainer { margin-top: -1px; }
    .replyContainer .post.reply { margin: 0 !important; border: none !important; }
    .replyContainer .post.reply.highlight { margin: 0 !important; border: none !important; }
    .thread { margin-bottom: 50px !important; }
    blockquote.postMessage { margin: 1em 1.8em; }

    span.quote { padding-left: 10px !important; }
    /*span.quote:before, span.quote:after { content: "\"" !important; }*/
    /*span.quote+br+br { margin-bottom: 1ex !important; }*/

   
    .bottomad
    {
        padding-top: 50px !important;
    }

    /*hr { visibility: hidden; margin: 0; padding: 0; width: 0; height: 0; }*/

    img.title { display: none; }

/* extension buttons */

    .post.op .threadHideButton,
    .fileText .extButton,
    .postInfo .extButton,
    .sideArrows .replyHideButton,
    .hide_thread_button span,
    a.menu_button, .sideArrows
    {
        opacity: 0;
    }
    
    .post.op .threadHideButton:hover,
    .fileText .extButton:hover,
    .postInfo .extButton:hover,
    .sideArrows .replyHideButton:hover,
    .hide_thread_button span:hover,
    a.menu_button:hover, .sideArrows:hover
    {
        opacity: 1;
    }
    
    .post.op .threadHideButton,
    .hide_thread_button,
    .sideArrows
    {
        position: absolute;
        left: 50%;
        margin-left: -422px !important;
        padding-top: 1px;
    }
    
}
