css:
@-moz-document url-prefix("https://news.google.com/"), url-prefix("https://news.google.de/") {
/* Hide News.Google.com Footer */
.clearfooter + .footer {display: none !important;}
}

@-moz-document url-prefix("https://www.google.com/"), url-prefix("https://www.google.de/") {
/* Hide FireFox "Come here often? Make Google your homepage." */
#gb :last-child > DIV.gb_g[aria-hidden="false"]:first-child:last-child {display: none !important;}

/* Hide Search Footer */
#footcnt {display: none !important;}

/* Hide News Tab Footer "The selection and placement..." */
#foot #navcnt + .f.nwd {display: none !important;}

/* Hide Search Report Images, Image Preview Copyright & Feedback */
#imagebox_bigimages A#irl_r_a, .irc_c DIV.irc_ft {display: none !important;}

/* Hide Shopping junk - Search Page: Footer "Google is compensated by..." */
#navcnt + .f.shpd {display: none !important;}

/* Hide Shopping junk - Product Page: Feedback Footer, Footer "Google is compensated by...", Footer */
#pp-main > #feedback-section, #footer > P.disclaimer, div#pp-footer {display: none !important;}
}

@-moz-document url-prefix("https://drive.google.com/drive/"), url-prefix("https://drive.google.de/drive/") {
/* Hide Buy More Storage & Get Drive for OS - Adjust Margin of Div Class, 4 levels up */
.Oc-uj > .Oc-I, div.a-pa-ob-yd-aa-J div.a-Yygnk-ee-Qe {display: none !important;}
.a-Ij-Qe {height: 53px !important; top: -61px !important;}
}

@-moz-document url-prefix("https://www.google.com/maps/"), url-prefix("https://www.google.de/maps/") {
/* Hide Help Icon & Footer Junk */
.widget-gear-container > .widget-gear-help-icon, .scene-footer > #fineprint .fineprint-container {display: none !important;}
}

@-moz-document domain("youtube.com"), domain("youtube.de") {
/* Hide Footer */
#footer-container {display: none !important;}

/* Adjust Footer Container Margin */
body #footer-container {margin-top: 0px !important;}

/* Adjust Body Padding */
#content-container, #body-container {padding-bottom: 50px !important;}
}

@-moz-document domain("google.com"), domain("google.de") {
/* Hide Google+ Header Profile First Name */
div#gb span.gb_P {display: none !important;}
}

@-moz-document url-prefix("https://accounts.google.com/"), url-prefix("https://accounts.google.de/") {
/* Hide Google Login clutter */
div.banner > h1, a.need-help, div.one-google, div.google-footer-bar {display: none !important;}
}
