css:
@-moz-document domain("ask.fm") {
    .container, #wrapper, #footer{
        border-radius: 0px;
        background-color: rgba(16,18,20, 0.8);
    }
    .searchBox-socialService:hover {
        background: rgba(44,44,44,.9) !important;
    }
    #searchBox {
        border: 1px solid rgb(51,53,57);
        background: rgba(16,18,20,.08) !important;
    }
    .searchBox-separator {background: rgb(51,53,57);}
    #menu {box-shadow: 5px 17px 61px -5px rgba(252,252,252,.452);}
    #menu, #cboxTitle {
       background: linear-gradient(to bottom,#171C1C, #050505);
    }
    #wrapper, #footer {
       box-shadow: 0px 0px 0px 10px rgba(51, 53, 57, 0.6);
    }
    #footer {
        margin-top: 20px;
    }
    .flyout-ask_follow {
        background: rgba(16,18,20, .9) !important;
        border: 5px solid rgba(51,53,57, .5) !important;
    }
    .headline-menu .link-headline-active, .blueBox_introduction, #post-input-pre, #profile-input, .motivationBlock-share, .motivationBlock-service, .text-12, .questionBox-borderNone, .text-bold, .checkLevel-label, label, h1, #profile_answer_counter, #profile_liked_counter, #profile_gifts_counter, .qofday-label, .answer, .link-profile-name span, .text-bold span, .question, #footer, #footer .footerItem a, input[type=submit], a.link-name, .people-bio, .recommendedName span, #searchBox-formFrame, .searchBox-socialLabel, #user_name, #user_location, #user_language_id, #user_about_me, #user_website, #user_headline, #user_login, #user_email, #user_born_at_day, #user_born_at_month, #user_born_at_year, #user_password, #user_password_confirmation, .searchBox-inputForm{
        color: #FFF;
    }
    .searchBox-socialLabel, .searchBox-pointer {
        text-shadow: none;
    }
    .profileEditAnonymous label  {
        color: #000 !important;
    }
    #profile-input, #post_options, #post_options-border, #post-input-pre, #searchBox-formFrame, #user_name, #user_location, #user_language_id, #user_about_me, #user_website, #user_headline, #user_login, #user_email, #user_born_at_day, #user_born_at_month, #user_born_at_year, #user_password, #user_password_confirmation{
        background: rgba(0,0,0,.8) !important;
        border: 3px;
    }
    #recommended_users_box, .recentQuestionBox, #blueBox {
        background: transparent;
        border: none;
    }
    .questionBox, .headline-menu {
        border-bottom-color: #999;
    }
    #more-container input[type=submit], #more-container input[type=hidden] {
        border: transparent;
        border-radius: 1px;
        background: #000;
        opacity: .7;
        transition: all .19s;
    }
    #more-container input[type=submit]:hover, #more-container input[type=hidden]:hover, .link-headline:hover, .text-headline {
        opacity: 1;
        color: #FFF !important;
    }
    .like-active, .question img{
        border-radius: 2px;
        box-shadow: 20px 0px 0px rgb(233, 81, 81) inset !important;
    }
    .ghostLink a {
        border-radius: 2px;
        box-shadow: 20px 0px 0px rgb(217,217,217) inset !important;
    }
    .stream-profile, .recommendedHead {
        border-radius: 5px;
    }
    a.notification-like, a.notification-like-active, a.notification-like-selected, a.notification-question, a.notification-question-active, a.notification-answer, a.notification-answer-active, a.notification-answer-selected {
        background-image: url('http://pik.vn/2014b4afc909-3208-4581-abb1-09eef7ba6d8a.png') !important;
    }
    .blueFriendsButton-active, .submitBlue-active, .link-modern-left, .searchBox-submit {
        background: rgba(12,173,242,.75) !important;
        border: transparent !important;
        border-radius: 1px !important;
        transition: all .25s;
        text-shadow: 0px 1px #2D6484 !important;
    }
    .blueFriendsButton-active:hover, .submitBlue-active:hover, .link-modern-left:hover, .searchBox-submit:hover {
        background: rgba(12,173,242,.95) !important;
        color: #FFF;
    }
    #profile-head {
        background: linear-gradient(to bottom, rgba(29,32,36,.8), rgba(16,18,20,.01)) !important;
        border-bottom: none;
    }
    .link-unfollow, .profileEditControl-cancel {
        background: rgba(255,221,163,.85) !important;
        border: transparent !important;
        border-radius: 1px !important;
        transition: all .25s;

    }
    .link-unfollow:hover, .profileEditControl-cancel:hover{
        background: rgba(255,187,69,.95) !important;
        text-shadow: 0px -1px 0px #FFBB45 !important;
    }
    .submit-button {
        background: rgba(250,250,250,.85) !important;
        border: transparent !important;
        border-radius: 1px !important;
        transition: all .25s;
        color: #000 !important;
    }
    .submit-button:hover {
        color: #FFF !important;
        background: rgba(12,173,242,.95) !important;
    }
    .recommendedFollow, .link-green, a.followAll-big, .profileEditControl-save {
        background: rgba(156,207,109,.85) !important;
        transition: all .25s;
        border-radius: 1px !important;
        border: transparent !important;
    }
    .recommendedFollow:hover, .link-green:hover, a.followAll-big:hover, .profileEditControl-save:hover {
        background: rgba(111,171,34,.95) !important;
    }
    .recommendedBox, #shareLinkReminder {
        background: rgba(0,0,0, .267) !important;
        border: transparent !important;
    }
}
