/** PHPInfo Beauty
/*  http://
/*  by Стрэк
/*  update 2014-04
/*  http://userstyles.org/styles/100557/phpinfo-beauty-by
*/

/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp("(https?)://\.\*\.\*/phpinfo.php?\.\*")
{

body {
  background: #E5E5FF url('https://blagoyar-tishina.inf.ua/img/phpinfo.jpg') !important;
  background-repeat: repeat-x repeat-y !important;
  background-attachment: fixed !important;
  font-weight: normal !important;
}

::selection {
  background: #C3C3F3 !important;
  color: #000 !important;
}
::-moz-selection {
	background: #C3C3F3 !important;
	color: #3B3B3B !important;
	text-shadow: 2px 2px 2px #000,0 -2px 3px #000,-2px 0 3px #000 !important;
}
.e::-moz-selection {
	background: #C3C3F3 !important;
	color: #B8B8B8 !important;
	text-shadow:2px 2px 2px #fff,0 -2px 3px #fff,-2px 0 3px #fff !important;
}
h1::-moz-selection, h2::-moz-selection, a::-moz-selection {
	background: #E6E6E6 !important;
	color: #B8B8B8 !important;
	text-shadow:2px 2px 2px #fff,0 -2px 3px #fff,-2px 0 3px #fff !important;
}
.p::-moz-selection {
	background:#9999CC !important;
	color:#B8B8B8 !important;
}
a:link {
	color: #ACDB1A !important;
	background:none !important;
}
a:visited {
	color: #89B800 !important;
	background:none !important;
}
pre {
  font-weight: bold !important;
  font-size: 110% !important;
  font-family:'Ubuntu', sans-serif !important;
}
table {
  width:1100px !important;
  box-shadow: 0 0 5px 2px rgba(0,0,0, 0.5) !important;
  border-radius:5px !important;
  border-spacing:8px !important;
  border-collapse:separate !important;
}
td, th, h1, h2 {
  font-family:'Ubuntu', sans-serif !important;
}
th {
  background-color:rgba(255,255,255, 0.4) !important;
  border:rgba(255,255,255, 0.0) !important;
  border-radius:5px !important;
  text-align:center !important;
  font-size: 100% !important;
  text-decoration:underline !important;
  vertical-align:middle !important;
  text-shadow:2px 2px 2px #fff,0 -2px 3px #fff,-2px 0 3px #fff !important;
  box-shadow: 0 0 1px 2px #000 !important;
}
td {
  font-size: 80% !important;
  text-align:center !important;
  border:none !important;
  border-radius:5px !important;
  vertical-align:middle !important;
  padding: 10px !important;
}
h1, h2 {
  font-size:170% !important;
  color:#ACDB1A !important;
  text-shadow:1px 1px 3px #000,0 -1px 3px #000,-1px 0 3px #000 !important;
}
td.v {
  width:800px !important;
  word-break:break-all !important;
  font-weight:bold !important;
  border:none !important;
  border-radius:5px !important;
  background-color:rgba(215,215,215, 0.9) !important;
  color:#333 !important;
  text-align:center !important;
  box-shadow:0 0 3px #000 !important;
}
tr.v {
  background:rgba(81,81,132, 0.4) !important;
  text-align:center !important;
  font-weight:bold !important;
  color:#FFF !important;
  border:none !important;
  border-radius:5px !important;
}
td.e {
  width:300px !important;
  color:#FFF !important;
  text-shadow:1px 1px 3px #000,0 -1px 3px #000,-1px 0 3px #000 !important;
  background-color:rgba(191,191,242, 0.9) !important;
  border:none !important;
  border-radius:5px !important;
  box-shadow:0 0 3px #000 !important;
}
.p {
	text-align:center !important;
	margin: 19px 0px 0px 700px !important;
	border:none !important;
	border-radius:5px !important;
	text-shadow:0px 0px 5px #000,0 -1px 4px #000,-1px 0 4px #000,0px 0px 10px #000 !important;
}

}
