/* Tacoma World Dark -- Version: 1.01 (2014-06-26-11-50) */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tacomaworld.com") {

/* Common */

.page {background: #000000 !important;}
body {background: #000000 !important; color: #FFFFFF !important;}
a, a:active, body_alink, a:link, a:visited, body_avisited, font[color="#800080"] {color: #FFFFFF !important;}
a:hover {color: #AABBCC !important;}

h1.myh1 {color: #FFFFFF !important; text-shadow: 0px 0px !important;}
.alt1, .alt1Active, .mysig {background: #555555 !important; color: #FFFFFF !important;}
.alt2, .alt2Active {background: #444444 !important; color: #FFFFFF !important;}
.tcat, .thead, .vbmenu_control {background: #334455 !important;}
.wysiwyg {background: #F5F5FF !important; color: #000000 !important;}
.wysiwyg a, .wysiwyg a:active, .wysiwyg a:visited {color: #0000BB !important;}
.time, .smallfont, .navbar {color: #FFFFFF !important;}
legend {color: #FFFFFF !important;}
.bigusername span[style="color:#c00"] {color: #FF6F00 !important;}

.tborder {border: 1px solid #223344 !important; background: #444444 !important;}
td {border: 0px !important;}
.panel {background: #555555 !important; color: #FFFFFF !important; border: #555555 2px groove !important;}
.panelsurround {background: #444444 !important;}

.amenuitem, .subinforeplies {background: #555555 !important; color: #DDDDDD !important;}
.amenu, .vbmenu_popup {border: 0px !important; background: #334455 !important;}
.vbmenu_option {background: #555555 !important; color: #FFFFFF !important;}
.subinfocat {background: #334455 !important;}

.tfoot {background: #334455 !important;}
#fnav {background: #555555 !important;}

#navcont, #navcont li {background: #334455 !important; border: 0px !important;}

div[style="background-color:#f3f3f3;border:1px solid #888;margin:0 0 3px 0;zoom:1"] {background: #334455 !important; color: #FFFFFF !important; border: 1px solid #334455 !important; margin: 0 0 3px 0 !important;}

/* Hover Info / Gallery */

.longdesc {background: #334455 !important; color: #FFFFFF !important;}
.galshow {background: #555555 !important; border: 0px !important; box-shadow: 8px 8px 15px rgba(0, 0, 0, 0.3) !important;}
.galshow .title {background: #334455 !important; color: #FFFFFF !important; font-weight: bold !important;}
.galshow .prev, .galshow .next {color: #FF8F00 !important;}
.galshow .state {color: #FFFFFF !important;}

/* Quick PM */

.qpmshow {background: #555555 !important; border: 0px !important; box-shadow: 8px 8px 15px rgba(0, 0, 0, 0.3) !important;}
.qpmbcclabel, .qpminsertref {color: #FFFFFF !important;}

/* Misc */

#topsellers span {color: #FF6F00 !important;}
#vB_Editor_001_smiliebox legend, #vB_Editor_001_smiliebox td {color: #000000 !important;}
#vB_Editor_001_smiliebox a, #vB_Editor_001_smiliebox a:link, #vB_Editor_001_smiliebox a:visited {color: #334455 !important;}
#vB_Editor_001_smiliebox a:hover {color: #AABBCC !important;}

/* Profile */

#main_userinfo h1, #main_userinfo h2 {color: #FFFFFF !important;}
#aboutme dt {color: #FFFFFF !important; font-weight: bold !important;}
#stats_mini dt {color: #FFFFFF !important; font-weight: bold !important;}
#stats span {color: #FFFFFF !important; font-weight: bold !important;}
#last_online span {color: #FFFFFF !important;}
#contactinfo dt {color: #FFFFFF !important;}
#user_avatar.alt2 {background: #555555 !important;}

/* Gallery */

h1[style="margin:3px 0 0;font-size:18px;font-weight:bold;"] {color: #FFFFFF !important;}
label[for="embedcode"] {color: #FF6F00 !important;}

/* Tire Calc Section */

#main {color: #FFFFFF !important; background: #555555 !important;}
#tiresizes, #recentsearches, #modelsizeyears {background: #555555 !important; color: #FFFFFF !important;}
label[for="statslinkcode"], label[for="speedolinkcode"] {color: #FFFFFF !important;}
.factory, .econ, .power, span[style="background-color:#ffffb9"], span[style="background-color:#baffc5"], span[style="background-color:#ffc2c2"] {color: #000000 !important;}
#popularsearches h2 {color: #000000 !important;}
#results td {background: #555555 !important;}
#modelsizeselectors span {color: #FFFFFF !important;}
th[scope="col"] {background: #334455 !important; color: #FFFFFF !important;} 
label[class="textlabel"], a[title="Clear your tire size calculator history"] {color: #FFFFFF !important;}
.calclink {color: #FFFFFF !important;}

