css:
@-moz-document url-prefix('http://www.tumblr.com/dashboard'),
url-prefix('http://www.tumblr.com/inbox'),
url-prefix('https://www.tumblr.com/settings'),
url-prefix('http://www.tumblr.com/search'),
url-prefix('http://www.tumblr.com/likes'),
url-prefix('http://www.tumblr.com/blog'),
url-prefix('http://www.tumblr.com/followers'),
url-prefix('http://www.tumblr.com/tagged'),
url-prefix('http://www.tumblr.com/reblog'),
url-prefix('http://www.tumblr.com/new'),
url-prefix('http://www.tumblr.com/liked')
, url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/inbox"), url-prefix("https://www.tumblr.com/search"), url-prefix("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/blog"), url-prefix("https://www.tumblr.com/followers"), url-prefix("https://www.tumblr.com/tagged"), url-prefix("https://www.tumblr.com/reblog"), url-prefix("https://www.tumblr.com/new"), url-prefix("https://www.tumblr.com/liked") {

/*CONTENT begin - background for posts, icon bar, sidebar*/
 #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
   background: rgba(44,71,98, 0.0) !important;

 /*CONTENT end*/

}

/*POST BACKGROUND begin*/
 #posts LI[class*=" post"] {
   background: rgba(232, 232, 232, 0.0) !important;
 }
 /*POST BACKGROUND end*/

/*ICON BAR BACKGROUND begin - background for the icon bar*/
 #new_post {
   background-color: rgba(255, 255, 255, 0.0) !important;
 }
 /*ICON BAR BACKGROUND end*/

/*POST BACKGROUND begin*/
 #posts LI[class*=" post"] {
   background: rgba(255, 255, 255, 0.25) !important;
 }
 /*POST BACKGROUND end*/

}

@-moz-document url-prefix('http://www.tumblr.com'),
url-prefix('https://www.tumblr.com') {


html, body {
    background: url('http://upload.wikimedia.org/wikipedia/commons/5/54/Claude_Monet,_Impression,_soleil_levant.jpg') center #fefefe no-repeat fixed !important;
background-size:cover!important;
}

ol#posts li blockquote,body.mceContentBody blockquote {
padding-left: 10px !important;
border-width: 4px !important;
}

.blue_bar {
background:none!important;
border:none!important;
border-bottom:none!important;
}

.section.first.divider{background:none!important;}

#sidebar_footer_nav.pinned_sidebar_footer{display:none;}

#right_column:after{background:none !important;}

.search_form_row{background:rgba(255, 255, 255, 0.0)!important;}

.selection_nipple{display:none!important;}


.chrome.blue, .chrome.flat.close, button.chrome, .button.gray.light, #cancel {
border-color:#d9d9d9!important;
background:#f0f0f0!important;
border-radius:0px !important;
color:grey!important;
}

.chrome.blue.txt, .chrome.blue.options, .chrome.grey, .send_fan_mail, #send{
background:#555!important;
color:#ddd!important;
}

a.post_permalink {
background-image:none!important;
color:white!important;
border-bottom-left-radius:15px!important;
border-top-right-radius:6px!important;
}

.plexi.color.active.show {
background: rgba(0,0,0,0.0)!important;}

a:before {
background:none!important;
border-right:none!important;
}

}

@-moz-document url-prefix('http://www.tumblr.com/search'), url-prefix('https://www.tumblr.com/search')   {

input{color:rgba(255,255,255,0.0)!important;}

.search_results_bottom_wrapper.posts_wrapper,
#container:before { 
background:none!important;
}

}

@-moz-document url-prefix('http://www.tumblr.com/reblog'),
url-prefix('http://www.tumblr.com/new') , url-prefix("https://www.tumblr.com/reblog"), url-prefix("https://www.tumblr.com/new") {

body#tinymce, html {
background:white!important;
}

}
