css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.freekick.org") {

/***ARKAPLAN - BACKGROUND***/
body {
	font-family: Verdana !important;
	color: #000 !important;
}

    #page-wrap{
        min-width: 940px;
        width:100% !important;
    }

/***UST KISIM - TOP***/
#top-wrap{	
	width: 100% !important;
	min-width: 940px !important;	
	background-image: none !important;
	background-color: #3e5998 !important;
	background-repeat: repeat-x !important;
	background-position: left top !important;
	/*border-bottom:2px solid #3e5998 !important;*/
}	
#logo{		
	background-image: url(http://i195.photobucket.com/albums/z252/siriusj/fklogo.jpg) !important;
}
#top-info{
	background-image: none !important;
	background-color: #3e5998 !important;
}
/***SOL KISIM - LEFT PART***/
#lang-select {
	background-image: url(http://i195.photobucket.com/albums/z252/siriusj/banneralt.jpg) !important;
}	

/***SOL MENU - MENU LEFT***/
#menu-left{
	background-image:none !important;
	background-color: #f7f7f7 !important;
}
#inner-menu{
	background-image:url(http://i195.photobucket.com/albums/z252/siriusj/inner_menu.jpg) !important;
}
#menu-left2{
	background-image:none !important;
	background-color: #f7f7f7 !important;
}
#inner-menu2{
	background-image:url(http://i195.photobucket.com/albums/z252/siriusj/inner_menu.jpg) !important;
}
#inner-menu2 ul li p{color:#3b5998 !important; border-left: 4px solid #f7f7f7 !important;}
#inner-menu2 ul li p:hover{background-color: #e9e9e9 !important;}
.title-menu-l{color:#3e5998 !important;}
.menu-btm{
background-image: url(http://i195.photobucket.com/albums/z252/siriusj/menualt-1.jpg) !important;
}
#form-login input.login-submit:hover{background-color:#3e5998 !important;}
.reg{background:url(http://i195.photobucket.com/albums/z252/siriusj/arrow_red.gif) center left no-repeat !important;}
.reg a{color:#3e5998 !important; font-size:11px;}
.rem{margin-left:-10px;background:url(http://i195.photobucket.com/albums/z252/siriusj/arrow_red.gif) center left no-repeat !important;}
.rem a{color:#3e5998; font-size:11px;}

/***SAG KISIM - RIGHT PART***/

#blue-bar{
background-image: none !important;
background-color: #3e5998 !important;
}
#blue-nav{
background-image: none !important;
background-color: #3e5998 !important;
}
#main-green-wrap{
background-image: none!important;
background-color: #3e5998 !important;
width:100% !important;
}
#green-round{
background-image: url(http://i195.photobucket.com/albums/z252/siriusj/crn_green_tabs_left.jpg) !important;
}
#main-tabs li.tab-hidden{
background-color: #fff !important;
background-image: url(http://i195.photobucket.com/albums/z252/siriusj/tab_r-1.jpg) !important;
}
#main-tabs li.tab-hidden a {
color:#3e5998 !important;
}
#main-tabs li.tab-active{
background-image: url(http://i195.photobucket.com/albums/z252/siriusj/tab_sel_r.jpg) !important;
}
#main-tabs li.tab-active a {
color:#000 !important;
}
    #content-l{
        float:left !important;
        width:77% !important;
        height:100% !important;
        margin-right:0 !important;
        padding:0 !important;
        font-size:10px !important;
        border-right:0 solid #ccc !important;
        padding-right:0 !important;
    }
    #content-r{
        float:right !important;
        font-size:10px !important;
        width:20% !important;
        height:100%;
        padding:0 10px 0 0 !important;
    }
#white-content{
background-color:#fff !important; margin-left:15px !important;margin-right:0 !important;
background-image: url(http://i195.photobucket.com/albums/z252/siriusj/white_tab_r_bg-1.jpg) !important;
}
.rigth-crn{
background-image: none !important;
}
.btm-shadow{
background-image: url(http://i195.photobucket.com/albums/z252/siriusj/altkose.jpg) !important;
background-color: #3e5998 !important;
}
.news-title {
background-color:#f7f7f7 !important;color:#000 !important;
}
.news-header a {
font-weight:bold;color:#000 !important;
}
.guestbook-title {
background-color:#e9e9e9 !important; color:#000 !important;
}
.guestbook-comment {
color:#000 !important;
}
/*** THE ROUND BOX***/
.green-box .inside {
background-color:#3e5998 !important;
}
.blue-box .top-left, .blue-box .top-right, .blue-box .bottom-left, .blue-box .bottom-right {
background-image: none !important;
background-color: #3e5998 !important;
}
.blue-box .inside-box {
background-color:#3e5998 !important;
}
.blue-round {
background-color:#3e5998 !important;
}
.plain-dotted-list li {
background-image:url(http://i195.photobucket.com/albums/z252/siriusj/cizgi.jpg) !important;
}
.club-name a{
color:#000 !important;
}
.settings-header {
color:#000 !important;
}
.club-name a{
color:#000 !important;
background-image:none !important;
}
#nav-left li{
background-image:url(http://i195.photobucket.com/albums/z252/siriusj/cizgi.jpg) !important;
}
#nav-left li ul{
background-color:#f7f7f7 !important;
}
#nav-left li a{
color: #3e5998 !important;
border-left: 4px solid #3e5998 !important;
}
#nav-left li a:visited{
color: #3e5998 !important;
}
#nav-left ul li a{
border-left: 4px solid #3e5998 !important;
}
#nav-left li a:hover{
background-color: #e9e9e9 !important;
}
#inner-menu .subfolderstyle{
background: url(http://i195.photobucket.com/albums/z252/siriusj/folder_o-1.gif) no-repeat center right !important;
}

.awesome{color:#dd6611 !important; font-weight:bold !important;}
.brilliant{color:#ff8833 !important; font-weight:bold !important;}
.superb{color:#ff9966 !important; font-weight:bold !important;}
.excellent{color:#ff99cc !important; font-weight:bold !important;}
.good{color:#cc33cc !important; font-weight:bold !important;}
.decent{color:#000000 !important; font-weight:bold !important;}
.weak{color:#31abb3 !important; font-weight:bold !important;}
.poor{ color:#6b9b0f !important; font-weight:bold !important;}
.awful{color:#ff0000 !important; font-weight:bold !important;} 

table th{
background-color:#3e5998 !important;
color: #fff !important;
}
table th a{
color:#fff !important;
}
.dotted, table.dotted tr th , table.dotted tr td, table.dotted-header tr th , table.achievers tr th , table.achievers tr td, table.transfers tr th , table.transfers tr td, table.moments tr th , table.moments tr td, table.clubnews tr th , table.clubnews tr td {
background-image:url(http://i195.photobucket.com/albums/z252/siriusj/cizgi.jpg) !important;
}
.players th{
background-image:url(http://i195.photobucket.com/albums/z252/siriusj/cizgi.jpg) !important;
}
players td{
background-image:url(http://i195.photobucket.com/albums/z252/siriusj/cizgi.jpg) !important;
}
table.player-achievements .header {background-color:#3e5998 !important; 
}
.pos{color:#3e5998 !important;
}
.orange, .desired-position{color:#f00 !important;
}
.green-head{background-image: none !important;
background-color: #3e5998 !important;}
.red-head{background-image: none !important;
background-color: #3e5998 !important;}
.blue-head{background-image: none !important;
background-color: #3e5998 !important;}
.green-title{background-image: none !important;
background-color: #3e5998 !important;}
.red-title{background-image: none !important;
background-color: #3e5998 !important;}
.blue-title{background-image: none !important;
background-color: #3e5998 !important;}
.red-crn-r{background-image: none !important;
background-color: #3e5998 !important;}
.green-crn-r{background-image: none !important;
background-color: #3e5998 !important;}
.blue-crn-r{background-image: none !important;
background-color: #3e5998 !important;}
.red-crn-re{background-image: none !important;
background-color: #3e5998 !important;}
.green-crn-re{background-image: none !important;
background-color: #3e5998 !important;}
.blue-crn-re{background-image: none !important;
background-color: #3e5998 !important;}

.marked, tr.marked tr, .odd-marked, tr.odd-marked tr {
  background-color: #f0f0f0 !important;
}
.even-marked, tr.even-marked tr {
  background-color: #fff !important;
}

.view-active, .view-passive {
background-image:url(http://i195.photobucket.com/albums/z252/siriusj/cizgi.jpg) !important;
}
.message-header {
background-image:url(http://i195.photobucket.com/albums/z252/siriusj/cizgi.jpg) !important;
}
.message-content {
background-image:url(http://i195.photobucket.com/albums/z252/siriusj/cizgi.jpg) !important;
}
.dots, .promotion-demotion-none{
background-image:url(http://i195.photobucket.com/albums/z252/siriusj/cizgi.jpg) !important;
}
.dotsL{
background-image:url(http://i195.photobucket.com/albums/z252/siriusj/cizgi.jpg) !important;
}
.dotsLine, .promotion-demotion-qualification{
background-image:url(http://i195.photobucket.com/albums/z252/siriusj/cizgi.jpg) !important;
}

.green-stripe{
background-image:none !important;
background-color: #f7f7f7 !important;
}
.red-stripe{
background-image:none !important;
background-color: #3e5998 !important;
}

#banner-left {display: none !important;}
#top-banner {display: none !important;}

.matchreport-goal, .matchreport-goalattempt, .matchreport-throwin, .matchreport-cornerkick, .matchreport-freekick, .matchreport-penaltykick, .matchreport-playcontinued, .matchreport-counterattack { 
color: #3e5998 !important;
font-weight: bold !important;
}

table.matchreport-table th{ background-color:#3e5998 !important; }
.min, .min a, .matchreport-penaltykicks, .matchreport-halftime, .matchreport-penaltyround {
color: #3e5998 !important;
font-weight: bold !important;
}

.matchreport_masterful {color: #d61 !important; font-weight: bold !important;} 
.matchreport_unbelievable {color: #d61 !important; font-weight: bold !important;} 
.matchreport_awesome {color: #d61 !important; font-weight: bold !important;} 
.matchreport_brilliant {color: #f83 !important; font-style: normal !important;} 
.matchreport_superb {color: #f96 !important; font-style: normal !important;} 
.matchreport_excellent {color: #f9c !important; font-style: normal !important;} 
.matchreport_good {color: #c3c !important; font-style: normal !important;} 
.matchreport_decent {color: #000 !important; font-style: italic !important;} 
.matchreport_weak {color: #3cc !important; font-style: normal !important;} 
.matchreport_poor {color: #a4a400 !important; font-style: normal !important;} 
.matchreport_awful {color: #f00 !important; font-style: normal !important;} 
.matchreport_dreadful {color: #aaa !important; font-style: normal !important;}
.matchreport_prepared {color: #d61 !important; font-style: italic !important;}
.matchreport_there {color: #f9c !important; font-style: italic !important;}
.matchreport_close {color: #3cc !important; font-style: italic !important;}
.matchreport_too_late {color: #f00 !important; font-style: italic !important;}
.matchreport_ready{color: #d61 !important; font-style: italic !important;}
.matchreport_hesitant{color: #f9c !important; font-style: italic !important;}
.matchreport_fooled{color: #3cc !important; font-style: italic !important;}
.matchreport_totally_wrong{color: #f00 !important; font-style: italic !important;}
.matchreport_net_wide_open {color: #d61 !important; font-style: italic !important;}
.matchreport_totally_free {color: #d61 !important; font-style: italic !important;}

.green-bl, .green-br, .green-tl, .green-tr{ 
	background-image: none !important;
background-color: #3e5998 !important;
}
.blue-bl, .blue-br, .blue-tl, .blue-tr { 
		background-image: none !important;
background-color: #3e5998 !important;
}

/***FORUM***/

.bodyline	{ background-color: #3e5998 !important; border: 1px #3e5998 solid !important; }
.forumline	{ background-color: #ccc !important; border: 2px #3e5998 solid !important; }
td.row1	{ background-color: #f1f1f1 !important; }
td.row2	{ background-color: #e7e7e7 !important; }
td.row3	{ background-color: #fff !important; }
td.rowpic {
background-color: #FFFFFF;
background-image: none !important;
background-repeat: repeat-y;
}
th	{
color: #FFA34F; font-size: 11px; font-weight : bold;
background-color: #3e5998 !important; height: 25px;
background-image: none !important;
}
td.cat,td.catHead,td.catSides,td.catLeft,td.catRight,td.catBottom {
background-image: none !important;
background-color:#fff !important; border: #3e5998 !important; border-style: solid; height: 28px; color: #3e5998 !important;
}
a.gen,a.genmed,a.gensmall { color: #fff !important; text-decoration: none; }
a.gen:hover,a.genmed:hover,a.gensmall:hover	{ color: #f7f7f7 !important; text-decoration: underline; }
.gen,.genmed,.gensmall { color : #31abb3 !important; }

.admincolor         { color: #f00 !important; }
.admincolor:link    { color: #f00 !important; }
.admincolor:active  { color: #f00 !important; }
.admincolor:visited { color: #f00 !important; }
.modcolor           { color: #090 !important; }
.modcolor:link      { color: #090 !important; }
.modcolor:active    { color:  #090 !important; }
.modcolor:visited   { color: #090 !important; }
.usercolor          { color: #3e5998 !important; }
.usercolor:active   { color: #3e5998 !important;  }
.mainmenu	    { font-size : 11px; color : #fff !important}
a.mainmenu	    { text-decoration: none; color : #fff !important;  }
a.mainmenu:hover    { font-weight: bold; color : #d9d9d9 !important; }
.cattitle           { font-weight: bold; font-size: 12px ; letter-spacing: 1px; color : #3e5998 !important}
a.cattitle          { text-decoration: none; color : #3e5998 !important; }
.forumlink	    { font-weight: bold; font-size: 12px; color : #6d84b4 !important; }
a.forumlink 	    { text-decoration: none; color : #6d84b4 !important; }
a.forumlink:hover   { text-decoration: underline; color : #3e5998 !important; }
.nav		    { font-weight: bold; font-size: 11px; color : #fff !important;}
a.nav		    { text-decoration: none; color : #fff !important; }
a.topictitle:link   { text-decoration: none; color : #3e5998 !important; }
a.topictitle:visited { text-decoration: none; color : #6d84b4 !important; }
.postdetails	   { font-size : 10px; color : #3e5998 !important; }
a.postlink:link	{ text-decoration: none; color : #3e5998 !important }
a.postlink:visited { text-decoration: none; color : #6d84b4 !important; }
a.postlink:hover { text-decoration: underline; color : #6d84b4 !important}
.maintitle,h1,h2	{color : #fff !important;}
a:link,a:active,a:visited { color : #31abb3 !important; }

.code {
font-family: Courier, 'Courier New', sans-serif; font-size: 11px; color: #f66 !important;
background-color: #d9d9d9 !important;
border: #3e5998 !important; border-style: solid;
border-left-width: 1px; border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px
}
.quote {
	font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 11px; color: #444444; line-height: 125%;
	background-color: #f7f7f7 !important; border: #D1D7DC; border-style: solid;
	border-left-width: 1px; border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px
}
input.post, textarea.post, select {
	background-color : #f1f1f1 !important;
}
input.mainoption {
	background-color : #d9d9d9 !important;
	font-weight : bold;
}
input.liteoption {
	background-color : #d9d9d9 !important;
	font-weight : normal;
}
.helpline { background-color: #f1f1f1 !important; border-style: none; }
.copyright		{ font-size: 10px; font-family: Verdana, Arial, Helvetica, sans-serif; color: #fff !important; letter-spacing: -1px;}

}
