css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("animeonline.su") {
    .button {background-color:#C1433F;color:white !important}
    .select2-container .select2-choice {color:#777777}
    .select2-container .select2-choice {border:1px solid #444444}
    .select2-container-multi .select2-choices {background:none; border:1px solid #444444}
    .select2-default {color:#777777 !important}
    #top_anime .view span {color:#C1433F}
    #top_anime .comm span {color:#C1433F}
    .select2-container .select2-choice div {background:#C1433F}
    .select2-drop {background:#f4f4f4;color:#777777}
    .select2-dropdown-open .select2-choice {background:none; border:1px solid #444444 !important}
    .select2-drop {border:1px solid #C1433F !important; }
    .select2-results .select2-highlighted {background:#C1433F}
    #top_anime .rating div {background-image: url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/rating.png")}
    .select2-container-multi.select2-container-active .select2-choices {border:1px solid #C1433F}
    
    a {color:#C1433F}    
    #panel a {color:#C1433F}
    a:hover {color:#4488BB}
    #panel a:hover {color:#4488BB}
    a:visited {color:#C1433F}
    h1 {color: #777777 !important}
    h2 {color: #777777 !important}
    .ang-slider .paginator a {color: #777777}
    .ang-slider .item {color: #777777 !important}
    .ang-slider .eplink {color: #777777 !important}
    #header {height: 0px !important}
    #toppanel {position:relative !important; min-width:840px !important; background-color: #f4f4f4 !important}
    .sl {display:none !important}
    .sr {display:none !important}
    #panel {min-width:840px !important;  width: 840px !important; box-shadow: none !important}
    #footer {display:none !important}
    #wrapper {width: 840px !important}
    html, body {min-width:840px !important; width:100% !important;}
    #header .center {display: none !important;}
    #content {padding: 0 !important}
    body {color: #777777; background-color:#efefef !important}
    .clearfix {color:#777777}
    
    div.info_ {border-bottom:1px solid #444444; border-left:1px solid #444444; border-right:1px solid #444444; border-top:1px solid #444444}
    div.info_2 {background-color: #efefef !important; border-bottom:1px solid #444444; border-left:1px solid #444444; border-right:1px solid #444444; border-top:1px solid #444444}
    div.new_ {background-color: #efefef !important}
    div.new_ .head_ {width: 100%; background: #d8d8d8; border:none}
    div.new_ div.footer_ {box-shadow: none; background-image: none; border: none; background: #f4f4f4}
    
    #player_info .episodefind {background-color: #ffffff !important; color: #000; border: 1px solid #606060 !important}
    #player_info .inp {color: #fff; background-color: #777777}

    .unit-rating li.current-rating {background-image: url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/ratingstarts2.png")}
    .unit-rating li a:hover {background-image: url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/ratingstarts2.png")}
    .unit-rating {background-image: url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/ratingstarts2.png")}
    .icona {background-image: url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/icona3.png")}
    #findForm input {background-image: url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/icona2.png")}
    #findForm input.active {background-image: url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/icona3.png")}
    div.new_ div.footer_ .views {background-image: url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/news_icons.png"); color:#C1433F}
    div.new_ div.footer_ .comments {background-image: url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/news_icons.png"); color:#C1433F}
    #download button {color:#C1433F}
    #panelLoginButton:hover {color:#C1433F !important; text-shadow: 0 0 5px #C1433F !important}
    
    #fastSearchresults {background-color:rgba(255, 255, 255, 0.9) }
    #fastSearchresults a i {color:#777777 !important}
    
    #panel ul li span, #panel ul li a {color: #000 !important}
    #panel ul ul {background-color:rgba(255, 255, 255, 0.9) !important;z-index:1}
    #panel ul ul a:hover {color:#C1433F !important; text-shadow: 0 0 5px #C1433F}
    #panel .hover .lb {color:#C1433F !important; text-shadow: 0 0 5px #C1433F}
    #panel .hover .gb {background:#C1433F; box-shadow:0 -1px 5px 0 #C1433F}
    
    #version_select button {color:#C1433F}
    #version_select button.active {color:#777777 !important}
    #src_select button {color:#C1433F}
    #src_select button.active {color:#777777 !important}
    div.new_ div.footer_ .white {color:#777777}
    #crew {color:#777777}
    data-ng-show {color: red !important}
    .com_date {color:#C1433F}
    
    .title_spoiler {background-color: #d8d8d8}
    .text_spoiler {background-color: #efefef}
    .title_quote {background-color: #d8d8d8}
    .quote {background-color: #efefef}
     
  /*.ang-slider {display:none}*/
    .ang-slider .hoverme:hover .img_cont {border: 1px solid #C1433F}
    .ang-slider .eplink:hover {border-bottom: 1px dashed #C1433F; color: #C1433F !important;}
    .com_table {border:1px solid #444444}
    .com_cominfo {border-bottom:1px solid #444444}
    .ang-slider .item.active {background-color:#D0D0D0}
    .ang-slider .hoverme:hover .watch:hover {background:url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/play.png") no-repeat scroll center center rgba(193, 67, 63, 0.3) !important}
    .ang-slider .watch {background:url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/play.png") no-repeat scroll center center rgba(0, 0, 0, 0) !important}
    .ang-slider .eplink:hover {background:url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/play.png") no-repeat scroll center 30px rgba(0, 0, 0, 0)}
    .ang-slider .prev, .ang-slider .next {background-image:url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/arrows2.png")}
    .ang-slider .episodeActions span:hover {background-color:#C1433F;color:#fff}
    .ang-slider .watched {background-color:#C1433F;color:rgba(255, 255, 255, 0.9)}
    .ang-slider .episodeActions span.on {color:#fff}
    #panelEpisodes .number {background-color: #C1433F !important;color:#fff !important}
    .episodesAnime .label_rus {color:#C1433F}
    .episodesAnime .anime_link:hover span {color:#C1433F !important}
    #notices ul a:hover {background:#dedede;}
    #notices {padding-top:0;margin-top:45px !important;}
    #panel .menuTab.hover .l {color:#fff !important}
    
    #scroll_up {color:#fff;background-color: #C1433F; background-image:url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/scroll_up-w.png")}
    #reformal_tab img {display:none}
    
    .navigation a:link, .navigation a:visited, .navigation a:hover {background:none;border:1px solid #444444;color:#777777}
    .navigation a:hover {border-color:#C1433F;color:#fff;background:#C1433F}
    .navigation span {border-color:#C1433F;color:#fff;background:#C1433F}
    
    .ang-slider .paginator a:hover {border-color:#C1433F;color:#fff;background:#C1433F}
    .ang-slider .paginator a {background:none;border:1px solid #444444;color:#777777}
    .ang-slider .paginator a.active {border-color:#C1433F;color:#fff;background:#C1433F}
    
    div.new_ .head_, div.new_ .footer_ {color:#C1433F}
    
    /* User login */
    #loginWindow {background:#f4f4f4}
    #loginWindow h4 {color:#777777}
    #loginWindow .text {color:#777777;border:1px solid #444444}
    
    /* More Buttons */
    .bbcodes {background:none;border:1px solid #444444;color:#777777}
    .f_input {background:none;border:1px solid #444444;color:#777777}
    .main_block_links a {border-color:#C1433F;color:#fff;background:#C1433F}
    
    /* Кнопки пикчи */
    #rotator .slider .prev {background-image:url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/sprite.png")}
    #rotator .slider .next {background-image:url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/sprite.png")}
    #rotator .shapka a.active {color:#fff;background-image:url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/sprite.png")}
    #rotator .shapka a {color:#C1433F;text-shadow:none}
    #rotator .shapka {background:#f4f4f4}
    #rotator .slider .item .head {color:#C1433F}
    
    /* icona */
    .icona-24-notwatch {background-image:url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/icona3.png")}
    .icona-20-done {background-image:url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/icona3.png")}
    .icona-19-watch {background-image:url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/icona3.png")}
    .icona-22-time {background-image:url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/icona3.png")}
    li.active .icona-24-notwatch {background-image:url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/icona3.png")}
    li.active .icona-20-done {background-image:url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/icona3.png")}
    li.active .icona-19-watch {background-image:url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/icona3.png")}
    li.active .icona-22-time {background-image:url("https://dl.dropboxusercontent.com/u/34087831/Styles/AOS/icona3.png")}
    .wrap-dd-select .dropdown {background-color:rgba(255, 255, 255, 0.9)}
    .wrap-dd-select .dropdown li a {color:#777777}
    .wrap-dd-select .dropdown li:hover a {color:#C1433F}
    .wrap-dd-menu .dropdown {background-color:rgba(255, 255, 255, 0.9)}
    .infopop {background:#f4f4f4;color:#777777;border:4px solid #444444}
    
    /* Панель оповещений */
    #panel ul ul li {color:#777777 !important}
    #panel .menuTab {background-color:#f4f4f4}
    #panel .menuTab.hover {background:#C1433F}
    #notices .footerLink a:hover {background-color:#C1433F !important}
    #notices .footerLink a {background-color:#f4f4f4}
    .hover .panelAva img {box-shadow:0 0 3px 1px #C1433F}
    div#full_news h1 {text-shadow:none;background:none}
    #notices {padding-top:0 !important}
    
    /* User Menus */
    #search_tabs a.active span.l {background-color: #C1433F; color:#fff}
    #search_tabs a span.n {color:white}
    .filterinput {background:none;border:1px solid #444444;color:#777777}
    .filterinput:focus {border:1px solid #C1433F;color:#777777}
    #myanimeCtrl .label_rus {color:#C1433F !important}
    #myanimeCtrl .label_eng {color:#777777 !important}
    
    /* add a comment */
    div.new_com .head_ .date_ {background:none}
    div.new_com .head_ {background:#d8d8d8; color:#C1433F}
    div.info_com {border:1px solid #444444}
    div.new_com div.footer_ {background:#d8d8d8}
    div.new_com div.brbg_ {background:none}
    #comments {background:none}
   
    /* AdBlock */
    body.loaded {background-image:none}
    #div-gpt-ad-1368777909502-6 {display:none}
    
    /* load */
    #loading-layer {background:#efefef;border:1px solid #444444}
    
    /* report */
    .ui-widget-content {background:#efefef;border:1px solid #444444;color:#777777}
}
