css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com") {
body {

background: #423d49 url('https://31.media.tumblr.com/70199b2e8e88bab5689eca235702ec12/tumblr_n4d7gbCJfo1qduolvo1_r2_500.png') no-repeat bottom right fixed! important;

}

.search_form_row {
    background-color: rgba(4,0,30,.3) !important;
}

.selection_nipple:not([class="selection_nipple white"]) {

  border-bottom: solid 11px rgba(0,0,0,0.1)  !important;

}

ol#posts li blockquote,

body.mceContentBody blockquote {

margin-left: 0 !important;

margin-right: 0 !important;


padding-left: 10px !important;

border-width: 4px !important;

border-color: #000000 !important;

}

ol#posts li blockquote blockquote,

body.mceContentBody blockquote blockquote {

border-color: #000000 !important;

}

ol#posts li blockquote blockquote blockquote,

body.mceContentBody blockquote blockquote blockquote {

border-color: #000000 !important;

}

ol#posts li blockquote blockquote blockquote blockquote,

body.mceContentBody blockquote blockquote blockquote blockquote {

border-color: #000000 !important;

}

ol#posts li blockquote blockquote blockquote blockquote blockquote,

body.mceContentBody blockquote blockquote blockquote blockquote blockquote {

border-color: #000000 !important;

}

.l-content {


    background: rgba(27,0,45,0.1) !important;

    -webkit-border-radius: -5px;

    -moz-border-radius: -5px;

    border-radius: -5px;

    margin: auto;

    padding: -5px -5px;

  }

.l-header-container {background: #423d49!important; background-image:none!important; padding:5px 0px; max-width:3980px!important;}
.l-content {background:transparent!important; border-radius:0px !important; text-shadow:none !important;}

#content_top{opacity:0.0;filter:alpha(opacity=0);background:#fff!important;}
#content_bottom{opacity:0.0;filter:alpha(opacity=0);background:#fff!important;}
.content_cap{opacity:0.0;filter:alpha(opacity=0);background:#fff!important;}

.compose-button {border-color:#fff!important; background-color:#423d49!important; color:#fff!important;}

#left_column, #right_column {
  background-color: transparent;
}

#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {

    background: none;


  }

#right_column .dashboard_nav_item {

    background: none repeat-x;

    background-attachment: initial;

    background-position-x: 50%;

    background-position-y: 100%;

    background-origin: initial;

    background-clip: initial;

    background-color: initial;

}

#right_column .dashboard_nav_item #hide_radar {

background-image: none;

background-repeat-x: no-repeat;

background-repeat-y: repeat;

background-attachment: initial;

background-position-x: 0%;

background-position-y: 0%;

background-origin: initial;

background-clip: initial;

background-color: #131313;

}

#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {

background: rgba(0,0,0,0.0);

}

#nav .nav_item.active {

    background: rgba(00,0,0,1) !important;

  }

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

    background: rgba(00,0,0,0.5) !important;

  }

#auto_pagination_loader {
background: transparent!important;
border: 0px!important;
font-weight: normal;
font-family: Georgia, serif;
font-style: italic;
color: #c3c3c3;
}

ol#posts li.notification.alt {

    background-color: rgba(0,0,0,0.5);

}

ol#posts li.notification.first_notification {

    background-color: rgba(0,0,0,0.5);

}

ol#posts li.notification {

    background-color: rgba(0,0,0,0.5);

    border-bottom: 1px solid #000000;

}

ol#posts li.notification.last_notification {

    border-bottom: 1px solid #000000;

}

ol#posts li.post .permalink {

    background: rgba(00,0,0,0.5);

}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {

background: rgba(0,0,0,0.8);

border: solid #000000;

}

ol#posts li.post .post_avatar{

border-bottom: 0px solid #000000;

}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {

border-top: 1px solid #000000;

border-bottom: 1px solid #000000;

}

#right_column a#dashboard_switch_blog_menu_current:hover {

background: none;

}

#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {

background: none;

}

form.dashboard_options_form {

background-color: rgba(255, 255, 255, 1);

border-bottom: 1px solid #000000;

border-bottom-left-radius: 10px 10px;

border-bottom-left-radius: 10px 10px;

border-bottom-right-radius: 10px 10px;

border-bottom-right-radius: 10px 10px;

border-top-left-radius: 10px 10px;

border-top-left-radius: 10px 10px;

border-top-right-radius: 10px 10px;

border-top-right-radius: 10px 10px;

color: #000000;

display: block;

font-size: 13px;

margin-bottom: 25px;

padding: 15px 22px;

}

form.dashboard_options_form .option_container {

border-top: 1px solid #000000;

}

.no_posts_found, h1.dashboard_header {

color: #131313;

}

a.new_post_label > i {
  height: 90px;
}

.new_post_label {
  padding: 71px 1px 8px 0 !important;
  font-size: 0pt !important;
  text-transform: none;
}
.new_post_label/*_icon*/ > I {
  background-image: url('https://31.media.tumblr.com/591622c22a528412312063ca045d2f75/tumblr_mxl3z3ewNa1qduolvo1_500.png') !important;
  z-index: -1;
}

.new_post_label > I {
  color: transparent !important;
  transition: background-position .08s linear !important;
}
#new_post_label_text > I {background-position: 3px -5px !important}
#new_post_label_photo > I {background-position: -69px -5px !important}
#new_post_label_quote > I {background-position: -141px -5px !important}
#new_post_label_link > I {background-position: -212px -5px !important}
#new_post_label_chat > I {background-position: -284px -5px !important}
#new_post_label_audio > I {background-position: -354px -5px !important}
#new_post_label_video > I {background-position: -428px -5px !important}

#new_post_label_text:hover > I {background-position: 3px -9px !important}
#new_post_label_photo:hover > I {background-position: -69px -9px !important}
#new_post_label_quote:hover > I {background-position: -141px -9px !important}
#new_post_label_link:hover > I {background-position: -212px -9px !important}
#new_post_label_chat:hover > I {background-position: -284px -9px !important}
#new_post_label_audio:hover > I {background-position: -354px -9px !important}
#new_post_label_video:hover > I {background-position: -428px -9px !important}

#new_post_label_text:active > I {background-position: 3px -5px !important}
#new_post_label_photo:active > I {background-position: -69px -5px !important}
#new_post_label_quote:active > I {background-position: -141px -5px !important}
#new_post_label_link:active > I {background-position: -212px -5px !important}
#new_post_label_chat:active > I {background-position: -284px -5px !important}
#new_post_label_audio:active > I {background-position: -354px -5px !important}
#new_post_label_video:active > I {background-position: -428px -5px !important}
}
