/* Make all skills visible */
html>body>table.Paper>tbody>tr>td.DarkColumn>div>form>div {
display:block !important;
}

/* Make the titles black */
html>body>table.Paper>tbody>tr>td.DarkColumn>div>form>b>a {
color:black !important;
padding-left:3px !important; 
} 

/* Highlight Checked Skill */
td>input[checked]+a {
color:#154157 !important; 
font-weight:bold !important; 
background: #FEFFBF !important; 
padding:5px 5px 1px 5px !important;
text-decoration: underline !important;
}

