css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com")
{
    
/* Left bar stuff, still testing */
   
   
	.homeSideNav /* The lower left bar has no background/borders. This adds them (looks better :D) */
	{
		background:
			#FFF !important;

		padding:
			5px;

		margin-top:
			15px;

		margin-left:
			-15px;

		border-radius:
			3px;

		box-shadow:
			0 0 0 1px rgba(0, 0, 0, 0.05);
	}

	#pagelet_welcome_box /* Your name/profile picture on the left "bar" has no background, this adds them */
	{
		background:
			#FFF;

		margin-left:
			-15px;

		padding:
			8px;

		border-radius:
			3px;

		box-shadow:
			0 0 0 1px rgba(0, 0, 0, 0.05);
	}


	.sideNavItem
	{
		margin-left:
			0px
				!important;
		
		transition:
			0.3s;
		
		border:
			1px solid rgba(0, 0, 0, 0);
	}

	.sideNavItem:hover /* Effects when hovering the elements on the left bar, applies to buttons on the left of the element */
	{
		background:
			rgba(0, 0, 0, 0.05);

		border-radius:
			3px;

		border:
			1px solid rgba(0, 0, 0, 0.2);

		transition:
			0.2s;
	}

	.sideNavItem .item:hover /* Effects when hovering the elements on the left bar, applies to the elements itself */
	{
		background:
			rgba(0, 0, 0, 0.025)
				!important;
	}
}
