css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("ebgames.com.au") {

html, #page-header, .body-content, #logo, .clearfix, #footer, body,
#content, .bdr-main, .ui-tabs-nav,
.ui-tabs-selected, .ui-corner-top,
table.shopping-cart tbody tr.product td.multi-row-top, table.invoice-history tbody tr.invoice td.multi-row-top, table.shopping-cart tbody tr.product.multi-row-bottom td, table.shopping-cart tbody tr.product td.thumbnail, table.order-history tbody td.left-border,
.source, .select-table td, .select-table tr:hover td,
#content .dialog-heading h2, .product-details,
table.shopping-cart tbody tr.product td:not(.spacer), table.order-history tbody tr.invoice td:not(.spacer),
.ui-dialog, #collection-page-featured,
#ebworld-levels td, #FreeMembership, #SatisfactionReturns,
#ReceiptFree, #ExpressTrades, #OnlinePortal, #ReviewOnline,
#PreorderHold, #MemberOffers, #SalesEvents, #BonusTradeCredit, #BetaEvents,
#EarlyPreorders, #TradeGift, #LevelUpBonusVoucher, #BirthdayOffer, #VIPEvents, #RequiredCarrots,
.product-details div, .product-details li,
#content .dialog-heading span
{
background: #000 !important;
}
#site-search, .ui-autocomplete, .white-box
{
color: #FFF !important;
background: #000 !important;
}
.ui-dialog
{
border: 1px #FFF solid !important;
}
.date, .activity, .bonus, .carrots, .total
{
color: #FFF !important;
background: #000 !important;
border-top: 1px #FFF solid !important;
}
#navigation a, #navigation .inline-list,
.flyout-menu, .collapsable-filter h3, 
.collapsable-filter a:hover, .float-left,
.inline-item, .sub-menu,
.profile-panel .data-table-row td,
#mini-cart-popover, .shipping,
.prd-stock-status a, .header, .search-filter-btn
{/* Red */
background: #BD0100 !important;
}
#contact-help
{
background-color: #BD0100 !important;
}
#middle-column
{
position: relative !important;
top: 10px !important;
width: 100% !important;
}
#left-panel, #right-panel
{
display: none !important;
}
.purchase-price
{
color: #00ff7f !important;
}
.product-search-result .product-info
{/* blue */
border-color: #004496 !important;
}
div.content .filter-container .collapsable-filter li
{/* Red */
border-color: #BD0100 !important;
}
div.page-controls
{
border-bottom: 1px #004496 solid !important;
}
h1, h2, h3, h4, h5, #breadcrumbs, label, li,
a, .product-information, b, p, .product-summary,
.total-points-label, .total-points-total,
.shopping-cart, th, td, .centered, .inline-item,
.product-categories, .product-department-name,
.two-col, .uppercase, .uppercase span, strong,
#contact-help span, .department-names, .price,
.stock-level, .pseudo-label, .pseudo-label-associated,
.product-details div, legend, .result, .status,
.shipping-and-totals .left-align, .product-details,
.bonus-item-line, .left-align, .title, .was
{
color: #FFF !important;
}
.button, .shopping-cart th, .grey-box, .address-selection,
.note, .confirm-items, .confirmation-box, .confirm-items td,
.success-message, .order-history, .spacer, .subtotal,
.grand-total, .white-box h2, .remaining-amount,
.product-cta
{/* Blue */
background: #004496 !important;
}
}
