css:
@-moz-document domain(strimoid.pl) {

.container {background:none;}
div.col-md-8 {;padding-left:0;width:75%;}
div.col-md-4 {float:right;width:25%}
.content-row .img-thumbnail {width:138px;height:103px;border:1px solid #1c1d1f;padding:3px;border-radius: 0px;
-webkit-transition: none;background:#000;
}

.media .media-body {font:12px Arial;line-height:18px;color:#77818f;}
.media .media-body h4.media-heading {border-bottom:1px solid 

#131416;padding-bottom:3px;}
.media .media-body h4.media-heading a{font:bold 16px Arial;color:#a5b2c5;}
.content-row p.summary {
margin-top:6px;background:#121314;padding:4px 4px 4px 4px;border:1px solid #000;
}

.content-row p.summary small {
font:12px Arial;color:#5e5e5e;
}
.content-row p.summary small a {color:#647287;}
div.col-md-4 .well {padding:10px 10px 10px 

10px;background:#131416;border-radius: 4px;border:1px solid 

#000;-webkit-box-shadow:inset 1px 1px 0 0 #202327;
box-shadow:inset 1px 1px 0 0 #202327;}.input-group-btn>.btn 

{background:#2c323c;border:none;}

.content-row .vote-btn-up {
width: 57px;
font-size: 14px;
padding: 3px 5px;background:#2e333a;border:1px solid #101112;color:#00a10f;margin-top:10px;
}

.content-row .vote-btn-down {
width: 57px;
font-size: 14px;
padding: 3px 5px;background:#0d0d0e;border:1px solid #000;color:#e1000b;
}

.content-row .btn-danger {
color: #FFF;
background-color: #D9534F;
border-color: #D43F3A;
}

.content-row .btn-success {
color: #FFF;
background-color: #2c762c;
border-color: #0d0f0d;
}
a.content_preview_link {background:#5776a0; font:11px Arial;color:#ffffff!important;}

.col-md-8 .content-row {
margin-bottom: 15px;
position: relative;
}

.navbar { background: #141517; border-color: #000; border:none;height:50px;-webkit-box-shadow: 0 2px 3px 0 #0e0e0f;box-shadow: 0 2px 3px 0 #0e0e0f;border-bottom:1px solid #000;opacity:1;} 

.navbar .container { background: #141517; }
.groupbar {background:#0d0e0f;border-bottom:1px solid #1d2023;}
.groupbar>ul li a {font:12px Arial;color:#7e7e7e;}

.navbar-nav>li>a {font:12px Arial; color: #ffffff; padding:17px 25px 16px 25px;}
.navbar-inverse .navbar-brand, .navbar-inverse .navbar-brand  img{margin-top:-3px;margin-right:10px;}
.navbar .navbar-nav .active a, .navbar .navbar-nav .active a:hover, .navbar .navbar-nav .active a:focus { color: #fff; 

background-color: #1e2125; }

li.dropdown.user_dropdown {padding:0px 0px 0px 0px;background:#0f1011;}
li.dropdown.user_dropdown img {width:31px;border:1px solid #000;}
body {background:#181a1d;}

.btn {background:#0e0e0f;border:1px solid #000;}
input.form-control {background:#0e0e0f;border:1px solid #000;}
.col-lg-8 p {font:11px Arial;color:#a8afba;margin-left:20px;margin-top:2px;}
div.well.popular_contents_widget h4 {text-align:center;font:bold 13px Arial;border-bottom:1px solid #000;padding-bottom:6px;margin-bottom:3px;}
div.well.popular_contents_widget li.media {background:#0c0d0d;margin-top:5px;padding:8px;}

div.well.popular_contents_widget li.media h6.media-heading a {color:#5f6e84;}
}
