css:
@namespace url("http://www.w3.org/1999/xhtml");

@-moz-document domain("archiveofourown.org") {

body {
  background: url("http://nsm08.casimages.com/img/2014/03/16//14031603062910339312069902.jpg") fixed no-repeat !important ;
  background-size: 100% auto !important ;
}

#outer {
  background: rgba(255,0,0,0.2) !important ;
}

#footer ul {
  padding: 0 1em !important ;
  margin: 0 !important ;
  width: 98% !important ;
  text-align: center !important ;
  font-size: 0.8em !important ;
}

.notice,
.comment_notice,
ul.notes,
.caution,
.error,
.comment_error {
  background: rgba(255,255,255,0.4) !important ;
  border: dotted white 2px !important ;
}

a:hover {
  color: rgb(220,220,220) !important ;
}

.dropdown-menu {
  background: rgba(255,255,255,0.9) !important ;
  border-radius: 0 20px 20px 20px !important ;
}

#header a:hover {
  color: #B43B18 !important ;
}

#greeting .dropdown-menu {
  border-radius: 20px 0 20px 20px !important ;
}

#greeting {
  background: #F4F4F4 !important ;
  box-shadow: grey 4px 4px 4px !important ;
}

.listbox,
fieldset fieldset.listbox {
  background: -webkit-linear-gradient(left, #910000, #AD3A1A, #8D1A08) !important ;
  background-image: -moz-linear-gradient(left, #910000, #AD3A1A, #8D1A08) !important ;
  border: 2px dotted grey !important ;
}

.listbox .index,
.dashboard .listbox .index {
  padding: 2px 20px !important ;
}

.heading {
  padding-bottom: 10px !important ;
}

.blurb {
  background: rgb(244,244,244) !important ;
  border-radius: 30px 0 30px 0 !important ;
  box-shadow: black 3px 3px 8px !important ;
}

.relationships {
  background: white !important ;
}

.wrapper .meta {
  background: rgba(255,255,255,0.75) !important ;
  border: 2px dashed grey !important ;
  box-shadow: black 3px 3px 8px !important ;
  font-size: 0.8em !important ;
}

dl.meta {
  padding: 0.75em !important ;
}

#workskin {
  color: white !important ;
}

#work_filters fieldset,
.filters fieldset,
#dashboard .navigation {
  background: transparent url("http://archiveofourown.org/images/skins/textures/tiles/red-ao3.png") !important ;
  border: none !important ;
  border-radius: 5px !important ;
}

#dashboard .navigation a:hover {
  color: white !important ;
  background: transparent !important ;
}

#dashboard .navigation {
  border: 1px solid #555555 !important ;
  box-shadow: #555555 2px 2px 10px !important ;
}

#work_filters fieldset a {
  color: white !important ;
}

.cloud, #inner #main.about .userstuff, #inner #main.faq .userstuff, .module .userstuff, .css.module {
  background: rgba(255,255,255,0.2) !important ;
  padding: 20px !important ;
  border-radius: 10px !important ;
}

#dashboard.own {
  border-top: transparent solid 15px !important ;
  border-bottom: transparent solid 15px !important ;
}    
    
    
}
