css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://wikiwiki.jp/diablo3blizz/") {
    body, #backframe, #naviframe, td, th, div {
        background-color: #ffffff !important;
        border-color    : #000000 !important;
        color           : #000000 !important;
    }
    #topframe, #naviframe, #footframe, #naviframe a, #footframe a {
        background-color: #ffffff !important;
        border-color    : #000000 !important;
        color           : #000000 !important;
    }
    #title, h1, h2, h3, h4, h5, table {
        background-color: #ffffff !important;
        border-color    : #000000 !important;
        color           : #000000 !important;
    }
    .title2 {
        background-color: #ffffff !important;
        border-color    : #000000 !important;
        color           : #000000 !important;
    }
    div#topicpath {
        background-color: #ffffff !important;
        border-color    : #000000 !important;
        color           : #000000 !important;}
    div#lastmodified {
        background-color: #ffffff !important;
        border-color    : #000000 !important;
        color           : #000000 !important;
    }
    hr {
        background-color: #ffffff !important;
        border-color    : #000000 !important;
        color           : #000000 !important;
    }
    pre {
        background-color: #ffffff !important;
        border-color    : #000000 !important;
        color           : #000000 !important;
    }
    a {
        background-color: #ffffff !important;
        border-color    : #000000 !important;
        color           : #000000 !important;
    }
    a, span.wikicolor[style="color:orange"] {
        background-color: #ffffff !important;
        border-color    : #000000 !important;
        color           : #000000 !important;
    }
    span.noexists {
        background-color: #ffffff !important;
        border-color    : #000000 !important;
        color           : #000000 !important;
    }

    span.wikicolor[style="color:white"] {color: #000000 !important;}
    span.wikicolor[style="color:White"] {color: #000000 !important;}

    span.wikicolor[style="color:gold"] {color: #000000 !important;}
    span.wikicolor[style="color:Gold"] {color: #000000 !important;}

    span.wikicolor[style="color:deepskyblue"] {color: #000000 !important;}
    span.wikicolor[style="color:Deepskyblue"] {color: #000000 !important;}

    span.wikicolor[style="color:crimson"] {color: #000000 !important;}
	span.wikicolor[style="color:Crimson"] {color: #000000 !important;}

    span.wikicolor[style="color:orange"] {color: #000000 !important;}
    span.wikicolor[style="color:Orange"] {color: #000000 !important;}

    span.wikicolor[style="color:blueviolet"] {color: #000000 !important;}
    span.wikicolor[style="color:Blueviolet"] {color: #000000 !important;}

    span.wikicolor[style="color:forestgreen"] {color: #000000 !important;}
    span.wikicolor[style="color:Forestgreen"] {color: #000000 !important;}

    span.wikicolor[style="color:yellow"] {color: #000000 !important;}
	span.wikicolor[style="color:Yellow"] {color: #000000 !important;}

	span.wikicolor[style="color:lime"] {color: #000000 !important;}
    span.wikicolor[style="color:Lime"] {color: #000000 !important;}

    span.wikicolor[style="color:red"] {color: #000000 !important;}
    span.wikicolor[style="color:Red"] {color: #000000 !important;}
    
    span.wikicolor[style="color:green"] {color: #000000 !important;}
    span.wikicolor[style="color:Green"] {color: #000000 !important;}

	span.wikicolor[style="color:aqua"] {color: #000000 !important;}
	span.wikicolor[style="color:Aqua"] {color: #000000 !important;}

	span.wikicolor[style="color:#10A0FF"] {color: #000000 !important;}
	span.wikicolor[style="color:#70B0F0"] {color: #000000 !important;}
	span.wikicolor[style="color:#FF6050"] {color: #000000 !important;}
    span.wikicolor[style="color:#FFFFE0"] {color: #000000 !important;}
    span.wikicolor[style="color:#B08040"] {color: #000000 !important;}
    span.wikicolor[style="color:#663300"]{color: #000000 !important;}
}
