css:
@namespace url(http://www.w3.org/1999/xhtml);
@namespace svg url(http://www.w3.org/2000/svg);

@-moz-document regexp("^https?://((raw[2-9]*|gist|guides|help|status|developer).)?github.com.*") {
html, body, .sunken-menu.vertical-right .sunken-menu-contents, .sunken-menu .sunken-menu-item.selected ,.tabnav-tab.selected, .social-count, .file-code, .highlight, .commit.file-history-tease .participation, .capped-box, #readme .markdown-body, #readme .plain, .box-body, h3.conversation-list-heading .inner, .commandbar .message, .command-bar:not(.command-bar-focus) input[type="text"], .commit-group-item:nth-child(2n+1), .boxed-group>h3, .boxed-group .heading, .accordion .section a.section-head, .boxed-group .boxed-group-inner, .commit-tease .commit-meta, #readme span.name, .dashboard-tabs a.selected, .dashboard-tabs a.selected:hover, .dashboard-tabs a, .blankslate, .box-header, .timeline-comment-wrapper, .discussion-timeline-actions, .timeline-comment, .timeline-commits-minimal .commit-icon .octicon, .thread-subscription-status, body.page-profile .profilecols .filter-bar, .news .alert .gravatar
{
  background: #f0f0f0 !important;
}

ul.color-label-list .color
{
	border: #f0f0f0;
}

.commit-group-item.navigation-focus
{
	background: #f6f6f6;
}

.header-logged-in
{
	background: #eaeaea; 
}

.header
{
text-shadow: 0 1px 0 #f0f0f0;
}

.timeline-comment-wrapper {
border-top: 2px solid #f0f0f0;
border-bottom: 2px solid #f0f0f0;
}

.discussion-timeline-actions {
border-top: 2px solid #e0e0e0;
}
  
.command-bar:not(.command-bar-focus) input[type="text"] {
border-color: #eaeaea !important;
box-shadow: 0px 0px 0 #f0f0f0 !important;
}

#contributions-calendar svg|rect[style="fill: rgb(238, 238, 238);"],
#contributions-calendar svg|rect[style="fill: #eeeeee;"]
{
  fill: #e0e0e0 !important
}

.site-footer, #contributions-calendar .contrib-details div .lbl
{
    border-top: 1px solid #e0e0e0;
}  

.sunken-menu.vertical-right .sunken-menu-contents
{
	box-shadow:inset 1px 0 0 #eee !important;
    background-image:-webkit-linear-gradient(left, #f0f0f0 0%, #f0f0f0 8px) !important;
    background-image:-moz-linear-gradient(left, #f0f0f0 0%, #f0f0f0 8px) !important;
    background-image:linear-gradient(to right, #e0e0e0 0%, #f0f0f0 8px) !important;
}

.sunken-menu.vertical-right .sunken-menu-contents:before {
  background-color: rgba(255, 255, 255, 0);
  background-image: -moz-linear-gradient(#f0f0f0, rgba(240,240,240,0));
  background-image: -webkit-linear-gradient(#f0f0f0, rgba(240,240,240, 0));
  background-image: linear-gradient(#f0f0f0, rgba(240,240,240, 0));
  background-repeat: repeat-x;
  background-color: rgba(0, 0, 0, 0);
  top: 0;
}

.sunken-menu.vertical-right .sunken-menu-contents:after {
background-color: #FFF;
background-image: -moz-linear-gradient(rgba(240,240,240,0), #f0f0f0);
background-image: -webkit-linear-gradient(rgba(240,240,240, 0), #f0f0f0);
background-image: linear-gradient(rgba(240,240,240, 0), #f0f0f0);
background-repeat: repeat-x;
background-color: rgba(0, 0, 0, 0);
bottom: 0;
}



.sunken-menu.vertical-right .sunken-menu-separator:before {
width: 100%;
height: 1px;
top: 0px;
border-top: 1px solid #eeeeee \9;
background-image: -webkit-linear-gradient(left, #e0e0e0 70%, #f0f0f0 100%);
background-image: -moz-linear-gradient(left, #e0e0e0 70%, #f0f0f0 100%);
background-image: linear-gradient(to right, #e0e0e0 70%, #f0f0f0 100%);
}

.file .meta {
padding: 5px 10px;
font-size: 12px;
text-align: left;
color: #555;
text-shadow: 0 1px 0 #FFF;
border-bottom: 1px solid #D8D8D8;
background-color: #EAEAEA;
background-image: -moz-linear-gradient(#f0f0f0, #f0f0f0);
background-image: -webkit-linear-gradient(#f0f0f0, #f0f0f0);
background-image: linear-gradient(#f0f0f0, #f0f0f0);
}

.button, .minibutton {
background-color: #EEE;
background-image: -moz-linear-gradient(#f0f0f0, #f0f0f0);
background-image: -webkit-linear-gradient(#f0f0f0, #f0f0f0);
background-image: linear-gradient(#f0f0f0, #f0f0f0);
}

.button.primary, .minibutton.primary {
color: #FFF;
text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);
background-color: #60B044;
background-image: -moz-linear-gradient(#8add6d, #8ADD6D);
background-image: -webkit-linear-gradient(#8ADD6D, #8ADD6D);
background-image: linear-gradient(#8ADD6D, #8ADD6D);
background-repeat: repeat-x;
border-color: #5CA941;
}

.boxed-group.dangerzone>h3 {
background-color: #f97171 !important;
}
}
