@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/*
 * betterAustralis v 1.0
 * by TRACKER88 (Robert Dłubak) - http://gplus.to/robertd
 * Bazuje na mockup'ie Stephen Horlander'a: https://people.mozilla.org/~shorlander/mockups-interactive/australis-interactive-mockups/windows8.html
 */

/*
 * Tabs / Karty
 */
#TabsToolbar {
  background: none !important;    
}
/*
 * Fix na tło w stylach Win 7 opartych o pseudo modern ui
 */
#main-window #TabsToolbar:after {
    display: none !Important;
}
#main-window[sizemode="normal"] #TabsToolbar[tabsontop="true"] {
  padding-top: 2px !important;
}

#TabsToolbar[tabsontop] :-moz-any(.tabbrowser-tab, .tabs-newtab-button) {
  border-width: 0!important;
  padding: 0!important;
  margin-bottom: -1px !important;
  background: none !important;
}

#TabsToolbar[tabsontop] .tabbrowser-tab {
  -moz-box-align: stretch !important;
}

#TabsToolbar[tabsontop] .tabbrowser-tab[pinned] .tab-content {
  margin: 0 2px !important;
}

#TabsToolbar[tabsontop] .tab-background-middle {
  -moz-box-flex: 1 !important;
  border-left: 15px solid rgba(0, 0, 0, 0) !important;
  border-right: 15px solid rgba(0, 0, 0, 0) !important;
  margin: 0 -15px !important;
}

#TabsToolbar[tabsontop] .tab-content {
  -moz-padding-end: 9px !important;
  -moz-padding-start: 9px !important;
}

#TabsToolbar[tabsontop] :-moz-any(.tab-throbber, .tab-icon-image)[pinned] {
  margin:0 !important;
}

#TabsToolbar[tabsontop] :-moz-any(.tab-throbber, .tab-icon-image):not([pinned]) {
  -moz-margin-start: 0 !important;
  -moz-margin-end: 6px !important;
}

#TabsToolbar[tabsontop] .tabbrowser-tab[pinned],
#TabsToolbar[tabsontop] :-moz-any(.tab-background,.tabs-newtab-button):not([pinned]) {
  -moz-margin-end: -15px !important;
  -moz-margin-start: -15px !important;
}

#TabsToolbar[tabsontop] .tabbrowser-tab[pinned]::before {
  -moz-margin-end: -16px !important;
  -moz-margin-start: 15px !important;
}

#TabsToolbar[tabsontop] .tabbrowser-tab[pinned] .tab-background {
  -moz-margin-end: 2px !important;
  -moz-margin-start: 2px !important;
}

#TabsToolbar[tabsontop] .tabbrowser-arrowscrollbox > .arrowscrollbox-scrollbox {
  -moz-padding-end: 15px !important;
  -moz-padding-start: 15px !important;
}

#TabsToolbar[tabsontop] :-moz-any(.tab-background-start,.tab-background-end) {
  min-height: 31px !important;
  width: 30px !important;
}

#TabsToolbar[tabsontop] .tabbrowser-tab:-moz-lwtheme {
  color: inherit !important;
}

#TabsToolbar[tabsontop] .tabbrowser-tab {
  pointer-events: none !important;
}

#TabsToolbar[tabsontop] .tabs-newtab-button {
  width: 66px !important;
}

#TabsToolbar[tabsontop] .tabbrowser-tab[selected="true"]:-moz-lwtheme .tab-content {
  text-shadow: none !important;
  color: #000 !important;
}

.tab-background-start:-moz-locale-dir(ltr),
.tab-background-end:-moz-locale-dir(rtl) {
  clip-path: url(chrome://browser/content/browser.xul#tab-curve-clip-path-start) !important;
}

.tab-background-end:-moz-locale-dir(ltr),
.tab-background-start:-moz-locale-dir(rtl) {
  clip-path: url(chrome://browser/content/browser.xul#tab-curve-clip-path-end) !important;
}

#TabsToolbar[tabsontop] :-moz-any(.tab-background-end, .tab-background-start) {
  clip-path: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg"><clipPath id="tab-curve-clip-path-start" clipPathUnits="objectBoundingBox"><path d="m 1,0.0625 0.05,0 0,0.938 -1,0 0,-0.028 C 0.32082458,0.95840561 0.4353096,0.81970962 0.48499998,0.5625 0.51819998,0.3905 0.535,0.0659 1,0.0625 z"/></clipPath></svg>#tab-curve-clip-path-start') !important;
}

#TabsToolbar[tabsontop] .tab-background-end:-moz-locale-dir(ltr),
#TabsToolbar[tabsontop] .tab-background-start:-moz-locale-dir(rtl) {
  transform: scaleX(-1) !important;
}

:-moz-any(.tab-background-start, .tab-background-end)[selected=true]:not(:-moz-lwtheme) {
  background: linear-gradient(#FAF9FA, #F6F6F6) !important;
}

#main-window .tab-background-middle[selected=true]:not(:-moz-lwtheme) {
  background-clip: padding-box, content-box !important;
  background-image: linear-gradient(transparent, transparent 2px, #FAF9FA 2px, #FAF9FA 2px, #F6F6F6), none !important;
  background-repeat: repeat-x !important;
  background-size: auto 100% !important;
}

#TabsToolbar[tabsontop] .tab-background-middle[selected=true] {
  padding-top: 1.5px !important;
}

:-moz-any(.tab-background-start,.tab-background-end):not(:-moz-lwtheme)::before,
:-moz-any(.tab-background-start,.tab-background-end):not(:-moz-lwtheme)::after {
  display: none !important;
}

:-moz-any(.tab-background-start,.tab-background-end)::after {
  margin-bottom: 0.5px !important;
}

#TabsToolbar[tabsontop] :-moz-any(.tab-background-middle, .tabs-newtab-button, .tab-close-button),
.tab-background-start,
.tab-background-end {
  pointer-events: auto !important;
}

#tabbrowser-tabs:not([overflow="true"]) .tabbrowser-tab[pinned],
.tabbrowser-tab:not([pinned]) {
  position: relative !important;
}

.tabbrowser-tab:hover {
  z-index: 1 !important;
}

.tabbrowser-tab[selected="true"] {
  z-index: 4 !important;
}

#main-window .tabbrowser-tab .tab-content {
  margin: 0 -15px !important;
  padding: 0 24px !important;
}

.tabbrowser-tab:hover:not([selected=true]) .tab-background,
#main-window[privatebrowsingmode] .tabbrowser-tab[selected=true] .tab-background,
#main-window:not([privatebrowsingmode]) .tabbrowser-tab[selected=true]:not(:-moz-lwtheme) .tab-content {
  background-image: url(chrome://browser/skin/tabbrowser/tab-background-start.png),
           url(chrome://browser/skin/tabbrowser/tab-background-middle.png),
           url(chrome://browser/skin/tabbrowser/tab-background-end.png) !important;
  background-position: left bottom, 30px bottom, right bottom !important;
  background-repeat: no-repeat !important;
  background-size: 30px 100%, calc(100% - 60px) 100%, 30px 100% !important;
}

.tabbrowser-tab:hover:not([selected=true]) .tab-background > * {
  background: none !important;
}

.tabbrowser-tab[selected=true]:-moz-lwtheme :-moz-any(.tab-background-end,.tab-background-start) {
  height: 32px !important;
  margin-bottom: -1px !important;
}

.tabbrowser-tab[selected=true]:-moz-lwtheme .tab-background {
  background-image: url(chrome://browser/skin/tabbrowser/tab-stroke-start.png),
           url(chrome://browser/skin/tabbrowser/tab-stroke-end.png) !important;
  background-position: left bottom, right bottom !important;
  background-repeat: no-repeat !important;
  background-size: 31px 100%, 31px 100% !important;
}

.tabbrowser-tab[pinned] .tab-background {
  min-width: 66px !important;
}

#TabsToolbar[tabsontop] .tabbrowser-tab[pinned] .tab-content > image,
.tabbrowser-tab[pinned] .tab-content > image {
  margin-left: 1px !important;
}

#TabsToolbar[tabsontop] .tabbrowser-tab:hover:not([selected=true]) .tab-background,
#main-window[privatebrowsingmode] #TabsToolbar[tabsontop] .tabbrowser-tab[selected=true] .tab-background,
#main-window:not([privatebrowsingmode]) #TabsToolbar[tabsontop] .tabbrowser-tab[selected=true]:not(:-moz-lwtheme) .tab-content,
#TabsToolbar[tabsontop] .tabs-newtab-button:hover {
  background-image: url(data:image/png;base64,),
            url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAYAAAAfCAYAAADEFbwbAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAExJREFUeNpiFJA15mSAAEYwYvj/H0SzAFm6YOH/ECkowcD4+8+fdAYsgAUo/wy7BCPjf6wSUNMxABMDDoBTx1CTIDlIRn1Oy+QDEGAA+9IhPPtNtxgAAAAASUVORK5CYII=),
            url(data:image/png;base64,) !important;
  background-color: transparent !important;
  background-position: left bottom, 30px bottom, right bottom !important;
  background-repeat: no-repeat !important;
  background-size: 30px 100%, calc(100% - 60px) 100%, 30px 100% !important;
}

#TabsToolbar[tabsontop] :-moz-any(.tab-background-end, .tab-background-start, .tab-background-middle)[selected="true"]:-moz-lwtheme {
  background-image: linear-gradient(rgba(250,250,250,0.6), rgba(255,255,255,0.35)) !important;
}

#tabbrowser-tabs[movingtab] > .tabbrowser-tab[beforeselected]:not([last-visible-tab])::after,
.tabbrowser-tab:not([selected]):not([afterselected-visible]):not([afterhovered]):not([first-visible-tab]):not(:hover)::before,
#tabbrowser-tabs:not([overflow]) > .tabbrowser-tab[last-visible-tab]:not([selected]):not([beforehovered]):not(:hover)::after {
  background: rgba(24, 25, 26, 0.25) !important;
  content: "" !important;
  display: -moz-box !important;
  margin: 3px -0.5px 1px !important;
  width: 1px !important;
}

#tabbrowser-tabs[movingtab]:-moz-lwtheme-brighttext > .tabbrowser-tab[beforeselected]:not([last-visible-tab])::after,
.tabbrowser-tab:not([selected]):not([afterselected-visible]):not([afterhovered]):not([first-visible-tab]):not(:hover):-moz-lwtheme-brighttext::before,
#tabbrowser-tabs:not([overflow]):-moz-lwtheme-brighttext > .tabbrowser-tab[last-visible-tab]:not([selected]):not([beforehovered]):not(:hover)::after {
  background: rgba(240, 250, 260, 0.4) !important;
}
/*
 * Remove border radius / Wywalenie border-radius
 */
#main-menubar,
#downloads-indicator-progress > spacer,
#downloads-indicator-progress,
#nav-bar, #appmenu-button, .findbar-textbox, .autocomplete-richlistitem,
#searchFilter, #searchbar .searchbar-textbox, #urlbar,
panelview toolbarbutton, #widget-overflow-list > toolbarbutton,
.customizationmode-button,
menu, menuitem {
  border-radius: 0 !important;
}

/*
 * Toolbars
 */
#nav-bar:not(:-moz-lwtheme) {
  box-shadow: 0 -1px 1px rgba(0, 0, 0, 0.1) !important;
}

#nav-bar[tabsontop]:not(:-moz-lwtheme) {
  box-shadow: 0 -1px 1px rgba(0, 0, 0, 0.25) !important;
}

#main-window[customizing="true"] #navigator-toolbox > toolbar:not(#TabsToolbar):not(#toolbar-menubar) {
  visibility: visible !important;
  border-left: 1px solid rgba(10, 31, 51, 0.35) !important;
  border-right: 1px solid rgba(10, 31, 51, 0.35) !important;
}

#main-window[customizing="true"] #PersonalToolbar {
  visibility: visible !important;
}

#placesToolbar, #placesList, #detailsDeck,
#main-window:not([privatebrowsingmode]) #navigator-toolbox > toolbar:not(#TabsToolbar):not(#toolbar-menubar):not(:-moz-lwtheme),
#browser-bottombox:not(:-moz-lwtheme),
.browserContainer > findbar {
  background: #F8F8F8 !important;
}

#placesToolbar {
  padding: 4px !important;
}

#placeContent {
  background: #FDFDFD !important;
}

#PersonalToolbar {
  padding: 0px 5px 4.5px !important;
}

#personal-bookmarks {
  min-height: 0 !important;
}

#PersonalToolbar toolbarbutton {
  -moz-appearance: none !important;
}

#PersonalToolbar toolbarbutton:not([type=menu-button]) {
  -moz-appearance: none !important;
  margin: 0 !important;
  padding: 1px 4px !important;
}

#placesToolbar :-moz-any(toolbarbutton,menu,menuitem) {
  -moz-appearance: none !important;
  padding: 1px 4px !important;
}

#nav-bar-customization-target > .toolbarbutton-1 .toolbarbutton-icon {
  max-height: 24px !important;  
}

#navigator-toolbox::after {
  background: hsl(0, 0%, 80%) !important;
}

/*Prevent hidding toolbars on about: pages*/
#main-window[disablechrome=true] #navigator-toolbox[tabsontop="true"] toolbar:not([collapsed="true"]) {
    visibility: visible !important;
}

#main-window[disablechrome=true] #navigator-toolbox[tabsontop="true"]::after {
  visibility: visible !important;
}

/*
 * Back-Forward button / Wstecz-Dalej
 */
#nav-bar[currentset*="unified-back-forward-button,urlbar-container"] :-moz-any(#back-button,#forward-button) > .toolbarbutton-icon,
#nav-bar-customization-target :-moz-any(#back-button,#forward-button) > .toolbarbutton-icon {
  border: 1px solid rgba(24, 25, 26, 0.35) !important;
  background: rgba(250,251,252,.5)!important;
  box-shadow: none !important;
  transition-property: background-color, box-shadow !important;
}

#nav-bar[currentset*="unified-back-forward-button,urlbar-container"]:not(:-moz-lwtheme) #forward-button > .toolbarbutton-icon,
#nav-bar-customization-target:not(:-moz-lwtheme) #forward-button > .toolbarbutton-icon {
  border-color: rgba(23, 51, 78, 0.25) rgba(23, 51, 78, 0.27) rgba(23, 51, 78, 0.3) !important;
}

#nav-bar[currentset*="unified-back-forward-button,urlbar-container"] > #back-button > .toolbarbutton-icon {
  padding: 4px !important;
}

#main-window:-moz-any([privatebrowsingmode="temporary"],:-moz-lwtheme) #nav-bar[currentset*="unified-back-forward-button,urlbar-container"] :-moz-any(#back-button,#forward-button) > .toolbarbutton-icon,
#main-window:-moz-any([privatebrowsingmode="temporary"],:-moz-lwtheme) #nav-bar-customization-target :-moz-any(#back-button,#forward-button) > .toolbarbutton-icon {
  border: 1px solid rgba(24, 25, 26, 0.4) !important;
  background: rgba(250,251,252,.5)!important;
  box-shadow: none !important;
  transition-property: background-color, box-shadow !important;
}

#main-window:-moz-any([privatebrowsingmode="temporary"],:-moz-lwtheme-brighttext) #nav-bar[currentset*="unified-back-forward-button,urlbar-container"] #back-button > .toolbarbutton-icon,
#main-window:-moz-any([privatebrowsingmode="temporary"],:-moz-lwtheme-brighttext) #nav-bar-customization-target #back-button > .toolbarbutton-icon {
  border: 1px solid rgba(240, 250, 260, 0.3) !important;
}

#nav-bar[currentset*="unified-back-forward-button,urlbar-container"] :-moz-any(#back-button,#forward-button):not([disabled="true"]):not([open="true"]):not(:active):hover > .toolbarbutton-icon,
#nav-bar-customization-target :-moz-any(#back-button,#forward-button):not([disabled="true"]):not([open="true"]):not(:active):hover > .toolbarbutton-icon {
  background: rgba(24, 25, 26, 0.08) !important;
  box-shadow: none !important;
}

#main-window:-moz-any([privatebrowsingmode="temporary"],:-moz-lwtheme) #nav-bar[currentset*="unified-back-forward-button,urlbar-container"] :-moz-any(#back-button,#forward-button):not([disabled="true"]):not([open="true"]):not(:active):hover > .toolbarbutton-icon,
#main-window:-moz-any([privatebrowsingmode="temporary"],:-moz-lwtheme) #nav-bar-customization-target :-moz-any(#back-button,#forward-button):not([disabled="true"]):not([open="true"]):not(:active):hover > .toolbarbutton-icon {
  background: rgba(240, 250, 260, 0.7) !important;
}

#nav-bar[currentset*="unified-back-forward-button,urlbar-container"] :-moz-any(#back-button,#forward-button):not([disabled="true"]):hover:active > .toolbarbutton-icon,
#nav-bar[currentset*="unified-back-forward-button,urlbar-container"] :-moz-any(#back-button,#forward-button)[open="true"] > .toolbarbutton-icon,
#nav-bar-customization-target :-moz-any(#back-button,#forward-button):not([disabled="true"]):hover:active > .toolbarbutton-icon,
#nav-bar-customization-target :-moz-any(#back-button,#forward-button)[open="true"] > .toolbarbutton-icon {
    background: #D8DADC !important;
    box-shadow: 0 1px 0 0 rgba(24, 25, 26, 0.1) inset !important;
}

#nav-bar[currentset*="unified-back-forward-button,urlbar-container"] #forward-button > .toolbarbutton-icon,
#nav-bar-customization-target #forward-button > .toolbarbutton-icon {
  clip-path: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg"><clipPath id="forward-btn-clip-path" clipPathUnits="objectBoundingBox"> <path d="m 0,0 c .3,.25 .3,.75, 0,1 l 1,0 0,-1 z"/></clipPath></svg>#forward-btn-clip-path')  !important;
  border-left-style: none !important;
  border-radius: 0 !important;
}

#nav-bar[currentset*="unified-back-forward-button,urlbar-container"] #forward-button[disabled=true] > .toolbarbutton-icon,
#nav-bar-customization-target #forward-button[disabled=true] > .toolbarbutton-icon {
  margin-left: -6px !important;
}

#main-window:not([customizing]) #back-button[disabled] > .toolbarbutton-icon {
  box-shadow: none !important;
}
/*
 * Global buttons / Globalne przyciski
 */
.toolbarbutton-menubutton-button {
  -moz-margin-end: -0.5px !important;
}

.toolbarbutton-menubutton-dropmarker {
  -moz-margin-start: -0.5px !important;
}

.toolbarbutton-menubutton-dropmarker:before {
  box-shadow: none !important;
}

#main-window:-moz-any([privatebrowsingmode="temporary"],:-moz-lwtheme) .toolbarbutton-menubutton-dropmarker:before {
  background: rgba(240, 250, 260, 0.3) !important;
}

menu,
splitmenu,
.splitmenu-menuitem,
menuitem:not(.panel-subview-footer) {
  -moz-appearance: none !important;
  min-height: 23px !important;
  height: 23px !important;
  padding: 0 !important;
  margin: 0 4px !important;
}

.subviewbutton:not(.panel-subview-footer) {
  min-height: 23px !important;
  height: 23px !important;
}

.subviewbutton:not(.panel-subview-footer) > .toolbarbutton-text {
  padding-top: 0 !important;
  padding-bottom: 0 !important;
}

menu .menu-right {
  -moz-appearance: none !important;
  -moz-margin-start: 4px !important;
  -moz-margin-end: 6px !important;
  height: 10px !important;
  width: 10px !important;
  background: url("chrome://global/skin/arrow/arrow-rit-sharp.gif") center no-repeat !important;
  opacity: .7 !important;
}

.splitmenu-menuitem,
.splitmenu-menu {
  margin:0 !important;
}

.splitmenu-menuitem {
  -moz-margin-end: -1px !important;
}

.menu-iconic-left {
  -moz-appearance: none !important;
  min-width: 16px !important;
  width: 16px !important;
  padding: 0 !important;
  margin:0 6px !important;
}

.splitmenu-menuitem .menu-iconic-left {
  margin-bottom: 1px !important;
}

.menu-text {
  -moz-appearance: none !important;
  -moz-padding-start: 28px !important;
}

menuitem[checked="true"]::before {
  display: -moz-box !important;
  -moz-box-align: center !important;
  -moz-box-pack: center !important;
  width: 16px !important;
  height: 14px !important;
  margin: 0 6px !important;
}

menuitem[type="checkbox"][checked="true"]::before {
  content: "" !important;
  background: url("chrome://global/skin/menu/shared-menu-check.png") no-repeat center center !important;
  background-size: 14px !important;
}

.subviewbutton[checked="true"] {
  background-size: 14px !important;
}

menuitem[type="radio"][checked="true"]::before {
  content: "" !important;
  background: radial-gradient(circle, #505559 4px, #505559 4px, rgba(255, 255, 255, 0) 4px, rgba(255, 255, 255, 0) 100%) !important;
}

menuitem[checked="true"] > .menu-iconic-left {
  display: none !important;
}

#nav-bar > toolbarbutton:not(.toolbarbutton-1) > .toolbarbutton-icon,
#nav-bar-customization-target > toolbarbutton:not(.toolbarbutton-1) > .toolbarbutton-icon {
  margin: 0 5px !important;
}

:-moz-any(menu,menuitem,.splitmenu-menuitem, splitmenu):not(.panel-subview-footer),
#main-window :-moz-any(menu,menuitem,.splitmenu-menuitem, splitmenu):not(.panel-subview-footer),
.autocomplete-richlistitem,
:-moz-any(#navigator-toolbox, #browser-bottombox) toolbar:not(#TabsToolbar):not(#nav-bar):not(#developer-toolbar) toolbarbutton,
tree[disabled="true"] > treechildren::-moz-tree-row,
treechildren:not(.autocomplete-treebody)::-moz-tree-row,
.findbar-container toolbarbutton:not([class^="findbar-find"]),
#nav-bar:not([currentset*="unified-back-forward-button,urlbar-container"]) #unified-back-forward-button .toolbarbutton-1 > .toolbarbutton-icon,
#nav-bar .toolbarbutton-1:not(#back-button):not(#forward-button) > :-moz-any(.toolbarbutton-icon, .toolbarbutton-text, .toolbarbutton-badge-container),
#nav-bar .toolbarbutton-1 > .toolbarbutton-menubutton-button > .toolbarbutton-icon, 
#nav-bar .toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker > .dropmarker-icon {
  background: none repeat scroll 0 0 padding-box rgba(231, 237, 242, 0) !important;
  border: 1px solid rgba(23, 51, 78, 0) !important;
  border-radius: 0 !important;
  box-shadow: 0 1px rgba(255, 255, 255, 0) inset, 
        0 1px rgba(23, 51, 78, 0), 
        0 0 2px rgba(23, 51, 78, 0) !important;
  transition-duration: 250ms !important;
  transition-property: background-color, border-color !important;
}

:-moz-any(menu,menuitem,.splitmenu-menuitem,splitmenu)[disabled="true"]:hover,
#main-window :-moz-any(menu,menuitem,.splitmenu-menuitem,splitmenu)[disabled="true"]:hover,
treechildren:not(.autocomplete-treebody)::-moz-tree-row(selected,current),
#nav-bar .toolbarbutton-1:-moz-any(:hover, [open]):not([disabled]) > .toolbarbutton-menubutton-button > .toolbarbutton-icon, 
#nav-bar .toolbarbutton-1:hover:not([disabled]) > .toolbarbutton-menubutton-dropmarker > .dropmarker-icon {
  background: rgba(24, 25, 26, 0.02) !important;
  border: 1px solid rgba(24, 25, 26, 0.1) !important;
  box-shadow: none !important;
}

#main-window .splitmenu-menuitem[_moz-menuactive],
:-moz-any(menu,menuitem):-moz-any(:active:hover,:hover,[open],[checked="true"],[selected="true"]):not([disabled="true"]):not(.panel-subview-footer),
#main-window :-moz-any(menu,menuitem):-moz-any(:active:hover,:hover,[open],[checked="true"],[selected="true"]):not([disabled="true"]):not(.panel-subview-footer),
.autocomplete-richlistitem[selected="true"],
.findbar-container toolbarbutton:hover:not([class^="findbar-find"]):not([disabled]),
:-moz-any(#navigator-toolbox, #browser-bottombox) toolbar:not(#TabsToolbar):not(#nav-bar):not(#developer-toolbar) toolbarbutton:hover:not([disabled]),
treechildren:not(.autocomplete-treebody)::-moz-tree-row(hover,current),
treechildren:not(.autocomplete-treebody)::-moz-tree-row(hover),
#nav-bar:not([currentset*="unified-back-forward-button,urlbar-container"]) #unified-back-forward-button .toolbarbutton-1:hover:not([disabled]):not([open]):not(:active) > .toolbarbutton-icon,
