#downloadsRichListBox .downloadButton,
#downloadsPanel #downloadsListBox .downloadButton {
  border-left: 1px solid rgba(24, 25, 26, 0.15) !important;
  padding: 0 12px !important;
  margin: 6px 0 !important;
  height: 42px !important;
}

#downloadsRichListBox > richlistitem.download[selected] .downloadButton,
#downloadsRichListBox > richlistitem:hover .downloadButton,
#downloadsPanel #downloadsListBox > richlistitem:hover .downloadButton {
  margin: 0 !important;
  height: 54px !important;
}

:-moz-any(#downloadsRichListBox,#downloadsListBox) > richlistitem > vbox:hover,
#downloadsRichListBox .downloadButton:hover,
#downloadsPanel #downloadsListBox .downloadButton:hover {
  background-color: rgba(24, 25, 26, 0.04) !important;
}

:-moz-any(#downloadsRichListBox,#downloadsListBox) > richlistitem:not([state="2"]):not([state="3"]) > vbox:hover:active,
#downloadsRichListBox .downloadButton:hover:active,
#downloadsPanel #downloadsListBox .downloadButton:hover:active {
  background-color: rgba(24, 25, 26, 0.15) !important;
  box-shadow: 0 2px 0 0 rgba(24, 25, 26, 0.1) inset !important; 
}

.downloadButton.downloadCancel {
  -moz-image-region: rect(0px, 16px, 16px, 0px) !important;
}

.downloadButton.downloadShow {
  -moz-image-region: rect(16px, 16px, 32px, 0px) !important;
}

.downloadButton.downloadRetry {
  -moz-image-region: rect(32px, 16px, 48px, 0px) !important;
}

#downloadsHistory { 
  color: inherit !important;
  text-align: center !important;
  flex: 1;
}

:-moz-any(#downloadsRichListBox,#downloadsListBox) > richlistitem .downloadProgress .progress-bar,
#downloads-button:not([paused]) #downloads-indicator-progress .progress-bar {
  -moz-appearance: none !important;
  background: #0082EA !important;
  border-color: transparent !important;
}

#downloads-button:not([paused])[open="true"] #downloads-indicator-progress .progress-bar {
  background: #FFF !important;
}

:-moz-any(#downloadsRichListBox,#downloadsListBox) > richlistitem[state="4"] .downloadProgress .progress-bar,
#downloads-button[paused] #downloads-indicator-progress .progress-bar {
  background-color: #808080 !important;
  background-image: repeating-linear-gradient(-45deg, rgba(0, 0, 0, 0), rgba(0, 0, 0, 0) 3px, rgba(255, 255, 255, 0.2) 3px, rgba(255, 255, 255, 0.2) 6px) !important;
}

#downloads-indicator-progress .progress-remainder {
  background: #CCC !important;
  border-color: transparent !important;
}

/*
 * Identity panel
 */
#identity-popup-button-container > spacer {
  min-width: 1px !important;
  max-width: 1px !important;
  background: rgba(24, 25, 26, 0.15) !important;
  margin: 6px 0 !important;
}

#identity-popup-button-container:hover > spacer {
  margin: 0 !important;
}

#identity-popup-help-icon {
  width: 46px !important;
  list-style-image: url("chrome://browser/skin/menuPanel-help.png") !important;
  -moz-image-region: rect(0px, 16px, 16px, 0px) !important;
}

#editBookmarkPanelBottomButtons > button,
#identity-popup-more-info-button {
  flex: 1 !important;
  color: #222426 !important;
  font-size: 11px !important;
}

/*
 * Zakładki
 */
#editBookmarkPanel {
  font-size: 11px !important;
}

#editBookmarkPanel .panel-arrowcontent {
  padding: 0 !important;
}

#editBMPanel_rows row > * {
  margin-top: 0 !important;
  margin-bottom: 0 !important;
}

#editBMPanel_rows > row {
  margin: 0 0 8px !important;
}

row#editBookmarkPanelHeader {
  padding: 12px !important;
  border-bottom: 1px solid rgba(24, 25, 26, 0.15) !important;
  margin-bottom: 12px !important;
}

#editBookmarkPanelStarIcon {
  margin-right: 8px !important;
  list-style-image: url("chrome://browser/skin/places/bookmarks-notification-finish.png") !important;
}

#editBookmarkPanelTitle {
  font-size: 16px !important;
  margin: 0 4px !important;
}
#editBookmarkPanelDescription {
  margin: 1px 4px !important;
}

#editBookmarkPanelHeader > vbox > hbox {
  display: flex !important;
}

#editBookmarkPanelContent {
  margin: 0 !important;
}

#editBookmarkPanelContent .expander-down {
  width: 32px !important;
}

#editBookmarkPanelContent :-moz-any(menulist, button) {
  height: 24px !important;
}

#editBookmarkPanelContent button:not(#editBookmarkPanelRemoveButton) {
  opacity: .7 !important;
}

#editBookmarkPanelContent textbox {
  min-height: 24px !important;
}

#editBookmarkPanelContent .editBMPanel_rowLabel {
  text-align: right !important;
}

#editBookmarkPanelDoneButton {
  order: 2 !important;
  border-left: 1px solid rgba(24, 25, 26, 0.15) !important;
  margin: 6px 0 !important;
}

#editBookmarkPanelBottomButtons:hover #editBookmarkPanelDoneButton {
  margin: 0 !important;
}

/*
 * Powiadomienia synchronizacji
 */
.panel-promo-box {
  margin: 4px -4px -4px !important;
  padding: 8px 12px !important;
  background: rgba(200,200,200,.05) !important;
  box-shadow: none !important;
  border-top: 1px solid rgba(24, 25, 26, 0.1) !important;
}

/*
 * Menu aplikacji
 */
#appmenu-button {
  border: 0 !important;
  box-shadow: none !important;
  transition: all 250ms !important;
  margin-left: 1px !important;
  background: #E26515 !important;
}

#appmenu-button > .button-box > .button-menu-dropmarker {
  margin-top: 2px !important;
}

#appmenu-button[label="Aurora"] {
  background: #02519E !important;
}

#appmenu-button:hover {
  background-image: linear-gradient(rgba(255,255,255,.1),rgba(255,255,255,.1)) !important;
}

#appmenu-button[open],
#appmenu-button:hover:active {
  background-image: linear-gradient(rgba(0,0,0,.1),rgba(0,0,0,.1)) !important;
}

menupopup#appmenu-popup > .popup-internal-box {
  padding: 0 !important;
}

#appmenuSecondaryPane,
#appmenuPrimaryPane {
  padding: 4px 0 !important;
}

#appmenu-popup menuseparator {
  margin: 4px 0 !important;
}

#appmenuSecondaryPane {
  background: rgba(200,200,200,.1) !important;
  box-shadow: none !important;
  border-left: 1px solid rgba(24, 25, 26, 0.1) !important;
}

/*
 * Dopasowanie containera
 */
#customization-container {
  background-color: #FFF !important;
  background-clip: border-box !important;
  border: 1px solid rgba(10, 31, 51, 0.35) !important;
  border-top: 0 !important;
}

/*
 * Strona z dodatkami
 */
@-moz-document url-prefix("about:"), url("chrome://browser/content/places/places.xul") {
  menupopup {
    padding: 0 !important;
    margin: 0 !important;
  }
}

@-moz-document url("about:addons"), url("about:permissions"), url("about:preferences"), 
               url("chrome://browser/content/preferences/preferences.xul"), url("chrome://browser/content/pageinfo/pageInfo.xul") {
  page {
    background: #F7f7f7 !important;
    padding: 15px !important;
    color: #222426 !important;
  }
  
  .category, .main-content, button, #header-utils-btn, 
  #header-search, #detail-contributions, .alert, menulist {
    border-radius: 0 !important;
  }
  
  .main-content {
    background: rgba(255, 255, 255, 0.35) !important;
    border-color: rgba(24, 25, 26, 0.2) !important;
  }
  
  menulist:not([editable="true"]) > .menulist-dropmarker {
    -moz-margin-end: 0 !important;
  }
  
  menuitem {
    padding: 2px 5px !important;
    color: #222426 !important;
  }

  #header-utils-btn,
  menulist,
  button:not(.button-link) {
    -moz-appearance: none !important;
    background: rgba(24, 25, 26, 0.02) !important;
    border: 1px solid rgba(24, 25, 26, 0.12) !important;
    color: #222426 !important;
  }
  
  #header-utils-btn[disabled],
  menulist[disabled],
  button:not(.button-link)[disabled] {
    opacity: .6 !important;
  }
    
  #header-utils-btn:hover:not([disabled]),
  menulist:hover:not([disabled]),
  menuitem:hover:not([disabled]),
  menuitem[selected="true"],
  button:hover:not([disabled]):not(.button-link),
  button[open="true"]:not([disabled]):not(.button-link),
  button[checked=true]:not([disabled]) {
    background: rgba(24, 25, 26, 0.08) !important;
    box-shadow: none !important;
  }
  
  #header-utils-btn:-moz-any(:hover:active,[open]):not([disabled]),
  menulist:-moz-any(:hover:active,[open]):not([disabled]),
  button:hover:active:not([disabled]):not(.button-link) {
    background: rgba(24, 25, 26, 0.12) !important;
    box-shadow: 0 1px 0 0 rgba(24, 25, 26, 0.1) inset !important;
  }
  
  richlistitem:not([active="false"]) {
    color: #111 !important;
  }
  
  richlistitem[selected="true"] {
    background: rgba(24, 25, 26, 0.1) !important;
  }
  
  #categories {
    margin-left: 0 !important;
  }
  
  .category:not([disabled]) {
    height: auto !important;
  }
  
  .category {
    padding: 8px 5px !important;
    -moz-padding-start: 10px !important;
    border-width: 1px 0 1px !important;
  }
  
  .category[selected] {
    background: rgba(255, 255, 255, 0.35) !important;
    border-color: rgba(24, 25, 26, 0.2)  !important;
  }
}

/*
 * Fix na stronę z wtyczkami, dodatkami - about:Addons
 */
@-moz-document url("about:addons") {
  page {
    -moz-padding-start: 0 !important;
  }
  
  #header{
    margin-bottom: 15px !important;
  }
  
  #updates-container {
    margin-right: 10px !important;
  }
  
  #addons-page .global-info-container {
    background: #F5f5f5 !important;
  }
  
  .alert {
    background: rgba(24, 25, 26, 0.01) !important;
    border: 1px solid rgba(24, 25, 26, 0.15) !important;
    box-shadow: 1px 2px 4px rgba(24, 25, 26, 0.12) !important;
  }
  
  .addon[active="false"]:not([selected]) {
    background: rgba(24, 25, 26, 0.04) !important;
  }
  
  .addon[pending="disable"], .addon[pending="uninstall"] {
    background: url("chrome://mozapps/skin/extensions/stripes-info-negative.png") repeat-x rgba(24, 25, 26, 0.04) !important;
  }
  
  .addon[pending="disable"][selected], .addon[pending="uninstall"][selected] {
    background-color: rgba(24, 25, 26, 0.1) !important;
  }
  
  #header-search[focused] {
    border-color: #4595E5 !important;
  }
  
  .text-link, .discover-button, menulist,
  #header-utils-btn, button {
    color: #222426 !important;
  }
}

/*
 * In-content page - about:Preferences
 */
@-moz-document url("about:preferences") {
  page {
    -moz-padding-start: 0 !important;
  }
  
  caption {
    background: #FAFAFA !important;
  }
}

/*
 * Page info & preferences window
 */
@-moz-document url("chrome://browser/content/preferences/preferences.xul"), url("chrome://browser/content/pageinfo/pageInfo.xul") {
  prefwindow,
  window, 
  caption {
    background: #FAFAFA !important;
  }
  
  .paneSelector,
  #topBar {
    border-bottom: 1px solid rgba(24, 25, 26, 0.2) !important;
    padding: 0 5px !important;
  }
  
  :-moz-any(#viewGroup,.paneSelector) radio {
    border: 1px solid rgba(0,0,0,0) !important;
    border-width: 0 1px 0 !important;
    margin-bottom: -1px !important;
    min-width: 60px !important;
  }
  
  :-moz-any(#viewGroup,.paneSelector) radio:hover {
    background: rgba(24, 25, 26, 0.05) !important;
  }
  
  :-moz-any(#viewGroup,.paneSelector) radio[selected=true] {
    background: #FAFAFA !important;
    border-color: rgba(24, 25, 26, 0.2)  !important;
  }
  
  menulist,
  button {
    margin: 3px 4px !important;
    height: 23px !important;
  }
}
