css:
@-moz-document domain("mail.zoho.com") {
/*base font styles and color*/
body {
	font-family: 'Segoe UI', 'Segoe UI Web Regular', 'Segoe UI Symbol', 'Helvetica Neue', 'BBAlpha Sans', 'S60 Sans', Arial, sans-serif;	
	font-size: 14px !important;
	color: #000000;
	font-weight: normal;
}

.errBld, .bldtxt {
	font-weight: bold !important;
	font-size: 14px !important;
	font-family: 'Segoe UI Semibold', 'Segoe UI Web Semibold', 'Segoe UI Web Regular', 'Segoe UI', 'Segoe UI Symbol', Arial, sans-serif !important;
}

/*reply, forward icons in sidebar edit mode*/



.icFord {
	background-image: url(https://css.zohostatic.com/personal/docs_v293/css/lightgrey/images/index-img.gif);
	background-position: -105px -2312px;
	width: 16px;
	height: 15px;
}

.icRely{
	top: -1px !important;
	background-image: url(https://a.gfx.ms/h/c7.png);
	background-position: -134px -28px !important;
	width:10px !important;
	height:22px !important;
}

.icRell{
	background-image: url(https://css.zohostatic.com/personal/docs_v293/css/lightgrey/images/index-img.gif);
	background-position: -125px -2312px;
	width:13px !important;
}

.icEdew{
	background-image: url(https://css.zohostatic.com/personal/docs_v293/css/lightgrey/images/index-img.gif);
	background-position: -42px -2548px;
	width: 16px;
}

.icAdto{
	background-image: url(https://css.zohostatic.com/personal/docs_v293/css/lightgrey/images/index-img.gif);
	background-position: 0px -937px;
	width: 16px;
}

/*chat bar changes*/

#ptitle, .wms-ch-notify, .wms-ch{
	background-color:cornflowerblue !important;
}

.wms-ch-notify1{
	background-color: #A8DDB2 !important;
}

.wms-cn,.wms-cngptxt{
	color:#FFFFFF !important;
}

.wms-po {
	background-image: url(https://a.gfx.ms/h/c7.png) !important;
	background-position: -56px -31px !important;
	width:11px !important;
}

.wms-normalclose {
	background-image: url(https://a.gfx.ms/h/c7.png) !important;
	background-position: -209px -49px !important;
	width:13px !important;
}

.wms-ash{
	color:#E0E0E0 !important;
}

/* Footer Bar */

.wms-cd {
    background-color: #FFFFFF;
    background-image: none !important;
    border-top-color: #CCCCCC;
}

.wms-tab-notify {
    background-color: cornflowerblue;
    background-image: none;
    border-color: cornflowerblue;
}

/*default tab title, name and email id*/

#MAILTABI,.lftHead td{
	color:cornflowerblue !important;
}


/* backdrop */
#mask {
    transition: all 0.3s ease 0s;
    opacity: 0.3;
    background-color: #000000;
}

/*actions bar and compose button along with action bar of the message read tab*/
.zlLT{
	display:none !important;
}
/*adding the separater bar in dropdown menus*/
.zmbtn img.btAr1,.zmbtn img.btAr2,.btAr2 {
	top: -1px !important;
	position: static;
	background-image: url(https://a.gfx.ms/h/c7.png);
	background-position: -114px -33px !important;
	width: 8px !important;
	height: 8px !important;
}      

#mtView .sel table{
	color: cornflowerblue;
	border-top: 3px solid cornflowerblue !important;
	border-left: 1px solid cornflowerblue;
	text-shadow:none;
}

/*pagination buttons*/
.prevda,.prev{
	top: -1px !important;
	position: static;
	background-image: url(https://a.gfx.ms/h/c7.png);
	background-position: -135px -30px !important;
	width: 8px !important;
	height: 14px !important;
}

.next,.nextda{
	top: -1px !important;
	position: static;
	background-image: url(https://a.gfx.ms/h/c7.png);
	background-position: -174px -30px !important;
	width: 8px !important;
	height: 14px !important;
}

.rgtDiv span.sel {
	color: #FFFFFF;
}

.zmbtn li span{
    text-shadow:none;
    color:#ffffff;
}

#mHead,#TComps,#PreTR .btnBG,.mail .btnBG,.loSe{
    background-color: cornflowerblue;
}
/*compose dropdown*/
#ComOptMenu{
	width:149px !important;
	top:62px !important;
}
/*compose button*/
.lftCP{
	cellpadding:none !important;
}

/*reload button*/
.icFech{
	background: no-repeat url(http://ssl.gstatic.com/mail/sprites/general_black-16bf964ab5b51c4b7462e4429bfa7fe8.png) 0 -188px;
}
.btnBG{
    background-image:none;
}

.zmbtn td.zlMT, .zmbtn li {
    background-image: none;
}

.zbSep,.zlLT,.zlRT,#lftName,#MAILTABI{
    background-image:none !important;
}

.dsel td.a, .sel td.b, .dsel td.b, .sel td, .dsel td {
	background-image:none !important;
}

/*dropdown styles*/
.dropbox {
border: 1px solid cornflowerblue;
border-width: 1px 2px 2px 2px;
box-shadow: none;
}

.zmbtn li {
	position:inherit !important;
}

.topbox div.tpbxhr, .dropbox div.dpbxhr, .dropbox ul li.dpbxhr div, .gBg, .cpAtLi div div:hover, .zmlbl em:hover, .tpane .zmTcnt span, .zmload span {
	background-color: cornflowerblue !important;
	color: #ffffff !important;
}

/*forward button dropdown styles*/
#RepMenu{
	top:inherit;
}

/*header row*/
.headerwrapper{
	background-image:none;
	height:32px;
	border-bottom:1px;
	border-bottom-color:cornflowerblue;
}

/*full div of mail listing and read tab layout*/
.wrapper{
padding-top:4px;
}
/*mail listing mouse over color*/
.loYlw, .loRd, .loYlw .zmlbl, .loRd .zmlbl {
    background-color: #E7E7E7;
}

/*label icons in mail listing*/
.ZMSub .zmlbl{
	background:none;
}

/*mail create page - attach, google drive bar*/
.zmcpml .btnBG{
	background:none !important;
  	height:25px !important;
}

.cpBg {
	overflow: none;
}

.zmcpml .btnBG > div > ul > li > b > span{
	color:#000000 !important;
}

/*to, cc fields spacing*/
.zmABk{
	top:5px;
	left:1px;
}

.cpRT .txtfield {
	height: 20px !important;
}

.cpRow .cpRT{
	height:25px;
}

div.cpRow {
	height: 30px !important;
}

/*create page bg*/

.cpBg, .tabtd, .lftHead, .btnBG, .headerwrapper {
	background-image:none;
}

.ze_toolbarbg {
	background:none;
	border-bottom: 2px solid cornflowerblue;
	box-shadow: 1px 0px 2px gray;
}

/*preview close icon*/
#pV{
	padding-right: 3px;
}
.close{
	background-position: -120px -330px;
}


/*left area - folders, labels and views*/
#lb_Fol,#lb_Lbl,#lb_View{
	background-color:none;
	font-size:18px;
	font-weight:normal;
}

.ltLfHd{
	height:27px;
}

.clear,.ltLfHd,.zmlbl {
  background:#f3f3f3;
}
/*selected folder item style*/
.loSel, .loSel .zmlbl {
	background-color:#f3f3f3 !important;
	font-weight: bold !important;
	font-size: 15px !important;
	font-family: 'Segoe UI Semibold', 'Segoe UI Web Semibold', 'Segoe UI Web Regular', 'Segoe UI', 'Segoe UI Symbol', Arial, sans-serif !important;
	text-shadow:none;
	border-bottom:none !important;
}  
/*unselected folder style*/
.lTree td span{
	font-weight:normal;
}

/*folder name edit style*/
.lTree input, #addLabtr input {
	height: initial;
}

/*old unread mail count*/
.lTree em {
  background-color: #ffffff !important;
    color: #000000 !important;
 }
 /*new unread mail count*/
.lTree em.zmNew {
background-color: #ff5800 !important;
color: #ffffff !important;
}
  
/*reload button area*/
#lftFet{
	background-color:#f3f3f3;
}

/*force setting font style in most areas*/
.ZMLst, .tabtd td, .lft td, .lTree td, .ltLfHd, .btnBG, .logoBg, .prehead, .rgtbox, .dropbox, .cpBg, .zmpopup, .ZSWrap, .zmPoup, .ui-dialog, .zmload, .zmerror, .info, .zmalert, .ui-zalertbox, .ui-zsuccessbox, .autofill, .cpAtLi, .pvAtVi, .pvGybg, .zmqrep, #zmsdrag, #addToCrm, .headerwrapper td, .zmDiVi td, .zmloin, .Zmkbsc, #lftMore, #AccDrop, ul.ze_multitoolbar li a, select, .pvMore, .zmltsh td input, .mcont, .lTree input, #addLabtr input, .cpRT, .txtfield, .ui-widget, #welDiv, #printView, #addFil, #fTree, #filtDiv, #archComp, #popFrm, #popAEpop, .gadgethide, .gadgetshow, .Gtab, .gadgethide td, .gadgetshow td, .zm_hugAttmt td {
    font-family: 'Segoe UI', 'Segoe UI Web Regular', 'Segoe UI Symbol', 'Helvetica Neue', 'BBAlpha Sans', 'S60 Sans', Arial, sans-serif;
    font-size: 14px;
}


/*changes for decreasing logo size and the top header height*/
#img1{
height:15px !important;
}


.top {
    background-color: #FFFFFF;
    border: 1px solid #DDDDDD;
    height: 1px !important;
}

/*removing lines in between mails in listing*/
.ZMLst,.loSe {
	border-bottom:none !important;
}

/*pop up styles*/

.wcntrhd, .zmptop b {
    background-color: #F5F5F5 !important;
    margin: 0px !important;
}

.wcntrhd, .wcntrhd .close {
    height: 30px;
    line-height: 26px;
}

.wcntrhd span {
    color: #555555;
    padding: 0 10px;
    font-weight: 200;
    font-size: 16px;
}

.wcntrhd > .close {
    margin-top: 5px;
    height: 26px;
}

#altok,#delMok,#altconfm{
	background-color:cornflowerblue;
	background-image:none;
	color:#FFFFFF;
	border:none;
	height:30px;
	width:75px !important;
	font-size:14px;
}

.loBtn button{
	height:30px;
	width:75px !important;
	font-size:14px;
	background:none;
}

/* Delete folder pop */
.wcntrhd, .wcenter {
    background: #ffffff;
}

.zmpbottom b {
    border: none !important;
    background: none !important;
}

.ltgrey, .loBtn {
    background-color: #ffffff !important;
}

b.zmcbottom b, b.zmctop b {
    background-color: #ffffff;
}
/* vertical drag bar */


.WReze {
    background: #ffffff;
    border-left: 1px solid #C7C6C7;
    width:1px !important;  
/*    margin-left:2px; */
}

.mid{
    background-color:white;

}
.tpane .sep{
    background-color:white;
}


}
