css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
/*sets text color for ALL text*/
/*
* {
  color:rgba(255, 255, 255, 1.0) !important;
}
*/

/*BIG BACKGROUND begin - background for the entire screen*/
body {
  background: url(http://i145.photobucket.com/albums/r234/Hailey_Nicole_Gee/BCKGRND_zps5a7aa752.png) no-repeat fixed !important;
  background-color: rgba(44,71,98, 0.95) !important;
  background-position: 0% 0% !important;
  background-size: 100% 100%  !important;
}

#right_column {

background:none!important;

}

#left_column {

background:none!important;

}
/*BIG BACKGROUND end*/

/*AVATAR ARROW begin - arrow to the left of the icon bar*/
#new_post .arrow {
  opacity: 0.3 !important;
  margin-left: 0px !important; 
}
/*AVATAR ARROW end*/

/*CONTENT begin - background for posts, icon bar, sidebar*/
.l-content {
  background: rgba(34,31,78, 0.1) !important;
}
/*CONTENT*/ 

/*POST BACKGROUND begin*/
#posts LI[class*=" post"] {
  background: rgba(255, 255, 255, 0.3) !important;
}
/*POST BACKGROUND end*/

/*SELECTION ARROW begin - points to dashboard selection*/
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 11px rgba(255,255,255,0.3) !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
}

.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px rgba(44,71,98, 0.3)  !important;
}
/*SELECTION ARROW end*/

/*GREEN BUTTONS begin - blue/green buttons from sidebar to transparent*/
li.selected, li.recessed, a.active, div.post_filter_tabs a {
  background: none !important;
}
/*GREEN BUTTONS end*/

/*RADAR begin - removes radar blue frame - softens appearance*/
.post.photo_post .photo, .post.photo_post .video_thumbnail {
  -moz-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;
  border-radius: 12px !important;
}
.content.is_media, .mask.top, .mask.bottom {
  background: none !important;
}
/*RADAR end*/

/*NEW CONTENT begin - text post message body background*/
.mceContentBody {
  color: rgba(0, 0, 0, 0.3) !important;
  background: rgba(255,255,255, 0.3) !important;
}
/*NEW CONTENT end*/

#return_to_top .return_to_top_icon{
background: url('http://i145.photobucket.com/albums/r234/Hailey_Nicole_Gee/d6229773-0e64-4b61-aaab-52029b013e13_zps64d128c7.png') no-repeat transparent!important}


#popover_blogs .tab_notice:after {display:none}
DIV#xkit-control.xkit-help-forwarder > A,
.tab_bar .iconic A.tab_anchor {
background-image: url('http://i145.photobucket.com/albums/r234/Hailey_Nicole_Gee/transparenticons_zps84cf3612.png') !important;
background-size: 175px 36px;
}
/*DO NOT DELETE THIS BRACKET*/
}
