css:
@-moz-document url-prefix("https://www.pandora.com/"), url-prefix("http://www.pandora.com/") {
   #mainContainer, body { 
        background-color: #282828;
        background-image: url('https://31.media.tumblr.com/090eb2dad5dced7771278b1a70c62bf1/tumblr_inline_n4iii0VX851qmpver.png');
        background-repeat: repeat-x;
        background-attachment: fixed;
        background-position: 0 75px;
        font-family: Calibri, Arial, Sansserif;
        font-size: 1.2em;
    }
    body { background: #282828; }
    #brandingBar{ background: #101010; }
    #brandingBar .rightcolumn, #brandingBar .rightcolumn a, #brandingBar .middlecolumn {color: #ffffff;}
    #brandingBar .rightcolumn .user_activator:hover {
        background-color: #101010;
        background-image: -moz-linear-gradient(top, #282828 0%, #151515 100%);
        background-image: linear-gradient(to bottom, #282828 0%, #151515 100%);
    }
	#playerBar .middlecolumn .buttons { padding: 2px 0 10px 0; width: 242px; }
    #playerBar .progress .remainingTime, #playerBar .progress .elapsedTime { color: #e0e0e0; }
    #playerBar {
  		background-color: transparent;
  		background-image: url('https://31.media.tumblr.com/71bf62e663a0d151ee01d7373b65b450/tumblr_inline_n6eie5GfPN1qmpver.png');
  		height: 72px;
  		border-bottom: none;
	}
  #playerBar .thumbUpButton { margin: 4px 5px 0 0; }
  #playerBar .thumbDownButton { margin: 4px 5px 0 0; }
	#playerBar .playButton { margin: 0 3px 0 0; }
	#playerBar .skipButton { margin: 6px 5px 0 0; }
	#playerBar .volumePosition { padding-left: 10px; }
    #playerBar .searchBox { color: #101010;}
    #playerBar .volumeBackground, .welcome .outer_bg, .lightbox_bg.toastItem, .backstage .show_more, .backstage .stationButtons, .backstage .sample, .deletable, .comment_form, #currentSlideArrow, #playerBar .searchBox {
        filter: url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\'><filter id=\'grayscale\'><feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\'/></filter></svg>#grayscale"); 
        -webkit-filter: grayscale(100%);
    }
    #addArtistSeed.addButton, .slidesBackground.unselectable.clearfix img  {visibility: hidden; }
    .slides.unselectable {padding-top: 8px; padding-bottom: 6px; }
    .welcome .outer_bg { box-shadow: none;}
    .backstage {background: url('https://31.media.tumblr.com/c183e8b9fb0a8b3ed801870ef1deef57/tumblr_inline_n4j0o1Pz4y1qmpver.gif') repeat-x scroll 0 -690px #FFF}
    .backstage a, .backstage h2, .backstage h1, .backstage .show_more, .station .content .section .list .col1 h3 { color: #101010;}
    
    .slide .thumbContainer .thumbUp.indicator, .slide .thumbContainer .thumbDown.indicator { background: none; }
    .slide .thumbContainer .thumbUp a:link, .slide .thumbContainer .thumbUp a:visited { background: url("http://www.pandora.com/img/player-controls/btn_up.png") no-repeat scroll center transparent; background-size: 80%; }
    .slide .thumbContainer .thumbUp.indicator a:link, .slide .thumbContainer .thumbUp.indicator a:visited { background: url("http://www.pandora.com/img/player-controls/btn_up_indicator.png") no-repeat scroll center transparent; background-size: 80%;  }
    
    .slide .thumbContainer .thumbDown a:link, .slide .thumbContainer .thumbDown a:visited, .slide .thumbContainer .thumbDown {background: url("http://www.pandora.com/img/player-controls/btn_down.png") no-repeat scroll 50% 60% transparent; background-size: 80%; }
    .slide .thumbContainer .thumbDown.indicator a:link, .slide .thumbContainer .thumbDown.indicator a:visited {background: url("http://www.pandora.com/img/player-controls/btn_down_indicator.png")no-repeat scroll center transparent; background-size: 80%;}
    
    .slide .thumbContainer .menuArrow { background-image: url(http://www.pandora.com/img/content-area/smallthumbs/menu_arrow_hover.png); }
    
    #ad_container.masthead_ad{
        visibility: hidden;
        height: 0;
        width: 0;
    }
    #adLayout{
        margin: 0 auto 0 auto;
        position: static;
        max-width: 720px;
        padding: 0;
    }
    #mainContentContainer.contentContainer {
        width: 100%;
        margin: 0 auto 0 auto;
    }
    
    #splash {
        background-color: #282828;
        background-image: url('https://31.media.tumblr.com/090eb2dad5dced7771278b1a70c62bf1/tumblr_inline_n4iii0VX851qmpver.png');
        background-repeat: repeat-x;
        background-attachment: fixed;
        background-position: top center;
    }
    
    .slidesForeground, .slidesBackground{
        background-color: #101010;
        background-image: -moz-linear-gradient(top, #101010 0%, #282828 100%);
        background-image: linear-gradient(to bottom, #101010 0%, #282828 100%);
    }
    img.bottom{visibility: hidden;}
    .slide{ background: #282828; box-shadow: 2px 2px 3px rgba(0,0,0,0.5);}
    .slidesForeground .slides_left_scroll, .slidesForeground .slides_right_scroll, #user_menu_dd,  .searchPopupContainer .content .footer {
        filter: url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\'><filter id=\'grayscale\'><feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\'/></filter></svg>#grayscale"); 
        -webkit-filter: grayscale(100%);
    }
    
    .contentnav { background: #151515; }
    .contentnav .home_icon, .contentnav .friends_icon, .contentnav .myprofile_icon { 
        text-transform: uppercase; 
        width: 100px; 
        font-size: 0.9em;
        line-height: .7em;
        filter: url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\'><filter id=\'grayscale\'><feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\'/></filter></svg>#grayscale"); 
        -webkit-filter: grayscale(100%);
    }
    .contentContainer .top {background: #151515; border-top: 1px solid #505050;}
    .tip_box{border-radius: 6px 6px 0 0;}
    
    .stationListHolder { background: #d0d0d0; }
    .stationNameText { color: #101010;  text-shadow: 1px 1px 0 #ffffff; font-size: 1.1em;}
    .stationName li:hover  {background: #cccccc; }
    .stationNameText.shared {background: url("https://31.media.tumblr.com/2367f9cb10cdc368ebc5830dac7f8dd7/tumblr_inline_n4hymvC8Js1qmpver.png") no-repeat scroll 0px 2px transparent;}
    #shuffleIcon { color: #101010;  background: url("https://31.media.tumblr.com/97f81b7f0a400f42e21cbd07ba428931/tumblr_inline_n4hyuzOrKt1qmpver.png") no-repeat scroll left center transparent;  }
    .stationListItem.selected{
        background-color: #c0c0c0;
        background-image: -moz-linear-gradient(top, #F0F0F0 0%, #A0A0A0 100%);
        background-image: linear-gradient(to bottom, #F0F0F0 0%, #A0A0A0 100%);
        border-top: 1px solid #ffffff;
        border-bottom: 1px solid #b0b0b0;
    }
    #detailContainer { text-align: justify;}
    .stationListItem .options {color: #505050; border: none; background: #ffffff;}
    .stationListItem .option.addVariety {border-right: 1px solid #d0d0d0;}
    
    #trackInfo {background: #f0f0f0; border-bottom: 1px solid #d0d0d0; }
    .trackData, #trackInfo .info a.songTitle, #trackInfo .info a.artistSummary, #trackInfo .info a.albumTitle {color: #101010;}
    #thumbup.thumb, #thumbdown.thumb, .slide  img.noart, #playerBar .progressBar, #trackInfoButtons .buttons, #stationSortButtons.clearfix, .stationContent #promobox .dots, .jspContainer, .platformPromo.view-3  { 
        filter: url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\'><filter id=\'grayscale\'><feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\'/></filter></svg>#grayscale"); 
        -webkit-filter: grayscale(100%);
    }
    #trackInfoButtons, .view-3 {background: #e0e0e0; border-bottom: 1px solid #d0d0d0;}
    .stationContent #promobox {border-bottom: 1px solid #d0d0d0; border-top: 1px solid #d0d0d0;}
    .stationContent {border-right: 1px solid #d0d0d0;}
    #trackInfoButtons .buttons .button {
        border: 1px solid #d0d0d0;
        background-color: #e0e0e0;
        background-image: linear-gradient(to bottom, #ffffff 0%, #e0e0e0 100%);
        background-image: -moz-linear-gradient(top, #ffffff 0%, #e0e0e0 100%);
    }
    .home .homeRightSide, .stationContent #promobox .dots {  background: #f0f0f0; }
    
    #footer .legal, #footer a{color: #b0b0b0;}
    .detail .heading, .detail a { color: #101010; }
    .detail .itemContent .showMoreLyrics, .detail .itemContent .showMoreAbout {background-image: url("https://31.media.tumblr.com/cadb76a39a5234ebcac8769b1b073b99/tumblr_inline_n4hzzc5liy1qmpver.png"); }
    
    .lightbox_bg.stationSelectorPopup, .lightbox_bg.addVarietyPopup {
        filter: url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\'><filter id=\'grayscale\'><feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\'/></filter></svg>#grayscale"); 
        -webkit-filter: grayscale(100%);
    }
}
