css:
@-moz-document domain(weather.com) {
  body {
   background-image:none;
   /*[[background]]*/
  }
  #section1, #section2, #section3, .wx-promo, #section6, #section4, #section14, #section7, #section13, #section12, #section11, #section5, .wx-partner-spotlight, .wx-mod1, .wx-free-title, #wx-rightnowsecond-container, .twc-breaking-now-social, .twc-breaking-social-container, .wx-promo-links, .wx-misc-link, .trc_related_container, .wx-combinedPromo, .wx-map, .wx-tempgraph, .wx-plan-hour, .wx-trending-stories {
    display:none !important;
  }
  .wx-dl {
    margin:0 !important;
  }
  #feedbackify {
    display:/*[[feedback]]*/ !important;
  }
  #wx-breaking-now {
    display:none !important;
  }
  #wx-wrapper {
    background:none !important;
    border:0 !important;
    padding-top:15px !important;
  }
  ul.twc-breaking-now-list li h3 a {
    text-overflow:initial !important;
    overflow:visible !important;
  }
  .wx-today, .wx-hourly, .wx-12hour, .wx-24hour, .wx-desktop-branding a img, wx-elevated-collection a img {
    background-color:#eee !important;
    border-radius:15px !important;
  }
  .wx-24hour .wx-daypart {
    padding-left:10px !important;
  }
  .wx-more {
    padding-right:10px !important;
  }
  .wx-title-container, .wx-hourly h2, .wx-timestamp, .wx-title, .wx-24hour h2, .wx-links {
    padding:10px !important;
  }
  .wx-data-part img, .wx-weather-icon {
    background-color:#fff !important;
    border-radius:15px !important;
    margin-top:10px !important;
    margin-bottom:10px !important;
  }
  .wx-daypart {
    border:0 !important;
  }
  .wx-social-share-ls-wrapper, .wx-social-share-story-wrapper {
    display:/*[[social]]*/ !important;
  }
}
