/*
 * Nifty at night
 *
 * Author      : Skip R
 * Contact info: @beagleperth (twitter)
 * Updated     : April 22, 2014
 * Description : Night time themed colors (=black background) which are easier on the eyes.
 *         
 * Changes:
 * 2014-04-24 1.0 Initial release
 * 2015-04-08 1.1 Update comments
 * 2017-04-08 1.2 add support for mobile pages
 * 2017-12-28 1.3 and font familly to pre
 * 2017-12-28 1.4 any unformatted text increased 50%
 *
 */
 
/* How to customize:                        */
/* "#name" are id=name clauses on markup    */
/* tag.name are class=name clauses          */
/* tag means that markup everywhere on page */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.nifty.org") {
body {
    background:black !important;
    color:yellow !important;
    }

.ftr,
pre {
     font-size: 175%;
     font-family:Arial,Helvetica,sans-serif; 
}

    
    
a:link {color:green !important;} 
a:visited {color:yellow !important;}
}

@-moz-document domain("m.nifty.org") {

div#content div#story div,
.title-1,
.title,
h1,h2,
body {
    background:black !important;
    color:white !important;
    }

.ftr {
     font-size: 175%;
}     
     
/* for pages with no markup */
pre {
	 font-size: 175%;
     font-family:Arial,Helvetica,sans-serif; 

}

a:link {color:orange !important;} 
a:visited {color:green !important;}

#main { 
      background-color: black !important; 
      color: white !important;
}    

}
