css:
@-moz-document url-prefix("http://www.fixie-king.dk/forum"), url-prefix("http://fixie-king.dk/forum") {
/* Elements */
body {
    background:#fff;
    font-size:13px;
    max-width:1200px;
    margin: 0 auto;
}
table {border:0px;}

/* Links */
a:hover {text-decoration:none;}

/* IDs */
#wrapcentre {margin:10px 25px;}
#menubar {height:30px;margin-top:15px;}
#pageheader {margin-top:5px;}
#pageheader h2 {font-size:20px;}
#pageheader h2 a:link, #pageheader h2 a:visited {color:#333;} 
#pageheader h2 a:hover {text-decoration:none;}


/* Red header links */
#menubar .genmed:nth-child(1) a:link, #menubar .genmed:nth-child(1) a:visited {
color: #fff;
background-color: #d9534f;
padding: 6px;
border:1px solid #d43f3a;
border-radius: 3px;
text-decoration:none;
margin-right:-9px;
}
#menubar .genmed:nth-child(1) a:hover {background-color: #d2322d;}

/* White header links */
#menubar .genmed:nth-child(2) a:link, #menubar .genmed:nth-child(2) a:visited {
color: #333;
background-color: #fff;
padding: 6px;
border:1px solid #ccc;
border-radius: 3px;
text-decoration:none;
margin-right:-9px;
}
#menubar .genmed:nth-child(2) a:hover {background-color: #ebebeb;}

/* Blue header links */
p.searchbar span:nth-child(2) a {
color: #fff;
background-color: #428bca;
padding: 6px;
border:1px solid #357ebd;
border-radius: 3px;
text-decoration:none;
font-size:13px;
margin-right:-5px;}
p.searchbar span:nth-child(2) a:hover {background-color:#3276b1;}

/* Orange links */
td.cat td.nav a,
td.cat tr.nav td a,
td.cat a.nav {
color: #fff;
background-color: #f0ad4e;
padding: 6px;
border:1px solid #eea236;
border-radius: 3px;
text-decoration:none;
font-size:13px;}
td.cat a.nav:hover {background-color:#ed9c28;}

/* Mark as read pos */
_td.cat a.nav {
position: absolute;
left:386px;
top:48px;}

/* Hide */
#logodesc, 
#datebar, 
#wrapfooter,
p.searchbar span:nth-child(1),
table.legend,
p.datetime,
p.moderators,
#menubar img,
td.profile table,
td.profile strong
{display:none; visibility:hidden;}


/* Classed elements */
p.searchbar { padding:0;height:15px;}
p.searchbar span:nth-child(2) {float:left !important;color:#fff;}
p.forumdesc, p.topicdetails, td.nav, td.gensmall {color:#666;}
p.breadcrumbs {padding:3px 5px; font-size:1.3em;}
b.postauthor {font-size:12px;}
.cat, .catdiv {background-color:#fff;background:#fff;padding-top:5px;}
span.postdetails {width:180px;display:block;color:#999;}
b.gensmall {font-size:13px}
td div.postbody:nth-child(2) {color:#999;font-size:11px;}

/* Tables */
table {background-color:#fff;font-size:11px;}
table.tablebg {background-color:#fff;margin:0;}
table tbody tr th {background-color:#fff;color:#000;font-weight:bold;background-image:none;}
table.tablebg tr:nth-child(even) td.row1 { background:#FFF; }
tr.row1, td.row1 {background:#f9f9f9;}
tr.row2, td.row2 {background:#f9f9f9;}
td.row3, div.quotetitle {background:#f1f1f1;}
td.genmed {font-size:1.3em}
td.cat td.nav {height:40px}
td.gensmall div b {display:none}
}
