css:
@-moz-document domain(vk.com) {

/*avatars round corners*/
.ava img{
	border-radius: 500px !important;
	border: solid 1px #CCC !important;
	box-shadow: 0 0 3px #CCC;
}
.im_photo_holder img,
img.im_user_holder,
img.im_log_author_chat_thumb,
.post_image img,
.page_list_module .thumb img,
.published_by_photo img,
.reply_image img,
#pad_audio_friends_list img,
.dialogs_photo>a>img,
#friends_list .friends_photo_img
{
	border-radius: 50% !important;
}
   
  
/* player controls */
.audio .play_new {
	cursor: pointer;
	width: 16px;
	height: 16px;
	background: url(http://vk.com/images/playpause.gif) no-repeat 0px 0px;
}

.audio_friend_photo {
	border-radius: 50% ! important;
}

.play_new {
	border-radius: 50% !important;
}
.video_module .video div.page_video_play_icon, .page_post_video_play_inline {
    position: absolute;
    z-index: 10;
    background: url(http://vk.com/images/inline_video_play.png) no-repeat;
    margin-left: -23px;
    margin-top: -23px;
    width: 46px;
    height: 46px;
    top: 50%;
    left: 50%;
  	border-radius: 50% !important;
}
.video_module .video div {
    position: absolute;
    bottom: 10px;
    font-size: 10px;
    z-index: 10;
    width: 171px;
	margin-top: 105px;
}
.page_video_play_icon {
	border-radius: 50% !important;
}

}
