css:
@-moz-document domain(programata.tv) {

body{
  zoom:1.3;
}
#footer1, .newscontent, .newscat, #Pager, #top, #banner1, div[style="clear: both; background: #fff; height: 5px;"] {
  display:none !important;
  position: absolute;
  top:-5000px;
  left:-5000px;
  visibility:hidden;
}
#programBorder, #program_custom, .mCustomScrollbar _mCS_1, .types-programs {
  height:1500px !important;
}
#btns {
  margin-top:25px;
}
#content {
  border-top:8px solid #fff;
}
a[onclick="programsLeft(); return false;"] {
  position:fixed;
  top:50%;
  left:10px;
  zoom:4;
  display:block;
	width: 0; 
	height: 0; 
	border-top: 10px solid transparent;
	border-bottom: 10px solid transparent; 
	border-right:10px solid white; 
  opacity:0.1;
  transition: opacity 0.3s;
}
a[onclick="programsRight(); return false;"] {
  position:fixed;
  top:50%;
  right:10px;
  zoom:4;
  display:block;
	width: 0; 
	height: 0; 
	border-top: 10px solid transparent;
	border-bottom: 10px solid transparent; 
	border-left:10px solid white;
  opacity:0.1;
  transition: opacity 0.3s;
}
a[onclick="programsLeft(); return false;"]:hover, a[onclick="programsRight(); return false;"]:hover {
  opacity:1;
  transition: opacity 0.3s;
}
a[onclick="programsLeft(); return false;"] img, a[onclick="programsRight(); return false;"] img {
  display:none;
}
#loginForm, #content, #content_wrap {
  background:none !important;
  border:0 !important;
}
#loginForm {
  opacity:0.3;
  transition: opacity 0.3s;
}
#loginForm:hover {
  opacity:0.6;
  transition: opacity 0.3s;
}
.weekInactive {
    background-image:none !important;
    background-color:#aaa !important;
    font-weight:bold;
    margin-bottom:0;
    border-bottom:1px solid #fff;
  }
#weekStart {
    color:#aaa !important;
    border:0 !important;
  }
.weekActive {
      background:#fff !important;
      border-bottom:1px solid #fff !important;
    }
#programFoot {
  background:#3E4047;
}
#btns {
  padding:-20px 0 0 7px !important;
}
}
