css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.sjgames.com") {
   /* vBulletin 3 CSS For Style 'Default Style' (styleid: 1) */
   body
   {
   	background-color: #181200 !important;
   	color: #000000 !important;
   	font: 12pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
   	margin: 5px 10px 10px 10px !important;
   	padding: 0px !important;
   }
   a:link
   {
   	color: #1E90FF !important;
   }
   a:visited
   {
   	color: #330066 !important;
   }
   a:hover, a:active
   {
   	color: #000066 !important;
   }
   .page
   {
   	background: #87CEFA !important; 
   	color: #000000 !important;
   }
   td, th, p, li
   {
   	font: 12pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
   }
   .tborder
   {
   	background-color: #181200 !important;
   	color: #000000 !important;
   	border: 1px solid #181200 !important;
   }
   .tcat
   {
   	background-color: #4682B4 !important;
   	color: #FFFFFF !important;
   	font: bold 11pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
   }
   .tcat a:link
   {
   	color: #ffffff !important;
   	text-decoration: none !important;
   }
   .tcat a:visited
   {
   	color: #ffffff !important;
   	text-decoration: none !important;
   }
   .tcat a:hover, .tcat a:active
   {
   	color: #FFFF66 !important;
   	text-decoration: underline !important;
   }
   .thead
   {
   	background-color: #4682B4 !important;
   	color: #FFFFFF !important;
   	font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
   }
   .thead a:link
   {
   	color: #FFFFFF !important;
   }
   .thead a:visited
   {
   	color: #FFFFFF !important;
   }
   .thead a:hover, .thead a:active
   {
   	color: #FFFF00 !important;
   }
   .tfoot
   {
   	background-color: #4682B4 !important;
   	color: #E0E0F6 !important;
   }
   .tfoot a:link
   {
   	color: #E0E0F6 !important;
   }
   .tfoot a:visited
   {
   	color: #E0E0F6 !important;
   }
   .tfoot a:hover, .tfoot a:active
   {
   	color: #FFFF66 !important;
   }
   .alt1, .alt1Active
   {
   	background-color: #CCE6FA !important;
   	color: #000000 !important;
   }
   .alt2, .alt2Active
   {
   	background-color: #EEF6FF !important;
   	color: #000000 !important;
   }
   .alt3
   {
   	background-color: #D5D8E5 !important;
   	color: #000000 !important;
   }
   .wysiwyg
   {
   	background-color: #F5F5FF !important;
   	color: #000000 !important;
   	font: 12pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
   }
   textarea, .bginput
   {
   	font: 12pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
   }
   .button
   {
   	font: 10px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
   }
   select
   {
   	font: 10px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
   }
   option, optgroup
   {
   	font-size: 10px !important;
   	font-family: verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
   }
   .smallfont
   {
   	font: 12px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
   }
   .time
   {
   	color: #181200 !important;
   }
   .navbar
   {
   	font: 10px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
   }
   .highlight
   {
   	color: #181200 !important;
   	font-weight: bold !important;
   }
   .fjsel
   {
   	background-color: #4682B4 !important;
   	color: #E0E0F6 !important;
   }
   .fjdpth0
   {
   	background-color: #F7F7F7 !important;
   	color: #181200 !important;
   }
   .panel
   {
   	background-color: #CCddFF !important;
   	color: #181200 !important;
   	padding: 10px !important;
   	border: 2px outset !important;
   }
   .panelsurround
   {
   	background-color: #4AC5E8 !important;
   	color: #181200 !important;
   }
   legend
   {
   	color: #4682B4 !important;
   	font: 10px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
   }
#vBulletin_editor {
	background: #e1ebff !important;
	padding: 6px;
}
.imagebutton {
	background: #e1ebff !important;
        color: #000000;
        padding: 1px;
        border: none;
}
.alt_pickbutton {
        border-left: 1px solid #e1ebff !important;
}
   .vbmenu_control
   {
   	background-color: #4682B4 !important;
   	color: #FFFFFF !important;
   	font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
   	padding: 3px 6px 3px 6px !important;
   	white-space: nowrap !important;
   }
   .vbmenu_control a:link
   {
   	color: #FFFFFF !important;
   	text-decoration: none !important;
   }
   .vbmenu_control a:visited
   {
   	color: #FFFFFF !important;
   	text-decoration: none !important;
   }
   .vbmenu_control a:hover, .vbmenu_control a:active
   {
   	color: #FFFFFF !important;
   	text-decoration: underline !important;
   }
   .vbmenu_popup
   {
   	background-color: #FFFFFF !important;
   	color: #000000 !important;
   	border: 1px solid #0B198C !important;
   }
   .vbmenu_option
   {
   	background-color: #CCDDFF !important;
   	color: #000000 !important;
   	font: 9px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
   	white-space: nowrap !important;
   	cursor: pointer !important;
   }
   .vbmenu_option a:link
   {
   	color: #1E90FF !important;
   	text-decoration: none !important;
   }
   .vbmenu_option a:visited
   {
   	color: #000099 !important;
   	text-decoration: none !important;
   }
   .vbmenu_option a:hover, .vbmenu_option a:active
   {
   	color: #FFFFFF !important;
   	text-decoration: none !important;
   }
   .vbmenu_hilite
   {
   	background-color: #4682B4 !important;
   	color: #FFFFFF !important;
   	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
   	white-space: nowrap !important;
   	cursor: pointer !important;
   }
   .vbmenu_hilite a:link
   {
   	color: #FFFFFF !important;
   	text-decoration: none !important;
   }
   .vbmenu_hilite a:visited
   {
   	color: #FFFFFF !important;
   	text-decoration: none !important;
   }
   .vbmenu_hilite a:hover, .vbmenu_hilite a:active
   {
   	color: #FFFFFF !important;
   	text-decoration: none !important;
   }
   /* ***** styling for 'big' usernames on postbit etc. ***** */
   .bigusername { font-size: 14pt !important; }
   
   /* ***** small padding on 'thead' elements ***** */
   td.thead, div.thead { padding: 4px !important; }
   
   /* ***** basic styles for multi-page nav elements */
   .pagenav a { text-decoration: none !important; }
   .pagenav td { padding: 2px 4px 2px 4px !important; }
   
   /* ***** define margin and font-size for elements inside panels ***** */
   .fieldset { margin-bottom: 6px !important; }
   .fieldset, .fieldset td, .fieldset p, .fieldset li { font-size: 11px !important; }
   
   /* ***** don't change the following ***** */
   form { display: inline !important; }
   label { cursor: default !important; }
   .normal { font-weight: normal !important; }
   .inlineimg { vertical-align: middle !important; }
   
   /* Added to handle [fnord] tags - NPE, 10/27/2005 */
   .alt1 .fnord { color: #CCe6fa !important; }
   .alt1 * .fnord { color: #CCe6fa !important; }
   .alt1Active .fnord { color: #CCe6fa !important; }
   .alt1Active * .fnord { color: #CCe6fa !important; }
   
   .alt2 .fnord { color: #EEF6FF !important; }
   .alt2 * .fnord { color: #EEF6FF !important; }
   .alt2Active .fnord { color: #EEF6FF !important; }
   .alt2Active * .fnord { color: #EEF6FF !important; }
   
   .alt3 .fnord { color: #D5D8E5 !important; }
   .alt3 * .fnord { color: #D5D8E5 !important; }
   .alt3Active .fnord { color: #D5D8E5 !important; }
   .alt3Active * .fnord { color: #D5D8E5 !important; }

   #cyb_flashimagebanners { display: none; }
}
