css:
@-moz-document domain("www.pandora.com") {
html {
overflow-y: scroll !important;
}

body {
background-color: #000 !important;
}

#brandingBar {
background: #630063; /* Old browsers */
background: -moz-linear-gradient(bottom,  #630063 65%, #990099 100%) !important; /* FF3.6+ */
background: -webkit-linear-gradient(bottom,  #630063 65%,#990099 100%) !important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(bottom,  #630063 65%,#990099 100%) !important; /* Opera 11.10+ */
background: -ms-linear-gradient(bottom,  #630063 65%,#990099 100%) !important; /* IE10+ */
background: linear-gradient(to top,  #630063 65%,#990099 100%) !important; /* W3C */
border-bottom: 1px solid #aaa !important;
}

#brandingBar .middlecolumn {
color: #c474c4 !important;
}

#brandingBar .rightcolumn {
color: #c474c4 !important;
}

#brandingBar .rightcolumn a {
color: #c474c4 !important;
}

#brandingBar .rightcolumn .user_activator:hover {
background: rgba(255,255,255,0.1) !important;
}

.stationChangeSelectorNoMenu p {
width: auto !important;
max-width: none !important;
}

#playerBar {
background: #990099; /* Old browsers */
background: -moz-linear-gradient(top,  #990099 55%, #630063 100%) !important; /* FF3.6+ */
background: -webkit-linear-gradient(top,  #990099 55%,#630063 100%) !important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top,  #990099 55%,#630063 100%) !important; /* Opera 11.10+ */
background: -ms-linear-gradient(top,  #990099 55%,#630063 100%) !important; /* IE10+ */
background: linear-gradient(to bottom,  #990099 55%,#630063 100%) !important; /* W3C */
}

#playerBar .searchBox {
margin-left: 18px !important;
background-image: none !important;
padding: 0 !important;
}

#playerBar .searchBox:before {
content: "Something Different?" !important;
color: #c671c6 !important;
font-weight: bold !important;
position: relative !important;
top: 6px !important;
left: 5px !important;
padding: 2px 5px 3px 6px !important;
border-top-left-radius: 3px !important;
border-bottom-left-radius: 3px !important;


background: #630063; /* Old browsers */
background: -moz-linear-gradient(top,  #630063, #731D73) !important; /* FF3.6+ */
background: -webkit-linear-gradient(top,  #630063,#731D73) !important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top,  #630063, #731D73) !important; /* Opera 11.10+ */
background: -ms-linear-gradient(top,  #630063 , #731D73) !important; /* IE10+ */
background: linear-gradient(to bottom,  #630063 , #731D73) !important; /* W3C */
}

#playerBar .searchInput {
width: 100% !important;
background-color: #FFF !important;
margin-left: 2px !important;
padding: 3px 5px 3px 5px !important;
border-bottom-right-radius: 2px !important;
border-top-right-radius: 2px !important;
}

#searchPopupNavPosition {
left: 80px !important;
}

.searchPopupContainer div { background-color: #e6bfe6 !important; }
.searchPopupContainer div.innerheading { border-color: #630063 !important; }
.searchPopupContainer div.innerheading .label { color: #630063 !important; }
.searchPopupContainer .content .item { color: #990099 !important; }
span.search_for { color: white !important; }

#mainContainer {
height: 950px !important;
background-image: url(http://i57.tinypic.com/24vqq6w.jpg);
}

.adSupported-layout #adLayout {
min-width: 666px !important;
width: 70% !important;
}

#mainContentContainer {
width: 100% !important;
background: #630063 !important;
}

.contentContainer .top {
background-color: #630063 !important;
border-top: 1px solid #994499 !important;
}

.contentnav {
background-color: #630063 !important;
border: 0 !important;
}

.stationContent {
background-color: #994499 !important;
border-right: 1px solid #630063 !important;
}

.stationListItem .stationName {
color: #ffffff !important;
text-shadow: 1px 1px 0 #630063 !important;
}

.stationListItem .stationName_hover {
background-color: #990099 !important;
}

.jspDrag {
    background-color: #360036 !important;
}

.jspTrack {
    background-color: transparent !important;
}

.stationListItem.selected {
border-top: 1px solid transparent !important;
border-bottom: 1px solid transparent !important;

background: #990099; /* Old browsers */
background: -moz-radial-gradient(center, ellipse cover,  #990099 0%, #630063 100%) !important; /* FF3.6+ */
background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%,#990099), color-stop(100%,#630063)) !important; /* Chrome,Safari4+ */
background: -webkit-radial-gradient(center, ellipse cover,  #990099 0%,#630063 100%)  !important; /* Chrome10+,Safari5.1+ */
background: -o-radial-gradient(center, ellipse cover,  #990099 0%,#630063 100%) !important; /* Opera 12+ */
background: -ms-radial-gradient(center, ellipse cover,  #990099 0%,#630063 100%) !important; /* IE10+ */
background: radial-gradient(ellipse at center,  #990099 0%,#630063 100%) !important; /* W3C */
}

.stationListItem.selected .options {
background: #c62dc6 !important;
border-top: 1px solid #630063 !important;
border-left: 1px solid #630063 !important;
}

.btn_bg_sort {
color: #ffffff !important;
text-shadow: 1px 1px 0 #000000 !important;

background: #990099; /* Old browsers */
background: -moz-radial-gradient(center, ellipse cover,  #990099 0%, #630063 100%) !important; /* FF3.6+ */
background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%,#990099), color-stop(100%,#630063)) !important; /* Chrome,Safari4+ */
background: -webkit-radial-gradient(center, ellipse cover,  #990099 0%,#630063 100%) !important; /* Chrome10+,Safari5.1+ */
background: -o-radial-gradient(center, ellipse cover,  #990099 0%,#630063 100%) !important; /* Opera 12+ */
background: -ms-radial-gradient(center, ellipse cover,  #990099 0%,#630063 100%) !important; /* IE10+ */
background: radial-gradient(ellipse at center,  #990099 0%,#630063 100%) !important; /* W3C */
}

.btn_bg_sort:hover {
background: #c62dc6 !important; /* Old browsers */
background: -moz-radial-gradient(center, ellipse cover,  #c62dc6 0%, #630063 100%) !important; /* FF3.6+ */
background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%,#c62dc6), color-stop(100%,#630063)) !important; /* Chrome,Safari4+ */
background: -webkit-radial-gradient(center, ellipse cover,  #c62dc6 0%,#630063 100%) !important; /* Chrome10+,Safari5.1+ */
background: -o-radial-gradient(center, ellipse cover,  #c62dc6 0%,#630063 100%) !important; /* Opera 12+ */
background: -ms-radial-gradient(center, ellipse cover,  #c62dc6 0%,#630063 100%) !important; /* IE10+ */
background: radial-gradient(ellipse at center,  #c62dc6 0%,#630063 100%) !important; /* W3C */
}

.btn_bg_sort.selected {
background: #630063 !important; /* Old browsers */
background: -moz-radial-gradient(center, ellipse cover,  #630063 0%, #990099 100%) !important; /* FF3.6+ */
background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%,#630063), color-stop(100%,#990099)) !important; /* Chrome,Safari4+ */
background: -webkit-radial-gradient(center, ellipse cover,  #630063 0%,#990099 100%) !important; /* Chrome10+,Safari5.1+ */
background: -o-radial-gradient(center, ellipse cover,  #630063 0%,#990099 100%) !important; /* Opera 12+ */
background: -ms-radial-gradient(center, ellipse cover,  #630063 0%,#990099 100%) !important; /* IE10+ */
background: radial-gradient(ellipse at center,  #630063 0%,#990099 100%) !important; /* W3C */
}

#stationSortDate {
border-top: 1px solid #630063 !important;
}

#stationSortAlpha {
border-top: 1px solid #630063 !important;
border-left: 1px solid #630063 !important;
}

.slidesForeground .slides {
width: 200% !important;
left: -100% !important;
padding: 0 !important;
}

.slidesForeground .slides .stationSlides {
left: 220px !important;
width: 100% !important;
}

.slidesBackground {
width: 100% !important;
background: #994499 !important;
}

.slidesBackground img {
opacity: 0 !important;
}

.slidesBackground div img {
opacity: 0 !important;
}

#currentSlideArrow {
opacity: 0 !important;
width: 0 !important;
height: 0 !important;
}

#trackInfo {
background: #630063 !important;
border: 0 !important;
margin-top: -4px !important;
}

#trackInfo .info {
color: #d1d1d1 !important;
text-shadow: 1px 1px 0 #000000 !important;
}

#trackInfo .info a {
color: #ffffff !important;
}

.fxCol-cont-5 {
background: none !important;
}

.fxCol-cont-1 {
background: none !important;
}

#trackInfoButtons {
  background-color: #360036 !important;
}

#trackInfoButtons .buttons .button {
  border: 0 !important;
  border-radius: 0 !important;
  background-color: #630063 !important;
  background-image: none !important;
  color: #ffffff !important;
}

#trackInfoButtons .buttons .button:hover {
  background-color: #994499 !important;
}

.btn_bg {
  text-shadow: 1px 1px #000000 !important;
}

.dropdownmenu ul {
  border-radius: 0 !important;
  background: #630063 !important;
}

.dropdownmenu ul li:hover a {
  background: #994499 !important;
}
  
.dropdownmenu ul li a {
  color: #000000 !important;
}

#trackDetail a {
color: #a80fa8 !important;
}

.detail .heading {
color: #c62dc6 !important;
text-shadow: 1px 1px 0 #000000 !important;
}

.detail .itemContent {
color: #ffffff !important;
}

.detail .divider {
border-bottom: 1px solid #990099 !important;
}

#ad_container {
opacity: 0 !important;
width: 0 !important;
height: 0 !important;
}

#footer {display:none; }
}
