css:
@-moz-document url("http://www.twitch.tv/rancidia/chat?popout=") {
@font-face {
	font-family: 'Continuum';
	src: url('https://dl.dropboxusercontent.com/u/70616174/Retriga.ttf')
        format('truetype');
}
body{
	font-family: 'Continuum';
        font-size:1.5em;
        hyphens: none;
}
.ember-chat .chat-messages .chat-line{
color:white;
text-shadow:
    3px 3px 0 #000,
    /* Simulated effect for Firefox and Opera
       and nice enhancement for WebKit */
   -1px -1px 0 #000,  
    1px -1px 0 #000,
   -1px  1px 0 #000,
    1px  1px 0 #000;
    hyphens: none;
}
.from{color:white !important;}
.chat-room{background: none repeat scroll 0% 0% #FF0000 !important;}
}
