css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.netvibes.com")
{
    .nv-reader-item .item-header .item-feedinfo
    {
      display:none!important;
    }
    
    .feed.nv-item-renderer
    {
      margin: 0px 10px !important;
      padding: 2px 5px 2px !important;
    }
    
    .nv-ir-feed-head
    {
      max-width: 1024px !important;
    }
    
    .nv-ir-feed-head h2
    {
      font-size: 1.5em !important;
      line-height: 1.5em !important;
      font-weight: bold!important;
      margin-top: 4px!important;
    }
    
    .nv-ir-feed-infos
    {
      margin-top: 2px!important;
    }
    
    .nv-ir-article-text
    {
      max-width: 1024px !important;
      font-size: 13px !important;
      line-height: 1.4em !important;
      color: #000000!important;
    }
    
    .nv-ir-article-text p
    {
      padding-top: 0.3em !important;
      padding-bottom: 0.3em !important;
      letter-spacing: 0.02em !important;
    }

    .nv-ir-article-text iframe, .nv-ir-article-text img:not(.wp-smiley)
    {
      margin: 0px 8px;
      padding: 2px;
    }

    .nv-sidebar-left
    {
      border-right: 1px solid #000000 !important;
    }

    div#smartreader-container div#smartreader-mainContainer div#smartreader-main.listView div#smartreader-feeds-container.smartreader-display div#smartreader-feeds-main.smartreader-main div#smartreader-feeds-inner div.nv-scroller div.no-native-scrollbars
    {
      font-size: 110%!important;
    }

    div#smartreader-container div.nv-sidebar div#smartreader-sidebar.nv-sidebarContainer div.nv-sidebar-scroller div.no-native-scrollbars div.nv-sidebar-scroller-content div#smartreader-mainTreeView ul
    {
      font-size: 105%!important;
    }
    
    div.nv-ir-article div.nv-ir-article-text
    {
      margin-right: 200px !important;
    }

    .nv-treeview-section .nv-treeview-row.hasUnread
    ,.hasUnread .nv-treeview-feed
    {
      color: #000000!important;
      font-weight: bold!important;
      background-color: #FFFFFF!important;
    }
    
    .nv-treeview-feed:hover .nv-treeview-row
    ,.nv-treeview-section .nv-treeview-row:hover
    {
      color: #000000!important;
      background-color: #B0B0B0!important;
    }
    
    .nv-treeview-feed.current:hover .nv-treeview-row
    {
      color: #FFFFFF!important;
    }
    
    .nv-treeview-feed.current:not(:hover) .nv-treeview-row
    {
      color: #000000!important;
      background-color: #B0B0B0!important;
    }
    
    .nv-sidebarContainer
    ,.nv-sidebarContainer div.nv-sidebar-scroller
    {
      color: #000000!important;
      background-color: #FFFFFF!important;
    }

    .nv-treeview-section .nv-treeview-row, .nv-treeview-feed
    {
      height: 20px!important;
    }
    
    .nv-treeview-section .nv-treeview-row:not(:hover) .nv-treeview-label
    {
      color: #000000!important;
    }
    
    .nv-treeview-section .nv-treeview-row .nv-treeview-label
    ,.nv-treeview-searchLabel
    {
      font-size: 12.6px!important;
      height: 20px!important;
      line-height: 20px!important;
    }

    .nv-treeview-searchLabel:before
    {
      vertical-align: top!important;
      top: -1px!important;
    }
    .nv-treeview-rowToggle:before
    {
      height: 16px!important;
      line-height: 16px!important;
      width: 16px!important;
    }

    .nv-treeview-section > .nv-treeview-content .nv-treeview-row .icon,
    .nv-treeview-section > .nv-treeview-row .icon
    {
      top: 2px !important;
    }

    .nv-treeview-unread
    {
      top: 0px !important;
    }

    .nv-reader-title
    {
      height: 24px!important;
      margin: 8px 6px 10px!important;
      padding-left: 4px!important;
    }
    
    .nv-sidebar-scroller
    {
      margin-top: -20px!important;
      bottom: 25px!important;
    }

    .nv-sidebar-left .nv-sidebar-scroller {
      transition: top 0.2s ease 0s!important;
    }

    .nv-sidebar-handler.left
    {
      margin-left: 5px!important;
      margin-right: 25px!important;
    }

    .toggle-static-sections {
      background-image: none!important;
      background-color: #F0F0F0!important;
      top: 5px !important;
      padding: 7px 0 22px 20px !important;
      margin: -1px !important;
      border: 1px solid black !important;
    }
    
    .nv-sidebar-footer
    {
      height: 25px !important;
      margin-top: 8px !important;
    }

    .smartreader-tab-button
    {
      height: 25px!important;
      line-height: 20px!important;
    }
    
    .smartreader-header-top .container
    {
      top: 2px !important;
    }

    .smartreader-header-top .container #smartreader-feeds-headerTitle
    {
      font-size: 0.9em !important;
    }

    .smartreader-header
    {
      height: 30px !important;
    }

    .nv-item-list-renderer .group-label
    {
      height: 26px !important;
      line-height: 26px !important;
      padding: 0 10px !important;
      background-color: #A0A0A0 !important;
    }
    
    .nv-reader-item
    {
      border-left: 0px !important;
      padding-left: 10px !important;
      padding-right: 10px !important;
    }
    
    .nv-reader-item .item-header
    {
      height: 25px !important;
      margin-left: 0px !important;
      left: -10px !important;
    }

    .nv-reader-item .item-header > * {
      line-height: 25px !important;
    }
    
    .nv-reader-item .item-header .item-enclosure-action
    {
      height: 20px!important;
      line-height: 20px!important;
    }

    .nv-reader-item .item-header .item-preview-wrapper
    {
      left: 10px !important;
    }
    
    .nv-reader-item:hover
    {
      margin-top: 0px !important;
      outline: none !important;
      background-color: #D0D0D0 !important;
    }
    
    .nv-reader-item.read
    {
      background-color: #E2E2E2 !important;
    }
    
    #smartreader-feeds-inner .nv-reader-listview .nv-reader-item:hover .item-content
    {
      background: none repeat scroll 0 0 #FFFFFF;
    }

    #smartreader-feeds-inner .nv-reader-listview .nv-reader-item .item-content
    {
      margin: 0 -10px !important;
      padding: 0 5px !important;
      background: none repeat scroll 0 0 #FFFFFF;
    }

    .nv-reader-item:not(.this-class-does-not-exist) .item-preview-wrapper:after
    ,.nv-reader-item.open .item-header .item-preview-wrapper:after
    {
      background-image: linear-gradient(90deg, rgba(255,255,255,0), white)!important;
    }

    .nv-reader-item:not(.this-class-does-not-exist) .item-header:hover .item-preview-wrapper:after
    ,.nv-reader-item.open:hover .item-header .item-preview-wrapper:after
    {
      background-image: linear-gradient(90deg, rgba(208,208,208,0), #D0D0D0)!important;
    }

    .nv-reader-item.read:not(.in-expanded-view):hover
    /*,.nv-reader-item.read.in-expanded-view:hover .item-content .feed .nv-ir-feed .nv-ir-feed-head*/
    ,.nv-reader-item.read.in-expanded-view:hover .item-header
    {
      background-color: #D0D0D0 !important;
    }

    .nv-reader-item.read:hover .item-header .item-preview-wrapper:after
    {
      background-image: linear-gradient(90deg, rgba(208,208,208,0), #D0D0D0)!important;
    }

    .nv-reader-item.read .item-header .item-preview-wrapper:after
    {
      background-image: linear-gradient(90deg, rgba(226,226,226,0), #E2E2E2)!important;
    }

    .nv-reader-item:after
    {
      display: none !important;
    }
    
    .nv-reader-item .item-title
    ,.item-preview-wrapper
    {
      color: #000000 !important;
      opacity: 1 !important;
    }
    
    .nv-reader-item .item-header .item-preview
    {
      color: #959595 !important;
    }
    
    .nv-item-renderer:not(:hover).in-expanded-view.read
    {
      background-color: #E2E2E2!important;
    }

    .nv-reader-expandedview .nv-item-renderer, .nv-reader-listview .nv-item-renderer
    {
      border: 0px!important;
    }
    
    #smartreader-feeds-header .grey.nv-dropdown
    {
      border: 1px black solid !important;
      top: 26px !important;
    }
    
    #smartreader-feeds-selection-actions .grey.nv-dropdown
    ,#smartreader-feeds-action-switcher .grey.nv-dropdown
    ,#smartreader-feeds-header .grey.nv-dropdown
    {
      border: 1px black solid !important;
      top: 26px !important;
      color: black !important;
    }
}
