css:
@-moz-document domain("hitbox.tv") {
.homeContent section {
  background-image: linear-gradient(to bottom, rgb(25, 25, 25) 0%, rgb(25, 25, 25) 100%) !important;
}
  
.chatContent {
	text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.8);
}
    
.title, .viewers, .meta {
	text-shadow: 3px 3px 2px rgba(0, 0, 0, 0.8);  
}

div.chatUsers{
	background:rgb(20, 20, 20)!important;
}

div.chatUsers > div.ng-scope > ul > li > a{
	color:rgb(202,202,202)!important;
}

div.chatUsers > div.ng-scope > ul > li > a:hover{
	color:rgb(100,202,100)!important;
}

/* Main Homepage */
div.homeContent, div.homeContent span {
	background:rgb(24, 24, 24)!important;
	color:rgb(200, 200, 200)!important;
}


form.form_enclosed.formL.ng-valid.ng-dirty{
	background:rgb(96, 96, 96)!important;
	color:rgb(200, 200, 200)!important;
}

/* Search slide out*/
.subNav .search{
	color:rgb(200, 200, 200)!important;
}

.subNav .search .head {
	color:rgb(200, 200, 200)!important;
}


/* Account settings */
.settingsContent .title{
	border-bottom: 1px solid rgb(0,0,0)!important;
}

div.settingsContent{
	background:rgb(24, 24, 24)!important;
	color:rgb(200, 200, 200)!important;
}

.active:after{
	border-color:transparent rgb(24, 24, 24)!important;
}

.homeContent section {
  	margin-top: -2px !important;
	border-top: 2px solid rgb(180, 180, 180)!important;
}

/* Footer */
.footer a{
	color:rgb(200, 200, 200)!important;
}

.footer a:hover{
	color:rgb(143, 191, 0)!important;
}

.active:after {
	border-color: transparent !important;
}

.subNav, .userSubNav {
	color:rgb(200, 200, 200)!important;
	background: linear-gradient(to bottom, #202020 0%,#070707 100%) !important;
}

div.scroll.ng-scope.ps-container{
	color:rgb(200, 200, 200)!important;
}


/* Stream page */
div.title > span.name.ng-scope.ng-binding,div.tName.ng-binding{
	color:rgb(200, 200, 200)!important;
}

div.title > span.status.ng-binding,div.tStatus.ng-binding{
	color:rgb(140, 140, 140)!important;
}

.detail .meta{
	color:rgb(200, 200, 200)!important;
	background: linear-gradient(to bottom, #202020 0%,#0C0C0C 100%);
	border-bottom:1px solid rgb(0, 0, 0)!important;
	border-top:1px solid rgb(0, 0, 0)!important;
  	box-shadow: 0 0 20px rgba(0, 0, 0, 0.8);
}

div.player.center{
	background:rgb(25, 25, 25)!important;
	border-bottom:none!important;
}

div.hover{
	color:rgb(252, 252, 252)!important;
}

div.hover.active{
	color:rgb(196, 196, 196)!important;
	border-bottom:6px solid rgb(196,196,196)!important;
}

div.hover:hover{
	color:rgb(200, 200, 200)!important;
}

div.detailInner,div.userContent{
	color:rgb(200, 200, 200)!important;
	background:rgb(25, 25, 25)!important;
}

section.ng-scope{
	color:rgb(200, 200, 200)!important;
}

h1,h2,h3,h4,h5,h6,h7{
	color:rgb(200, 200, 200)!important;
}

a.twitter-follow-button{
	color:rgb(152, 152, 152)!important;
}

a.twitter-follow-button:hover{
	color:rgb(63, 173, 240)!important;
}

div.link, div.embed{
	border:none!important;
}

h7.active{
	border-bottom:6px solid rgb(196,196,196)!important;
}

ul.chatBody{
	background:rgb(28, 28, 28)!important;
}

span.message.ng-binding, .chatBody .title, .chatBody .message {
	color:#D3D3D3!important;
}

section.chatInput{
	color:rgb(252, 252, 252)!important;
	background:rgb(25, 25, 25)!important;
	border-top: 3px solid rgb(45, 45, 45)!important;
}

textarea#chatInput{
	color:rgb(226, 226, 226)!important;
}

div.bufferTimestamp{
	background:rgb(28, 28, 28)!important;
}

li.admin{
	background:hsl(120,20%,12%)!important;
  border-bottom: 1px solid rgb(0, 0, 0) !important;
  box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.04) inset !important;
}

li.anon{
  background:rgb(30, 30, 30)!important;
  border-bottom: 1px solid rgb(0, 0, 0) !important;
  box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.04) inset !important;
}

li.anon:nth-child(2n){
  background-color: rgb(35, 35, 35)!important;
}

li.user:nth-child(2n){
  background-color: rgb(35, 35, 35)!important;
}

li.admin:nth-child(2n){
  background-color: hsl(120,20%,16%)!important;
}

li.user{
	background:rgb(30, 30, 30)!important;
    border-bottom: 1px solid rgb(0, 0, 0) !important;
  box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.04) inset !important;
}
	
span.name[style*="color: rgb(46, 46, 46);"],span.name[style*="color:#2E2E2E"]{
	color:rgb(200, 200, 200)!important;
}

span.name[style*="color: rgb(11, 97, 11);"],span.name[style*="color:#0B610B"]{
	color:rgb(22, 160, 22)!important;
}

ul.chatBody > *{
    font-family: Helvetica, Arial, sans-serif !important;
    line-height:1.2em !important;
}

span.icon-popup.hoverG2, span.icon-cancel.hoverG2{
	color:rgb(80, 80, 80)!important;
}

span.icon-popup.hoverG2:hover, span.icon-cancel.hoverG2:hover{
	text-decoration:underline!important;
	line-height:1.00em!important;
}

form.form_enclosed.formL.ng-valid.ng-dirty{
	background-color:rgb(24, 24, 24)!important;
}

textarea {
	color: #888;
  	background-color: rgb(25, 25, 25);
}

textarea:hover {
	background-color: rgb(35, 35, 35);
}

textarea::selection {
  	background-color: rgb(35, 35, 35);
}

.chatBody .time {
  margin-right: 0em!important;
}
    
.detail .header {
	background-color: rgb(25, 25, 25);
}

div.detail {
	box-shadow: 0 0 20px rgba(0, 0, 0, 0.5);
}
        
div.homeContent {
    color: rgb(25, 25, 25)!important;
}
        
/*div.player.center {
	margin-top: 37px!important;
  	margin-bottom: 38px!important;
}*/

.detail .mediaplayer {
  	width: 100% !important;
  	height: 0 !important;
    padding-bottom: 56.25% !important;
}

.nav {
	background: linear-gradient(to bottom, #202020 0%,#070707 100%);
  	box-shadow: 0 0 20px rgba(0, 0, 0, 0.8);
}

div.tPad {
	background-color: rgb(25, 25, 25)!important;
}

div.lower {
	background-color: rgb(25, 25, 25)!important;
}

div.detailInner {
	color: rgb(25, 25, 25)!important;
  	background-color: rgb(25, 25, 25)!important;
}

div.meta {
  	color: rgb(25, 25, 25)!important;
	border-left: 1px solid rgb(0, 0, 0)!important;
}

td.pollWrap {
  	background-color: rgb(30, 30, 30)!important;
  	color:rgb(200, 200, 200)!important;
}

button {
	background-color: rgb(55, 55, 55)!important;
}

div.votes > span.ng-binding {
  	color:rgb(200, 200, 200)!important;
}

div.followBubble {
  	background-color:rgb(200, 200, 200)!important;
}

.followBubble:after {
  	border-color: transparent rgb(200, 200, 200)!important;
   	content: "";
    position: absolute;
    top: 30%;
    left: -5px;
    border-style: outset solid;
    border-width: 5px 5px 5px 0;
    display: block;
    width: 0;
    z-index: 1;
}
    
.btnBlue, .btnBlue:hover, .btnBlueHover, .btnBlueHover:hover, .btn, .btn:hover, button.ng-scope, button.ng-scope:hover {
    background: -webkit-gradient(linear,left top,left bottom,from(#7CD636),to(#1A2E0B));
    background: -moz-linear-gradient(top,#7CD636,#1A2E0B);
    background: -o-linear-gradient(top,#7CD636,#1A2E0B);
    background: linear-gradient(top,#7CD636,#1A2E0B);
    background-color: #569626!important;
    border-top: 1px solid #5b3f8f;
    border-left: 1px solid #412771;
    border-right: 1px solid #412771;
    border-bottom: 1px solid #2a1453;
    box-shadow: inset 0 1px 0 rgba(255,255,255,0.1),0 1px 0 rgba(0,0,0,0.15);
    -moz-box-shadow: inset 0 1px 0 rgba(255,255,255,0.1),0 1px 0 rgba(0,0,0,0.15);
    -webkit-box-shadow: inset 0 1px 0 rgba(255,255,255,0.1),0 1px 0 rgba(0,0,0,0.15);
    text-shadow: 0 1px 0 rgba(0,0,0,0.75);
    -moz-text-shadow: 0 1px 0 rgba(0,0,0,0.75);
    -webkit-text-shadow: 0 1px 0 rgba(0,0,0,0.75);
    color: #fff;
    border-color: black!important;
  background-image: transparent!important;
}
    
button.btnBlue, button.btnBlue:hover, button.following, button.following:hover {
	text-shadow: 0 1px 0 rgba(0,0,0,0.75);
    -moz-text-shadow: 0 1px 0 rgba(0,0,0,0.75);
    -webkit-text-shadow: 0 1px 0 rgba(0,0,0,0.75);
    color: #fff;
    background-image: transparent !important;
  border-color: black!important;
}
   
div.hover:hover {
	color: hsl(75,100%,45%)!important;
}
    
div.hover.active {
	color: hsl(75,100%,45%)!important;
    border-bottom: 6px solid hsl(75,100%,45%)!important;
}
    
.userDescription div, .userDescription span, .userDescription li, .userDescription p {
	color: hsl(0,0%,55%);
}
      
.userDescription h1, .userDescription h2, .userDescription h3, .userDescription h4, .userDescription h5, .userDescription h6 {
	margin-top: 0.75em;
}
 
.detail .share .link, .detail .share .embed {
	border: 1px solid hsl(75,0%,20%)!important;
}
a {
	color: hsl(75,100%,40%)!important;
}
        
a:hover {
	color: hsl(75,100%,60%)!important;
}
    
body {
	background-color: rgb(25, 25, 25)!important;
}
        
.detail .btns {
	padding-top: 23px;
}

section.chatInput {
	box-shadow: inset 0 0 20px rgba(0, 0, 0, 0.5);
}

.chatBody .title, .chatBody .message {
	color: white;
}
}
