/* ROBLOX Theme made by Corey Blake (FreeModels) */
/* Any questions? Send me a PM on roblox! */
@-moz-document url-prefix('http://www.web.roblox.com'), url-prefix('https://www.web.roblox.com'),

    url-prefix('http://web.roblox.com'), url-prefix('https://web.roblox.com'),
    url-prefix('http://www.roblox.com'), url-prefix('https://www.roblox.com')


{
    body {
        background:url("http://i.imgur.com/tGQwZaq.png") no-repeat fixed;
        margin-top:10px;
        font-size: 8.5pt;
        font-family: Segoe UI;
        background-size: cover;
    }
    
    input[type=text] {
        border: 1px solid #A7A7A7;
    }
    
	#navigation-container a.btn-logo,
	#navigation-container a.btn-logo:visited,
	#navigation-container a.btn-logo:hover {
		background-image: url(http://i.imgur.com/RCsJUFL.png) !important
	}

    #BodyWrapper,#Body {
       -webkit-border-radius: 10px;
       -moz-border-radius: 10px;
       border-radius: 10px;
    }
    
    #ctl00_cphRoblox_rbxHeaderPane_statusBox {
       -webkit-border-radius: 4px;
       -moz-border-radius: 4px;
       border-radius: 4px;
    }
    
    .subMenu:after {
        position: absolute;
        color: white;
    	content: "ROBLOX Theme by Freemodels";
    	right: -70px;
    	padding: 5px;
    }
}
