css:
@namespace url(http://www.w3.org/1999/xhtml);
/* domain("www.gametrailers.com/")" */
@-moz-document regexp("http://www\\.gametrailers\\.com/videos/.*") {
    *{
        box-sizing:border-box;
    }
    
    #visible_header_m01{
       display:none; 
    }
    
    /*video - medium */
    .video_information-player .middle .player_wrap object{
        /*height:480px !important;*/
    }
    
    /*video - wide*/
    #content_holder{
       width:100%;
       min-width:720px;
    }
    #t2_lc{
        float:none !important;
    }
    .video_information-player{
        width:100%;
    }
    #player_wrap{
        width:100%;
        height:640px !important;
    }
    #video-player{
        height:100% !important;
        width:100% !important;
    }
    #video-player>object{
        width:100% !important;
        height:100% !important;
    }
    
    /*video - superwide*/
    @media screen and (min-width: 1600px){
        #player_wrap{
           height:750px !important;
        }
    }
        
    /*below video */
    .video_information-player .info{
        max-width:960px;
        width:100%;
        margin:auto;
    }
    #t4_lc{
        width:100%;
        margin-right:150px;
    }
    .flux_comments{
        width:100%;
    }
    
    #t4_rc{
       display:none;
    }
    
    #oo_tab{
        display:none;
    }
    .adv{
        display:none;
    }
}
