css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forecast.io") {
/* I'm aware that the CSS is really dirty and not optimized, I initially made it only for my personal use*/
/* Thanks for your understanding! ^____^ */





a.sponsor {display: none !important;}


#search_area {
    position: relative;
    width: 100%;
    height: 65px;
    padding: 10px 0px 6px;
margin-left: -5px !important;
    z-index: 3000;
    text-align: center;
    background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAABACAIAAAC/RDEUAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAADxJREFUeNpibNn+i4GBgeX///+4KCYGMMCnBEERVknQMOrax8jISA1XE+t4FgaG/1Szj+k/2DR8SgACDAD3gauHO9eS+QAAAABJRU5ErkJggg==") no-repeat scroll 0% 0% #ebecee !important;
    border-bottom: 0px solid rgb(88, 141, 210);}


#search_area .buttons {
    display: none;
}


body {
    background-color: rgb(248, 248, 248);
    color: rgb(34, 34, 34);
    font-family: "Segoe UI Light", "Open Sans", "Helvetica Neue", "Helvetica", "Arial",sans-serif;
    font-weight: 100;
    overflow-y: scroll;
}



#search_area .location_field {
    font-size: 24px;
    border: 0px none;
    height: 30px;
    line-height: 30px;
    background-color: rgba(255, 255, 255,0);
    padding: 1px 0px 0px 3px;
    text-align: center;
color:#333;
    width: 580px;
    border-radius: 5px 5px 5px 5px;
    box-shadow: 1px 1px 5px rgba(0, 0, 0, 0) inset, 0px 0px 0px transparent;
}



#search_area .refresh {
    position: absolute;
    top: 14px;
    left: 55px;
    height: 36px;
    line-height: 33px;
    width: 100px;
    color: #0483d9;
width: 125px !important;
font-weight: 800 !important;
font-size: 12px !important;
text-transform:uppercase;
    cursor: pointer;
    border-radius: 44px 44px 44px 44px;
    background-color: rgba(0, 0, 0, 0.0);
    box-shadow: 0px -1px 0px transparent, 0px 1px 0px transparent;
    -moz-user-select: none;
border: 1px solid #0483d9;
}


#search_area .refresh .icon {
    display: inline-block;
    position: relative;
    top: -1px;
    font-size: 16px;
    margin-right: 2px;
}



#units_toggle > .inner {
    margin: -5px 0px 0px -120px;
    width: 225%;
    height: 100%;
    border-radius: 0px 0px 0px 0px;
    overflow: hidden;
    white-space: nowrap;
    background-color: rgba(255, 255, 255, 0.15);
    background-image: linear-gradient(to bottom, rgba(255, 255, 255, 0), rgba(255, 255, 255, 0));
    background-repeat: repeat-x;
    border: 0px solid rgba(0, 0, 0, 0);
}



#units_toggle span {
    display: inline-block;
    width: 29px;
    height: 29px;
    line-height: 29px;
    font-family: "Segoe UI Light", "Open Sans", "Helvetica Neue", "Helvetica", "Arial",sans-serif;
    color: #333;
font-weight: 100 !important;
width: 65px !important;
font-size: 12px !important;
text-transform:uppercase;
    text-shadow: 1px 1px 0px rgba(255, 255, 255, 0.35);
    text-align: center;
display:none;
}




#units_toggle span.selected {
    box-shadow: 0px 0px 0px rgba(0, 0, 0, 0) inset, 0px 0px 0px transparent;
    background-color: rgba(0, 0, 0, 0.0);
    color: #0483d9;
    cursor: pointer;
    border-radius: 44px 44px 44px 44px;
    background-color: rgba(0, 0, 0, 0.0);
    -moz-user-select: none;
border: 0px solid #0483d9;
    top: 13px;
    left: 55px;
    height: 36px;
    line-height: 30px;
}









.desktop #location_strip {
    width: 100%;
    height: 40px;
    text-align: center;
    background-color: rgb(17, 17, 17);
    background-position: 0px 0px;
    background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAAUCAAAAACCUrN+AAAAFUlEQVQIHWP0ZmD5z8DyD4wgDKK5AD5hE3u4jBM0AAAAAElFTkSuQmCC");
    background-repeat: repeat-x;
    font-family: "Segoe UI Light", "Open Sans", "Helvetica Neue", "Helvetica", "Arial",sans-serif;
    font-size: 0px;
    white-space: nowrap;
    overflow: hidden;
    -moz-user-select: none;
display:none !important;
}





#map .map_container {
    position: absolute;
    top: 0px;
    left: 0px;
    width: 100%;
    height: 100%;
display:none !important;
}

#map.level2.hide_controls {
    position: absolute;
    top: 0px;
    left: 0px;
    width: 100%;
    height: 100%;
display:none !important;
}






.overview_container .section h2 {
    width: 100%;
    height: 35px;
    line-height: 40px;
    font-family: "Segoe UI Light", "Open Sans", "Helvetica Neue", "Helvetica", "Arial",sans-serif;
    font-size: 24px;
font-weight: 200;
    text-align: left;
    color: rgb(34, 34, 34);
}





.overview_container .section.currently .temp {
    display: inline-block;
    margin-top: 112px;
    font-size: 128px;
    font-family: "Segoe UI Light", "Open Sans", "Helvetica Neue", "Helvetica", "Arial",sans-serif;
font-weight: 100;
    line-height: 0;
    height: 0px;
}

.overview_container .section.currently #icon_current {
    display: inline-block;
    vertical-align: top;
    margin: 85px 0px 0px;
width:64px !important; 
height: 64px !important; 
}


.overview_container .section .desc {
    font-family: "Segoe UI Light", "Open Sans", "Helvetica Neue", "Helvetica", "Arial",sans-serif;
font-weight: 200;
    font-size: 16px;
    text-align: left;
}



.overview_container .section.currently .current_container .more .table {
    font-family: "Segoe UI Light", "Open Sans", "Helvetica Neue", "Helvetica", "Arial",sans-serif;
font-weight: 200 !important; 
    font-size: 16px;
    left: 0px;
    right: 0px;
    width: 146px;
    border-right: 1px dashed rgb(102, 102, 102);
}





.overview_container .section.currently .current_container .more {
    position: absolute;
    opacity: 0;
    transition: opacity 0.25s linear 0s;
    top: 65px;
    font-size: 16px;
}

.overview_container .section.currently .current_container .more .temps .m {
    font-family: "Segoe UI Light", "Open Sans", "Helvetica Neue", "Helvetica", "Arial",sans-serif;
font-weight: 200 !important; 
    font-size: 16px;
}

.overview_container .section.currently .current_container .more .label {
    display: inline-block;
    width: 68px;
    font-family: "Segoe UI Light", "Open Sans", "Helvetica Neue", "Helvetica", "Arial",sans-serif;
font-weight: 700 !important; 
    font-size: 13px;
text-transform:uppercase;
}














.overview_container .section.currently .desc {
    line-height: 1.2em;
    margin-top: 50px;
}







.overview_container .section.next_hour, .overview_container .section.next_24_hours {
    margin-left: 450px;
margin-top: -229px !important;
padding-bottom: 220px;
}



#outlook {
    position: relative;
    z-index: 99;
    width: 200%;
    background-color: rgb(248, 248, 248);
margin-left:-230px !important;
margin-top:-25px;
padding-left: 150px;
display:none;
}

#footer, #map, #weather_maps, #forecast_lines, #weather_maps, #forecast_lines  {
display:none;
}


.overview_container .section.next_24_hours {
margin-top: -215px !important;
}
   
    
/* 2014/12/29 - Little fix related to two-digits negative temperatures positioning */
    
.overview_container .section.currently {
    width: 400px !important;
}


}
