css:
@-moz-document domain("mycandylove.com") {
#D5E3E8
#forum>.pun.forum_post>h2 {
display: block;
background-color: #B6D0DA;
color: #000;
text-decoration: none;
font-size: 12px;
font-weight: 700;
margin: 0;
height: 20px;
border-bottom: none;
}
#forum>.pun.box>.inbox .author {
width: 167px;
float: left;
background-color: #D5E3E8;
margin-top: -20px;
padding: 5px;
}
#forum>.pun.box>.inbox .post {
padding: 2px 5px;
border-left: solid #D5E3E8 167px;
}
td.col1 .icon_offset a {
text-decoration: none;
font-size: 115%;
color: #317088;
}
.icon_offset.icon_offset_new a.pun_link_new {
color: #358DA3;
}
.icon_offset.icon_offset_new strong {
color: #3E89B4;
}
body {
 cursor:url(http://i61.tinypic.com/10pvwus.png), crosshairs !important;}
.forum_post {
border: 1px solid #5A82BE;
margin: 5px 0;
background: #FFFFFF;
clear: both;
}
a {
margin: 0;
padding: 0;
border: 0;
outline: 0;
vertical-align: baseline;
background: transparent;
font-family: sans-serif;
line-height: 20px;
color: #399ECE;
}
.friend .player-name a {
color: #7CBFDF;
font-size: large;
font-weight: bold;
margin-bottom: 6px;
display: block;
width: 140px;
}
span {
margin: 0;
padding: 0;
border: 0;
outline: 0;
vertical-align: baseline;
background: transparent;
font-family: sans-serif;
line-height: 20px;
color: #0076AD;
}
#logged-episode {
left: 6px;
top: 39px;
width: 88px;
height: 18px;
line-height: 18px;
color: #3698B1;
text-shadow: 2px -2px 2px #fff;
font-weight: bold;
font-size: 12px;
text-transform: uppercase;
text-align: right;
}
div {
margin: 0;
padding: 0;
border: 0;
outline: 0;
vertical-align: baseline;
background: transparent;
font-family: sans-serif;
font-size: 12px;
line-height: 20px;
color: #000;
}
#logged-pseudo a {
color: #84BEEB;
text-decoration: none;
}
#logged-pseudo span {
float: left;
color: #36A2CC;
text-shadow: 2px -2px 2px #fff;
font-weight: bold;
font-size: 14px;
}
h3, .integrated-title3 {
font-family: Nilland;
color: #1B95BB;
font-size: 11pt;
margin: 12px 0 6px;
text-transform: uppercase;
}
#right-informations #counter h2 .firstword .number {
letter-spacing: 5px;
font-family: Nilland;
font-size: 15pt;
color: #4DA7E2;
padding-right: 0;
background-position: -4px 2px;
}
.boyfriend h1 {
display: inline-block;
}
h1 {
color: #4DA7E2;
font-size: large;
margin: 0 0 16px;
padding: 0 8px;
text-transform: uppercase;
}
h1 {
border: 0;
outline: 0;
vertical-align: baseline;
background: transparent;
font-family: sans-serif;
line-height: 20px;
}
.boyfriend .show-comments, .boyfriend .show-comments-no-comment {
color: #81BEE0;
clear: both;
display: block;
}
.boyfriend .lom span {
background-color: #8CC0F0;
text-transform: uppercase;
color: white;
font-weight: bold;
font-size: x-small;
padding: 2px;
}
#astrological-sign a {
text-decoration: none;
font-family: Nilland;
font-weight: bold;
font-size: 13px;
color: #43A4D1;
}
.title-forum a {
text-decoration: none;
display: block;
font-size: 115%;
color: #2A74B8;
}
#punwrap thead {
border: 1px solid #1B8DCE;
border-bottom: 0;
background:#6B9ECE;
}
h2 .firstword, .integrated-title2 .firstword {
font-family: Nilland;
font-size: 14pt;
color: #1096A8;
font-style: normal;
}
#container.connected h2 {
font-family: Nilland;
margin: 12px 0 6px;
border-bottom: 1px dashed #cfcfcf;
}
h2, .integrated-title2 {
font-family: Nilland;
font-size: 14pt;
font-style: italic;
color: #A0C6E4;
text-transform: uppercase;
}

#profile div#avatar {
background: white url('http://i57.tinypic.com/akv51c.png') center bottom no-repeat;
position: relative;
width: 260px;
height: 400px;
overflow: hidden;
border: 2px #aed05d solid;
border-radius: 10px;
-moz-border-radius: 10px;
z-index: 1;
}
#news-list .news a {
text-decoration: none;
color: #2BB6C9;
font-weight: bold;
}
#episodes_list .episode_wrap {
position: absolute;
width: 522px;
height: 406px;
background-image: url("http://i60.tinypic.com/2zdt1rn.png");
background-repeat: no-repeat;
}
#sous-menu {
width: 100%;
height: 40px;
position: relative;
margin-top: -20px;
background: url("http://i59.tinypic.com/6ozu5c.png") no-repeat scroll top center transparent;
}
#container{
background-image : url(http://www.miscupload.com/upload/854821747386365870220344.gif) !important ; background-repeat: no-repeat;
}
#container.connected #header { background-image : url(http://i59.tinypic.com/r2kox0.png) !important ;
}
.idcard-admin {
background-image: url('http://i62.tinypic.com/4re053.png');
}
.idcard-moderator {
background-image: url('http://i57.tinypic.com/i1irm0.png');
}
.idcard-member { background : url(http://i57.tinypic.com/2iavix0.png) no-repeat !important ;
}
}
@-moz-document regexp("http://www.mycandylove.com/profil/daizy106") {
.idcard-member { background : url(http://i57.tinypic.com/2iavix0.png) no-repeat !important ;
}
}
