css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("volafile.io") {
    
*{

    border-color: rgb(214,214,214)!important;
    
}*

#overlay.visible {

    background: rgb(214,214,214);
}

#room_name, #room_name_container{

    text-align: center;

    background: rgb(180,180,163);

    color: rgb(40,40,40);

}

#header_row1.header_row {

    background: rgb(160,120,110);

}

.fa {
  
    color: rgb(80,80,80)!important;
    
}

.chat_message {

    background: rgb(208,212,194);

    color: rgb(60,60,60);

}

#chat_messages .chat_message.user a.username {

    color: rgb(80,80,80);

}

#chat_messages .chat_message.admin a.username {

    color: rgb(1,1,1);

}

#chat_messages .chat_message.admin {

    background: rgb(201,197,174);

}

#chat_messages .chat_message.rank a.username {

    color: rgb(124,40,80);

}

#chat_messages .chat_message a.username {

    color: rgb(70,70,70);

}

.chat_message.highlight, .chat_message.highlight.admin {

    background: rgb(164,154,174);

    border-color: rgb(1,1,1);

    border-width: 4px    

}

.chat_file {

    border-radius: 1px;

    background: rgb(214,214,214);
  
    border-color: rgb(0,0,0);
  
    color: rgb(0,0,0);
        
    text-align: center

}
    
.chat_file:hover {
        
        background: rgb(224,224,224)!important;
       
        color: rgb(0,0,0)
    }

.chat_file:visited {
        
        color: rgb(100,124,124)!important
    }    
    
#chat_frame a:visited { 
 
    color: rgb(214,214,214); 

}

.nano > .pane > .slider {

    border-radius: 1px;

    background: rgb(168,142,134);

}

.nano > .pane > .slider:hover {

    background: rgb(168,142,154)

}

.nano:hover > .pane, .pane.active, .pane.flashed {

    background: rgb(208,212,194);

    opacity: 1

}

.nano > .pane.active > .slider {

    background: rgb(208,212,194);

}

#chat_hbar, #header_row2.header_row {

    background: rgb(160,120,110);

    color: rgb(1,1,1);

}
 
.dropdown_item {
        
    background: rgb(160,120,110)
            
}
    
.dropdown:hover {
        
        background: rgb(80,80,80)
            
}
    
.dropdown_item:hover {
        
        background: rgb(214,214,214);
        
        color: rgb(120,140,200);
}

b, strong {
        
        color: rgb(214,214,214)
    
}    

textarea, input[type="text"], input[type="password"] {
        
        background: rgb(90,90,90)
                
    }    
    
.button, input[type="submit"] {
        
        border-radius: 1px;
        
        color: rgb(214,214,214)    
    }

.button:hover {
    
        background: rgb(224,224,234)!important;  
        
        
        
    }
    
#chat_name, #chat_input {

    background: rgb(210,220,213);

    color: rgb(50,40,40);
        
    border: none

}

a:hover {

    color: rgb(214,214,214);

}

.header_row_element, #header_row1, #header_row2 {
	
	border-color: white;
	
}

textarea.defaultValue {

    color: rgb(214,214,214);

}

#header_row_container a i.green-icon {

    color: rgb(1,1,1)!important;

}

#header_row_container a:hover i.green-icon {

    color: rgb(1,1,1)!important;

}

#header_row_container a:active i.green-icon {

    color: rgb(1,1,1)!important;

}

#search_input.defaultValue {

    background: rgb(190,190,190)!important;

    color: rgb(0,0,0);

}

#search_input {

    background: rgb(120,120,120)!important;

}

.toggle > i {
    
    color: rgb(0,0,0);
    
}

.toggle.enabled > i {

    color: rgb(1,1,1)!important;
    
}

.button, input[type="submit"] {

    background: rgb(214,214,214);

    color: rgb(0,0,0);

    margin-right: 1px;

}

#uploadButton {

    border-radius: 1px;

}

#uploadButton:hover {

    color: rgb(1,1,1);

    background: rgb(1,1,1)

}

#uploadButton:active {

    background: rgb(214,214,214);

    color: rgb(1,1,1)

}

#filter_reminder {

    background: rgb(124,124,124);

}

#filter_reminder:hover {

    background: rgb(114,124,164);

}

.file_uploading {

    background: rgb(214,214,174)!important;
    
}

#file_list.uneven .filelist_file:nth-child(2n+1) {
    background: rgb(214,212,194);
    color: rgb(1,1,1);
}

#file_list.even .filelist_file:nth-child(2n) {

    background: rgb(214,212,194);
    
    color: rgb(1,1,1);
    
}

.filelist_file {

    background: rgb(214,207,194);
    
    color: rgb(0,0,0);
    
}

.file_icon.filetype.music_playing {

    color: rgb(160,120,110)!important;
    
}

#gallery_title {
        
        color: rgb(214,214,214)
    }    
    
.gallery_button {
  
    border-radius: 1px;
  
    border-color: rgb(214,214,214)!important;
  
    color: rgb(1,1,1);
    
}

.ui_frame {
  
    background: rgb(160,120,110);
    
    border-color: rgb(214,214,214);
    
    border-radius: 25px;
    
}

a {

    color: rgb(80,80,80)!important;

}


    
a.file_name {
  
  color: rgb(0,0,0);
  
}

a.file_name:visited {
  
  color: rgb(0,0,0);
  
}

a.file_name:visited:hover {
  
  color: rgb(0,0,0);
  
}

a:hover {
  
  color: rgb(1,1,1);
  
}

.file_control_icon.clickable {
  
      color: rgb(0,0,0);
      
}
    
.file_control_icon.clickable:hover {
  
      color: rgb(1,1,1);
            
}
          
.file_icon {
        
      color: rgb(0,0,0);
              
}
    
.file_tag.tag_key_user{
        
        background: rgb(160,120,110);
        
        border-radius: 1px;
       
        color: rgb(214,214,214)
            
}
    
.file_tag.tag_key_user:hover {
        
        background: rgb(160,120,110);
        
}

.file_tag {
        
        background: rgb(170,130,120);
        
        border-radius: 1px;
            
        color: rgb(214,214,214);
}
    
.file_tag:hover {
        
        background: rgb(114,114,94)
    }    
    
html {

    background: rgb(208,212,194);
    
}

#main_logo {

    color: rgb(1,1,1);
    
}

#main_logo_suffix {

    color: rgb(0,0,0);
    
}

.button:hover {

    background: rgb(214,214,214);
    
}

.Button:active {

    background: rgb(214,214,214)!important;
    
}

#main_discover_button {

    background: rgb(214,214,214);
    
    color: rgb(1,1,1);
    
}

pre {
  
  color: rgb(1,1,1);
  
}
    
    table th {
        
        background: rgb(1,1,1);
        
        border-color: rgb(1,1,1)
    }
    
table td {
        
        border-color: rgb(1,1,1)
    }
    
#radio_container {
    
    background: rgb(214,214,214);
        
    border-color: rgb(1,1,1)
}
    
#radio_current_song {
    
    background: rgb(160,120,111);
        
    border-color: rgb(1,1,1)!important;
}   
    
#radio_current_time, #radio_left {
    
    background: rgb(124,124,124);
        
    border-color: rgb(1,1,1)
        
}
    
#radio_current {
    
    border-color: rgb(214,214,214)!important;
        
    border-radius: 1px
}
}
