css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("leagueoflegends.com") {
    html body {
        color: #ccc !important;
    }
    
    h1, h2, h3, h4 {
        color: #ddd !important;
        text-shadow: 1px 1px #444;
    }
    
    .white-stone {
        background: #333 !important;
        color: #eee !important;
    }
    
    .post-row .post-header {
        color: #aaa !important;
    }
    
    .post-row .post-message
    {
        color: #ddd !important;
    }
    
    .post-row .quote-message {
        background-color: #444 !important;
    }
    
    
    .section-wrapper .section-wrapper-top:before {
        background: rgba(32, 32, 32, 0.6) !important;
    }
    
    .section-wrapper .section-wrapper-top {
        background: none !important;
        color: #444 !important;
    }
    
    .section-wrapper .section-wrapper-content {
        background: rgba(32, 32, 32, 0.2)  !important;
         /*border-radius:80px;*/
    }
    
    .section-wrapper .section-wrapper-bottom {
        background: rgba(128, 128, 128, 0.2) !important;
background: linear-gradient(#000, #070707, #111) !important;
    }
    
    #footer {
        background: #111 !important;
    }
    
    #discussion {
        background: rgba(32, 32, 32, 0.2)  !important;
    }
    
    html:not(.oldie) .section-wrapper .section-wrapper-content .section-wrapper-content-wrapper .section-wrapper-content-wrapper-frame > .gs-container {
        background: none !important;
    }
    
    html:not(.oldie) .section-wrapper .section-wrapper-content .section-wrapper-content-wrapper .section-wrapper-content-wrapper-frame {
        background: none !important;
    }
    
    
    /* Comments */
    .riot-apollo .riot-discussion {
        background-color: rgba(0,0,0,0.0) !important;
        color: #ddd !important;
    }
    
    /* reply box */
    .reply-form-container, .edit-container {
        background-color: rgba(128, 128, 128, 0.4) !important;
    }
    
    /* reply blockquote preview */
    .discussion-list-item .reply-form-container .live-preview blockquote, .comment-list-item .reply-form-container .live-preview blockquote {
        background-color: rgba(0,0,0,0.4) !important;
    }
    
    /* parent comment */
    .riot-apollo .riot-discussion .riot-comment,
    .discussion-list-item, .comment-list-item {
        background: #333;
    }
    
    .riot-apollo .riot-discussion .riot-comment
    .riot-collection > .riot-comment,
    .comment-list-item > .list > .comment-list-item {
        background: #222;
    }
        
    .riot-apollo .riot-discussion .riot-comment
    .riot-collection > .riot-comment >
    .riot-collection > .riot-comment,
    .comment-list-item > .list > .comment-list-item > .list > .comment-list-item {
        background: #333;
    }
    
    .riot-apollo .riot-discussion .riot-comment
    .riot-collection > .riot-comment >
    .riot-collection > .riot-comment >
    .riot-collection > .riot-comment,
    .comment-list-item > .list > .comment-list-item > .list > .comment-list-item > .list > .comment-list-item {
        background: #222;
    }

    .riot-apollo .riot-discussion .riot-comment
    .riot-collection > .riot-comment >
    .riot-collection > .riot-comment >
    .riot-collection > .riot-comment >
    .riot-collection > .riot-comment,
    .comment-list-item > .list > .comment-list-item > .list > .comment-list-item > .list > .comment-list-item > .list > .comment-list-item {
        background: #333;
    }
    
    .riot-apollo .riot-discussion .riot-more {
        background: #272727 !important;
    }
    
    .riot-apollo .riot-discussion .riot-comment .riot-voting li {
        color: #ccc !important;
    }
    
    
    
    /* Match History update */
    /* may need to move this to custom domain matchistory.[SERVER].leagueoflegends.com ?? */
    
    /* fix border around blank item slot */
    .scoreboard .player .items-col .inventory .no-item {
        border-color: rgba(0,0,0,0.0) rgba(0,0,0,0.0) rgba(0,0,0,0.0) rgba(0,0,0,0.0) !important;
    }
    
    .scoreboard .team header > div {
        background-color: #111 !important;
    }
    
    .grid-list > li > * {
        background-color: #333 !important;
    }
    
    /* brighter summoner name link */
    html body a, html body a:visited {
        color: #45a3a2 !important;
    }
    
    .scoreboard .team footer > div {
        background-color: #111 !important;
    }
    
    /* team-100 = blue team */
    .scoreboard .team-100 footer > div {
        background-color: #112 !important;
    }
    
    /* team-200 = purple team */
    .scoreboard .team-200 footer > div {
        background-color: #212 !important;
    }
    
    
    /* GOLD GRAPH */
    
    /* gold graph axis text */
    svg .axis text {
        fill: #aaa !important;
    }
    
    .event-graph .team-100-objectives-bg, .event-graph .team-100-kills-bg {
        color: #aaa !important;
    }
    
    .event-graph .team-200-objectives-bg, .event-graph .team-200-kills-bg {
        color: #aaa !important;
    }
    
    
    
    /* DETAILED TABLE */
    
    table.table {
        background-color: #333 !important;
    }
    
    table.table-bordered tbody td {
        border-bottom: 1px solid #222 !important;
    }
    
    table.table-bordered td, table.table-bordered th,
    table.table-bordered thead tr:first-child th {
        border: 1px solid #555 !important;
    }
    
    .game-grid table .team-100 {
        background-color: #445 !important;
    }
    
    .game-grid table .team-200 {
        background-color: #545 !important;
    }
    
    .game-grid table .team-100.current-user {
        background-color: #334 !important;
    }
    
    .game-grid table .team-200.current-user {
        background-color: #434 !important;
    }
}
