css:
@-moz-document url-prefix("http://www.books24x7.com/toc.aspx") {
body *{
/*  background-color: #F7F2E6 !important; */
  text-rendering: optimizeLegibility !important; 
}

body {
  margin: 0px !important;
}

table#HeaderTable, #footerAsset, tr[height="11"], .blueline {
  display: none !important;
}

#ctl00_ContentPlaceHolder1_ContentAreaUpdatePanel {
    background: none repeat scroll 0 0 !important;
/*  	-webkit-column-count: 2 !important;   */
}
 
#ctl00_ContentPlaceHolder1_ContentPanel {
    margin: 20px 5% 20px 7% !important;
    line-height: 2.8em !important;
}
 
#ctl00_ContentPlaceHolder1_ContentPanel p {
    font-size: 1.5em !important;
}
 
#ctl00_ContentPlaceHolder1_ContentPanel h3 {
    font-size: 2em !important;
}
 
#ctl00_ContentPlaceHolder1_ContentPanel h2 {
    font-size: 2.5em !important;
}

tt {
  font-size: 0.9em !important;
  color: rgb(62, 144, 148) !important;
}

pre {
  font-size: 1.5em !important;
}
/*
span.figuremediaobject>img {
	height: 100% !important;
    width: 100% !important;
}
*/

div#ctl00_ContentPlaceHolder1_AssetMetaUpdatePanel {
  position: fixed !important;
  top: 20px !important;
  left: 20px;
}

div#ctl00_ContentPlaceHolder1_AssetMetaUpdatePanel td, div#ctl00_ContentPlaceHolder1_AssetMetaUpdatePanel img{
  width: auto;
  height: auto;
}


td#ctl00_ContentPlaceHolder1_AssetMetaControl_bookseparator {
  position: fixed !important;
  top: 260px !important;
  left: 20px !important;
  width: 20% !important;
  max-width: 250px !important;
}


div#TOCMenuDiv {
  position: fixed !important;
  top: 450px !important;
  left: 20px;
}

div#TOCMenuDiv a {
  font-size: 1.3em !important;
}

tr.b24-leftchapter {
  color: rgb(19, 111, 139) !important;
}

#ctl00_ContentPlaceHolder1_TopProgressControl_PreviousSection, #ctl00_ContentPlaceHolder1_TopProgressControl_NextSection {
  position: fixed !important;
  top: 50% !important;
  display: none !important;
}

#ctl00_ContentPlaceHolder1_TopProgressControl_NextSection {
  right: 20px !important;
}

/*
#ctl00_ContentPlaceHolder1_ContentAreaUpdatePanel :nth-child(7) {
  display: none !important;
}
*/

#ctl00_ContentPlaceHolder1_AssetMetaControl_bookseparator {
  width: 20% !important;
}
pre {
  overflow: auto !important;
}
code,
kbd,
pre,
samp {
  font-family: monospace, monospace !important;
  font-size: 1em !important;
}
pre,
  blockquote {
    border: 1px solid #999 !important;

    page-break-inside: avoid !important;
  }
pre,
samp {
  font-family: Menlo, Monaco, Consolas, "Courier New", monospace !important;
}
pre {
  display: block !important;
  padding: 9.5px !important;
  margin: 0 0 10px !important;
  font-size: 13px !important;
  line-height: 1.428571429;
  color: #333 !important;
  word-break: break-all !important;
  word-wrap: break-word !important;
  background-color: #f5f5f5 !important;
  border: 1px solid #ccc !important;
  border-radius: 4px !important;
}
pre code {
  padding: 0 !important;
  font-size: inherit !important;
  color: inherit !important;
  white-space: pre-wrap !important;
  background-color: transparent !important;
  border-radius: 0 !important;
}
.pre-scrollable {
  max-height: 340px !important;
  overflow-y: scroll !important;
}

.admon-title {
    color: red !important;
  }

#MenuTD, #MenuTD *, #ctl00_ContentPlaceHolder1_AssetMetaUpdatePanel * {
  background-color: #f5f5f5 !important;
}

#MenuTD {
  width: 22%;
}

#TOCMenuDiv {
  width: 20%;
}

#ctl00_ContentPlaceHolder1_BottomProgressControl_PreviousSection, #ctl00_ContentPlaceHolder1_BottomProgressControl_NextSection {
  display: none !important;
}


#container, #TOCMenuDiv {
  height: auto !important;
}


#ctl00_FooterPanel, #lefthandcol, #lefthandcol + td + td + td + td + td + td {
  display: none !important;
}
}
