css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {


body, a, a:hover {

}

body {

  background: #e1e1e1 url("http://i60.tinypic.com/a4c66u.jpg") top left fixed no-repeat !important;
  background-size:cover !important;
}



.login.sign_up,
A[href="/forgot_password"],
#login_form SPAN,
.footer_links {
  display: none !important; 
}


div.l-header .tab.iconic a{
  background-color: rgba(0,0,0, 0) !important;
  border-radius: 10px !important;  
}


.search_form_row  {
background: rgba(0,0,0, 0) !important;
color: #000 !important;
  }




     
::-webkit-scrollbar-thumb{
background-color: #000;
border: 2px solid #fff;
height:auto;
}
 
::-webkit-scrollbar {
height:9px;
width:9px;
background-color: #000;
border: 4px solid #fff;
}

::selection {
background-color:#f9f9f9;
}





















































#right_column:after { 
display: none !important; 
}

#right_column a {
color:rgba(255,255,255,1)!important;  
}

#right_column .controls_section li a {
  color: rgba(255,255,255,1) !important; /*(0,0,0, 0.75) (255,255,255,0.75)*/
}

#right_column .controls_section li a .count {

  color: #fff !important; 
}

.controls_section li .posts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.posts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .posts:before {
  content: "Posts" !important;
}.controls_section li .followers:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.followers  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .followers:before {
  content: "Followers" !important;
}.controls_section li .drafts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.drafts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .drafts:before {
  content: "Draft" !important;
}.controls_section li .queue:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.queue  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .queue:before {
  content: "Queue" !important;
}




.new_post_label_icon> I {
display: none !important
}

.new_post_label { 
   padding-top: 75px !important; 
   color: rgba(0,0,0,1) !important; 
   font: italic 19px georgia, serif !important; 
}

.new_post_label:hover { 
   color: rgba(0,0,0,0.5) !important; 
}

#new_post {
    background: #fff none no-repeat left bottom !important;
    height: 100px !important; 
}

#new_post .arrow {
    display: none !important;
}
        
        
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background: rgba(0,0,0, 0) !important; 
}


.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 11px rgba(236,191,201, 0.50) !important;
border-right: 11px solid transparent !important;
border-left: 11px solid transparent !important;
}
.selection_nipple:not([class="selection_nipple white"]) {
border-bottom: solid 11px rgba(236,191,201, 0.50)  !important;
}




.mceContentBody {
  background: url("http://i60.tinypic.com/a4c66u.jpg") !important; 
  background-size: 100% !important; 
  color: rgba(255,255,255, 1.0) !important; 
}


#content_top {
  width: 0px !important;
  height: 0px !important;




}


#content_bottom {
  width: 0px !important;
  height: 0px !important;




}


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  padding-top: 25px !important; 
  padding-bottom: 25px !important; 
   -moz-border-radius: 20px !important; 
}


LABEL {
  color: rgba(0,0,0, 1.0) !important; 
}



.radar {
  display: none !important; 
}



#footer {
  display: none !important; 
}
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue,
.selected .likes,
.selected .activity  {
  color: rgba(255,255,255, 0.9) !important; 
  background: transparent !important; 
}

img.content_bottom {
display:none !important;
}


#new_post .arrow {
  opacity: 0.50 !important;
  margin-left: -3px !important; 
}
}
