.xenOverlay .sectionFooter{background-color:#343434 !important;} 
    
/*Лайки*/
li.message .likesSummary{background-color:#2f3d31 !important;}
    
/*блок пользовательских меню*/
ul.visitorTabs{margin-top:-302px !important;} 
ul.visitorTabs li a{height:32px !important;}
.navTabs .navTab.selected.PopupOpen .navLink{background-color:#44444B !important;}
ul.visitorTabs li.account a .accountUsername, ul.visitorTabs .inbox a.navLink, ul.visitorTabs .alerts a.navLink{max-width:300px !important;color: #FFF !important;font-size: 12px !important;font-family: verdana !important;font-weight:normal !important;}

/*Учетная запись*/
.pageContent .container .mainContentBlock/*фон основного блока на странице "учетная запись"*/{background-color:white !important;}
.pageContent .container .mainContentBlock .personalDetailsForm{color:black !important;}
.pageContent .container .mainContentBlock .personalDetailsForm dl.avatarEditor dd a.avatar span.img/*корректировка расположения аватара на странице учетная запись*/{background-position: 0px !important;}
.xenOverlay .AvatarEditor/*всплывающий блок замены аватара*/{background-color:#ddd !important;color:white !important;}
.xenOverlay .AvatarEditor li.avatarOption{background-color:#44444B !important;}
.xenOverlay .AvatarEditor li.saveDeleteControls{color:black !important;}   
.pageContent .container .navigationSideBar ul li ul li a{color:#ddd !important;}
.pageContent .container .navigationSideBar ul li ul li a.secondaryContent/*корректировкаблока персональная информация*/{background-color: #44444B !important; font-size:11px !important;font-weight:normal !important;}
.pageContent .container .navigationSideBar ul li ul li a:hover/*выделение при наведении на меню*/{background-color: #DDDDF8 !important; color:black !important;border-left:1px solid black !important;}    
.pageContent .container .mainContentBlock div ol.alerts li.alertGroup h2/*дни на странице "оповещения"*/{color:black !important;}
.pageContent .container .mainContentBlock div ol.alerts li.alertGroup .primaryContent/*список оповещений*/{background-color:#ddd !important;} 
.pageContent .container .mainContentBlock .newsFeed ol.eventList li.NewsFeedItem{background-color:#ddd !important;}
.pageContent .container .mainContentBlock .newsFeed ol.eventList li.NewsFeedItem .content, .pageContent .container .mainContentBlock .newsFeed ol.eventList li.NewsFeedItem .content .description, .pageContent .container .mainContentBlock .newsFeed ol.eventList li.NewsFeedItem .content .description em{color:black !important;}    
.pageContent .container .mainContentBlock .newsFeed ol.eventList li.NewsFeedItem a.avatar .img{background-position:0px !important;}
.pageContent .container .mainContentBlock .PageNav .pageNavHeader{color: black !important;}
.pageContent .container .mainContentBlock .PageNav nav a.currentPage, .pageContent .container .mainContentBlock .PageNav nav a:hover{background-color:#DDD !important;}
.pageContent .container .mainContentBlock .PageNav nav a{background-color:white !important;}


/*всплывающий блок*/
form.formOverlay{background-color:#ddd !important;} 
form.formOverlay h2.heading{background-color:#44444B !important; color:white !important;}
form.formOverlay dl.ctrlUnit, form.formOverlay p{color:black !important;}
    
/*окно о слишком частой отправке сообщений*/
.xenOverlay .errorOverlay{background-color:#44444B !important;}  

    
.quickReply .messageUserInfo .avatar img/*борд у аватарки в быстром ответе*/{border:2px solid black !important;}     
    
/*-------блок аватара в сообщения---------*/
/*аватарка*/
#messageList .messageUserInfo .messageUserBlock .avatar img{border:2px solid black !important;}    
/*смешение нижнего блока*/
#messageList .messageUserInfo .messageUserBlock .extraUserInfo{margin-top:-3px !important;}
/*титул*/
#messageList .messageUserInfo .messageUserBlock .extraUserInfo em.userTitle{width:140px !important;max-width:140px !important;position:absolute !important;background-color:black !important;margin:-151px 0px 0px 8px !important;opacity:0.8 !important;}  
/*должность*/
#messageList .messageUserInfo .messageUserBlock .extraUserInfo em.bannerStaff{width:130px !important;max-width:130px !important;position:absolute !important;margin:-30px 0px 0px 8px !important;opacity:0.8 !important;font-size: 12px !important;max-height:15px !important; border:0px !important;}
/*информация*/    
#messageList .messageUserInfo .messageUserBlock .extraUserInfo dl.pairsJustified{background-color:#44444B !important;min-height:17px !important;margin:0px !important;}
#messageList .messageUserInfo .messageUserBlock .extraUserInfo dl.pairsJustified dt{padding-left:2px !important;}
#messageList .messageUserInfo .messageUserBlock .extraUserInfo dl.pairsJustified dd{padding-right:2px !important;}
 
li.node .nodeInfo .nodeLastPost a.avatar img{margin-top:-1px !important;}
 
/*блок опросов*/
.pollResult .barContainer{border:1px solid black !important;}

.rating-buttons a{background: none repeat scroll 0% 0% transparent !important;}
.rating-buttons a:hover{background:#ddddf8 !important;}
.rating-buttons a.active{background:#ddddf8 !important;}
  
    
