ol.discussionListItems li#thread-242584, ol.discussionListItems li#thread-242584 div.posterAvatar, ol.discussionListItems li#thread-242584 div.pairsJustified,  ol.discussionListItems li#thread-242584 div.pairsJustified dt, ol.discussionListItems li#thread-242584 .DateTime{color:black !important;}    
