css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deezer.com") {
    
    
    #page_feed .card-edito-track .waveform
    {
        color: green !important;
        background-color: red !important;
    }
    
    
    div#naboo_menu_list_music,     
    div#naboo_playlist,            
    div#naboo_album,               
    div#tab_review_content,        
    div#tab_comment_content,       
    div#naboo_artist,              
    div#tab_video_list_content,    
    div#tab_fan_content,           
    div#tab_related_content,       
    div#tab_discography_content,   
    div#tab_biography_content,     
    div#naboo_content,
    div.user-section-suggestion, 
    div#naboo_datagrid_options, 
    #page_content 
    {
       background-color: black !important;
       
    }
  
     a.link-gray-dark, 
    .link-gray-light { 
       color: #999 !important;
    }
    
    
    #page_feed .card {
       background-color: black !important;;
    }
    #page_feed .footer {
       background-color: black !important;
    }
    #page_feed .divider .divider-text { 
        color: white;
        background-color: #1990DB;
    }
    
    
    .thumbnail:hover .dropdown a {
       background-color: black !important;
    }
    
    
    .thumbnail-col {
       background-color: black !important;
       border: 1px solid #D8D8D8;
    }
    .thumbnail-caption dt > a {
        color: #1990DB !important;
    }
    
    .thumbnail-caption .thumbnail-caption-heading.genre-name {
       color: #CCCCCC !important;
        border-radius: 10px;
        border-color: #444444 !important;
        background-color:  #1A1A1A !important;
    }
    
    
    
    #page_top > .wrapper .thumbnail-col .thumbnail-caption.infos-top .number-top-cell .number-top:after {
        background-color: black !important;
    }
  
    
    #page_radio .radio-highlighted {
       background-color: black !important;
       border: 1px solid #D8D8D8;  
    }
    #page_radio .radio-highlighted .radio-detail h2
    {
       color: #999;
    }
    
    
    .navbar > ul {
       background: linear-gradient( #333333, #000000) repeat scroll 0 0 #000000 !important;
    }
    .navbar-nav > li > a {
       color: white !important;
       background-color: black !important;
       background: linear-gradient( #333333, #000000) repeat scroll 0 0 #000000 !important;
    }
    
    
    #page_profile .user-profil .user-info {
        background-color: #1A1A1A !important;
    }
    
    
    .dropdown-menu {
       background-color: #1A1A1A !important;
       border-radius: 2px;
       border-color: #444444 !important;
    }
    .dropdown-menu > li > a {
          color: #999999  !important;      
    }
   .dropdown-menu > li > a:hover {
    
       background-color: #333333 !important;
       color: #999999  !important;
    }
    
     
    .form-control,              
    .cell-input input,          
    #naboo_menu .block_create a 

    {
        background-color: #1A1A1A !important;
    }
    
    
    .comment-box .comment-list .user-comment .message
    {
        background-color: #1A1A1A !important;
    }
    
    
    .btn {
        color: #CCCCCC !important;
        border-radius: 2px;
        border-color: #444444 !important;
        background: linear-gradient( #333333, #000000) repeat scroll 0 0 #000000 !important;
    }
    .btn:hover {
        color: #666666 !important; 
        background: linear-gradient(#000000, #333333) repeat scroll 0 0 #000000 !important;
    }
    
     
    .nav-tabs > li.selected a, .nav-tabs > li.active a {
        background-color: black !important;
        text-shadow: none;
    }
    .nav-tabs > li > a {
        background-color: black !important;
        text-shadow: none;
    }
    
    
    #component_discography .heading > [class^="heading-"] {
       color: white !important; 
    }
    #component_discography .heading {
          
       background: linear-gradient(90deg, #1990DB, black);
    }
    
    
    #naboo_album_title, 
    #naboo_album_duration, 
    #naboo_playlist_title, 
    #naboo_playlist_duration_nbsong, 
    #naboo_folder_title 
    {
        color: #999 !important;
    }
    
    
    .datagrid-table thead th {
       background-color: #1990DB;
    }
    .datagrid-table tr:nth-child(odd){
       background-color: #1A1A1A !important;
    }
    .datagrid-table tr:nth-child(even) {
       background-color: #252525 !important;
    }
    
    .datagrid-table tbody tr.contributors td  { 
        background-color: #1A1A1A !important;;
    }
  
    
    .datagrid-table tbody tr td a{
       color: #999999 !important 
    }
    .datagrid-table tbody tr.active td {
       background-color: #1990DB !important; 
    }

    
    .datagrid-table tbody tr td.popularity .note, .datagrid-ghost .cell-popularity .note {
       background-image: url('http://i.imgur.com/A3z24ob.png');
    }  
    
    #naboo_menu_list_music .tinyscroll_overview a:hover, 
    .datagrid-table tbody tr:hover td            
    {
       background-color: #333333;
    }
   
    .datagrid-table tbody tr.active td  div,
    .datagrid-table tbody tr.active td  div a  {
       color: white !important;
    }
 
     
    tr.filler {
        background-image: none !important;
    }
   
    
    table {
       background-image: none !important;
       background-color: black !important;
    }
    
    
    .ts_fff, ul li.block_cover { 
        text-shadow: none !important;
    }
    
    
    ::-webkit-scrollbar {
       width: 13px;
       background: black;
    }

    ::-webkit-scrollbar-track {
	   background: #252525; 
       border-radius: 15px
    }

    ::-webkit-scrollbar-thumb {
	   background: -webkit-linear-gradient(left, black, gray, black);
	   border: 1px solid #333;
	   box-shadow: inset 1px 0 0 rgba(255, 255, 255, 0.4);
       border-radius: 15px;
    }

    ::-webkit-scrollbar-thumb:hover {
	   background: -webkit-linear-gradient(left, black, #1990DB, black);
	   border: 1px solid #333;
	   box-shadow: inset 1px 0 0 rgba(255, 255, 255, 0.4);
    }
   
   /* popularity sprite position */
  .datagrid-table tbody tr td.popularity .note-0.note, .datagrid-ghost .cell-popularity .note-0.note {background-position: -40px -784px;}
    .datagrid-table tbody tr td.popularity .note-1.note, .datagrid-ghost .cell-popularity .note-1.note {background-position: -36px -784px;}
    .datagrid-table tbody tr td.popularity .note-2.note, .datagrid-ghost .cell-popularity .note-2.note {background-position: -32px -784px;}
    .datagrid-table tbody tr td.popularity .note-3.note, .datagrid-ghost .cell-popularity .note-3.note {background-position: -28px -784px;}
    .datagrid-table tbody tr td.popularity .note-4.note, .datagrid-ghost .cell-popularity .note-4.note {background-position: -24px -784px;}
    .datagrid-table tbody tr td.popularity .note-5.note, .datagrid-ghost .cell-popularity .note-5.note {background-position: -20px -784px;}
    .datagrid-table tbody tr td.popularity .note-6.note, .datagrid-ghost .cell-popularity .note-6.note {background-position: -16px -784px;}
    .datagrid-table tbody tr td.popularity .note-7.note, .datagrid-ghost .cell-popularity .note-7.note {background-position: -12px -784px;}
    .datagrid-table tbody tr td.popularity .note-8.note, .datagrid-ghost .cell-popularity .note-8.note {background-position: -8px -784px;}
    .datagrid-table tbody tr td.popularity .note-9.note, .datagrid-ghost .cell-popularity .note-9.note {background-position: -4px -784px;}
    .datagrid-table tbody tr td.popularity .note-10.note, .datagrid-ghost .cell-popularity .note-10.note {background-position: 0px -784px;}
 
}
