css:
@-moz-document domain("youtube.com") {
::-webkit-scrollbar { 
  display: none !important; 
}

#yt-masthead-container {
  background: black !important;
  border-bottom: 1px solid #333 !important;
}

#player.watch-large {
  margin: 0 !important;
  width: 100% !important;
}

#player.watch-large #player-api {
  margin: 0 !important;
  width: 100% !important;
  height: 942px !important;
}

#player.watch-large video {
  width: 100% !important;
  height: 907px !important;
}

#player.watch-large .html5-video-content {
  display: none;
  visibility: collapse;
}

#alerts {
  display: none;
  visibility: collapse;
}

}
