css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("steamdb.info") {

div.footer-wrap, div.wrapper-info {
        background: #222;
        color: #fff;
    }    

.table-hover tbody tr:hover td, .table-hover tbody tr:hover th, h2, div.panel-heading, .post blockquote,
    .nav-tabs > .active > a, .nav-tabs > .active > a:hover, p.center, p.copyright, select, pre, div.page-header {
    background-color: #3d3d3d;
    color: #fff;
    text-shadow: none;
    }
    
div.panel, table.table {
        background: #3d3d3d;
        color: #fff
    }    
    
div.footer {
        background: #000;
        color: #fff
    }

p, b, li {
       color: #fff
    }

a:visited { color :#993399; }    

h2.header-subtitle { background: none; }

span.ribbon { background: #993399; }
    
}
