css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sc2tv.ru") {

    #bg-link {
        display: none;
    }
    .p_menu .write {
        font-size: 17px;
    }
    .stream_content .scroll-bar {
        width: 12px !important;
    }
    #streams_list_div > div {
        width: 360px;
        height: 48px;
        background-color: rgb(64,64,102);
        padding: 2px;
        margin: 1px 4px 1px 1px;
        position: relative;
        z-index: 1000;
    }
    #streams_list_div > div:nth-child(2n) {
        margin: 1px 1px 1px 1px;
    }
    .p_l,.p_m,.p_s,.stream_lt_img,.stream_mt_img,.stream_st_img {
        width: 72px;
        height: 40px;
        padding: 4px;
        margin: 0;
        background-color: rgb(64,64,102);
        float: right;
        opacity: 0.25;
        position: relative;
        transition: width 0.5s ease-in 1s,height 0.5s ease-in 1s,margin-top 0.5s ease-in 1s/*,opacity 0.3s*/;
        z-index: 3000;
    }
    #streams_list_div > div:hover .p_l,#streams_list_div > div:hover .p_m,#streams_list_div > div:hover .p_s,#streams_list_div > div:hover .stream_lt_img,#streams_list_div > div:hover .stream_mt_img,#streams_list_div > div:hover .stream_st_img {
        background-color: rgb(52,52,72);
        opacity: 1;
    }
    .p_l:hover,.p_m:hover,.p_s:hover,.stream_lt_img:hover,.stream_mt_img:hover,.stream_st_img:hover {
        /*background-color: rgb(52,52,72);*/
        /*v*/
        width: 160px;
        height: 96px;
    }
    #streams_list_div > div:hover {
        z-index: 2000;
        background-color: rgb(52,52,72);
    }
    #streams_list_div > div:nth-last-child(-n+2) .p_l:hover {
        margin-top: -54px;
    }
    #streams_list_div > div:nth-last-child(-n+2) .p_m:hover {
        margin-top: -54px;
    }
    #streams_list_div > div:nth-last-child(-n+2) .p_s:hover {
        margin-top: -54px;
    }
    #streams_list_div > div:nth-last-child(-n+2) .stream_st_img:hover {
        margin-top: -54px;
    }
    #streams_list_div > div:nth-last-child(-n+2) .stream_mt_img:hover {
        margin-top: -54px;
    }
    #streams_list_div > div:nth-last-child(-n+2) .stream_lt_img:hover {
        margin-top: -54px;
    }
    /*added*/
    #streams_list_div > div:nth-child(2) .p_l:hover {
        margin-top: 0 !important;
    }
    #streams_list_div > div:nth-child(2) .p_m:hover {
        margin-top: 0 !important;
    }
    #streams_list_div > div:nth-child(2) .p_s:hover {
        margin-top: 0 !important;
    }
    #streams_list_div > div:nth-child(2) .stream_st_img:hover {
        margin-top: 0 !important;
    }
    #streams_list_div > div:nth-child(2) .stream_mt_img:hover {
        margin-top: 0 !important;
    }
    #streams_list_div > div:nth-child(2) .stream_lt_img:hover {
        margin-top: 0 !important;
    }
    #streams_list_div > div:first-child .p_l:hover {
        margin-top: 0 !important;
    }
    #streams_list_div > div:first-child .p_m:hover {
        margin-top: 0 !important;
    }
    #streams_list_div > div:first-child .p_s:hover {
        margin-top: 0 !important;
    }
    #streams_list_div > div:first-child .stream_st_img:hover {
        margin-top: 0 !important;
    }
    #streams_list_div > div:first-child .stream_mt_img:hover {
        margin-top: 0 !important;
    }
    #streams_list_div > div:first-child .stream_lt_img:hover {
        margin-top: 0 !important;
    }
    .stream_head {
        background: none !important;
    }
    .st_n {
        font-size: 12px;
        width: 280px;
        overflow: hidden;
        text-overflow: ellipsis;
        /*background-color: rgb(64,64,102);*/
        white-space: nowrap;
        height: 16px;
        margin: 0;
        padding-top: 0;
        padding-bottom: 0;
        margin-top: 16px;
        margin-bottom: -16px;
        font-weight: normal;
        color: white;
    }
    /*#streams_list_div > div:hover .st_n {
        background-color: rgb(52,52,72);
    }*/
    .st_nm {
        font-size: 12px;
        color: #53A4FF;
        margin: -32px 0 16px 0;
        font-weight: bold;
        padding-top: 0;
        padding-bottom: 0;
        height: 16px;
        background: none !important;
    }
    /*#streams_list_div > div:hover .st_nm {
        visibility: hidden;
    }*/
    #streams_list_div > div a:last-child {
        position: absolute;
        display: block;
        margin: -32px 0 0 0;
        padding: 0;
        width: 361px;
        height: 48px;
    }
    .o_l_live,.o_l_live_m,.o_l_live_t_s {
        /*float: left;
        margin: 0;
        position: absolute;
        top: 1px;
        left: 0;
        width: 24px;
        height: 12px;*/
        display: none !important;
    }
    #streams_list_div > div a:last-child .adult {
        float: left;
        margin: 0;
        position: absolute;
        top: 25px;
        right: 5px;
        width: 18px;
        height: 18px;
    }
    #streams_list_div > div:hover a:last-child .adult {
        display: none;
    }
    .st_cat {
        position: absolute;
        right: 4px;
        top: -1px;
        padding: 1px;
        width: 70px;
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
        border-radius: 0 !important;
        right: -77px;
        display: block;
        background-color: rgba(0, 0, 0, 0.3) ;
        opacity: 0.7 !important;
    }
    #streams_list_div > div:hover .st_cat {
        display: none;
    }
    .o_l_bot,.o_m_bot,.o_s_bot {
        background: none;
        margin: 0;
        padding: 0;
        position: absolute;
        top: 0px;
        left: 0px;
        height: 48px;
        width: 280px;
    }
    #streams_list_div > div:hover .o_l_bot span,#streams_list_div > div:hover .o_m_bot span,#streams_list_div > div:hover .o_s_bot span,#streams_list_div > div:hover .st_span,#streams_list_div > div:hover .mt_span,#streams_list_div > div:hover .lt_span {
        background: rgb(52,52,72);
        height: 30px;
        white-space: normal;
        margin-top: 16px;
    }
    .o_l_bot span,.o_m_bot span,.o_s_bot span,.mt_span,.lt_span,.st_span {
        background: rgb(64,64,102);
        display: block;
        border-top: 1px #BBB solid;
        margin: 32px 0 0 0;
        padding: 0 3px !important;
        position: absolute;
        top: 0;
        left: 0;
        height: 14px;
        width: 277px;
        color: #BBB;
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
        transition: height 0.5s,margin-top 0.5s;
    }
    .over_l,.over_m,.over_s,.over_st,.over_mt,.over_lt {
        background: none;
        margin: 0 !important;
        padding: 0 !important;
        position: absolute;
        top: 0px;
        left: 0px;
        height: 48px;
        width: 280px;
        visibility: visible;
        float: right;
        margin: 0;
    }
    .s_black,.m_black,.l_black {
        display: none;
    }
    .stream_st_span,.stream_mt_span,.stream_lt_span {
        background: none;
        margin: 12px 0 0 0;
        padding: 0;
        position: absolute;
        top: 11px;
        right: 5px;
        font-size: 10px;
        text-align: right;
        text-transform: lowercase;
        line-height: 10px !important;                     
    }
    .stream_st_span span,.stream_mt_span span,.stream_lt_span span {
        font-size: 12px;
    }
}
