css:
@-moz-document domain(imdb.com) {

.aux-content-widget-2, .aux-content-widget-3, .article.on-tv, .aux-content-widget-4 {
	background: #f5f5f4 !important;
	border: 1px solid #e5e5e4;
	-moz-border-radius: 0 !important;
	-webkit-border-radius: 0 !important;
	border-radius: 0 !important;
	-moz-box-shadow: 0 2px 5px rgba(0,0,0,0.07);
	-webkit-box-shadow: 0 2px 5px rgba(0,0,0,0.07);
	box-shadow: 0 2px 5px rgba(0,0,0,0.07);
}
.article.title-overview, .article.name-overview, .redesign .article.highlighted {
	border-top: 0 !important;
}
.article {
	border-radius: 0 !important;
	border-left: 0 !important;
	border-bottom: 0 !important;
	border-right: 0 !important;
	border-color: #aaa;
	border-top-width: 2px;
}
div.image {
	border: 0 !important;
}
#styleguide-v2 {
	background: #e5e5e4 !important;
}
#root {
	box-shadow: 0 0 35px rgba(0,0,0,0.2);
}
h2 {
	font-size: 25px !important;
	font-weight: 400;
	line-height: 120%;
}
h2.rec_heading_wrapper, .mediastrip_container.combined h2 {
	line-height: 180% !important;
}
.article h1.header span.itemprop {
	font-size: 32px !important;
	font-weight: 400;
	line-height: 110%;
}
.btn2 {
	background: #ddd !important;
	padding: 4px 7px;
	border: 0;
	font-size: 14px !important;
}
.btn2.primary {
	background: #f3ce00 !important;
}
.btn2.primary:hover {
	background: #e6b800 !important;
}
.redesign .article.highlighted {
	margin: 0;
}
}
