@-moz-document domain(userstyles.org) {

@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 300;
  src: local('Open Sans Light'), local('OpenSans-Light'), url(https://themes.googleusercontent.com/static/fonts/opensans/v8/DXI1ORHCpsQm3Vp6mXoaTaRDOzjiPcYnFooOUGCOsRk.woff) format('woff');
}
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Open Sans'), local('OpenSans'), url(https://themes.googleusercontent.com/static/fonts/opensans/v8/cJZKeOuBrn4kERxqtaUH3bO3LdcAZYWl9Si6vvxL-qU.woff) format('woff');
}

#main-header {
    background-color: transparent;
    font-family: 'Open Sans';
    font-weight: 300;
    font-size: 19px;
    text-align: center;
    text-decoration: none;
}

#main-header h1 {
    font-weight: 300;
}

#main-header a {
    color: rgb(0,0,255);
    text-decoration: none;
}

#main-header a:hover {
    color: rgb(120,120,255);
}

#left-sidebar a,#show-advanced-search,footer a {
    color: rgb(100,100,100);
    text-decoration: none;
}

#left-sidebar a:hover,#show-advanced-search:hover,footer a:hover {
    color: rgb(100,100,255);
}

body {
    font-family: open sans;
}

footer {
    text-align: center;
}

#left-sidebar > * {
    margin-bottom: 10px;
    border: 1px solid rgb(221, 221, 221);
    padding: 5px;
    border-radius: 5px;
    background-color: transparent;
}

h3,h2, #more-screenshots span, th {
    font-family: 'Open Sans';
    text-decoration: none;
    font-weight: 300;
}

th {
    font-size: 13px;
}

.install {
    background-image: linear-gradient(rgb(60, 150, 60), rgb(60, 150, 74));
}

.install:hover {
    background-image: linear-gradient(rgb(100, 150, 100), rgb(100, 160, 114));
}

.stylebot-link:link {
    color: rgb(51,51,51);
}

.install-status a {
    color: inherit;
}

DIV#stylish-installed-style-installed.install-status.install-info div {
    height: 0px;
    width: 0px;
}

A.install-note {
    color: rgb(211,211,211);
}

A.install-note:hover {
    color: rgb(230,230,230);
}

.ad {
    display: none;
}

#main-article section {
    border-top: 1px solid rgba(0,0,0,.2);
}

section#style-info {
    padding: 15px 0 10px;
    border-top: none;
}

#social {
    display: none;
}

#main-article {
    padding-top: 1em;
}

#post-install-ad {
    display: none !important;
}

#main-style-info .install-status.install-info,
#main-style-info .install-status.install-info div {
    height: auto !important;
    width: auto !important;
}

}
